import React, { useState, useRef, useEffect } from 'react';
import { Bookmark, Check, Volume2, Globe, ChevronDown, Share2, Home, Search, Heart, User } from 'lucide-react';
import { doc, setDoc, arrayUnion } from "firebase/firestore";
import { auth, db } from '../firebase';

// Language options
const LANGUAGES = {
  ENGLISH: 'en',
  KINYARWANDA: 'rw'
};

// Language display names
const LANGUAGE_NAMES = {
  [LANGUAGES.ENGLISH]: 'English',
  [LANGUAGES.KINYARWANDA]: 'Kinyarwanda'
};

// Speech synthesis language codes
const SPEECH_LANGUAGES = {
  [LANGUAGES.ENGLISH]: 'en-US',
  [LANGUAGES.KINYARWANDA]: 'rw-RW'
};

const HomeTab = ({ theme, verseOfDay, addBookmark, fetchVerseOfDay }) => {
  const [bookmarked, setBookmarked] = useState(false);
  const [bookmarkLoading, setBookmarkLoading] = useState(false);
  const [isPlaying, setIsPlaying] = useState(false);
  const [selectedLanguage, setSelectedLanguage] = useState(LANGUAGES.ENGLISH);
  const [dropdownOpen, setDropdownOpen] = useState(false);
  const speechSynthRef = useRef(window.speechSynthesis);
  const dropdownRef = useRef(null);

  // Toggle dropdown
  const toggleDropdown = () => {
    setDropdownOpen(!dropdownOpen);
  };

  // Close dropdown when clicking outside
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (dropdownRef.current && !dropdownRef.current.contains(event.target)) {
        setDropdownOpen(false);
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, []);

  // Handle language change
  const handleLanguageChange = (language) => {
    if (language === selectedLanguage) return;

    setSelectedLanguage(language);
    setDropdownOpen(false);

    // Fetch verse in the selected language
    if (fetchVerseOfDay) {
      fetchVerseOfDay(language);
    }
  };

  // Function to handle bookmarking the verse of the day
  const handleBookmarkVOTD = async () => {
    if (!verseOfDay || bookmarked || !auth.currentUser) return;

    try {
      setBookmarkLoading(true);

      // Parse the reference to extract book, chapter, and verse
      const referenceText = verseOfDay.reference;

      // Parse book, chapter and verse details from the reference
      const matches = referenceText.match(/^((?:\d\s)?[A-Za-z]+)\s+(\d+):(\d+)(?:-\d+)?$/);

      if (!matches) {
        console.error("Could not parse reference:", referenceText);
        return;
      }

      const [_, book, chapter, verse] = matches;

      // Create bookmark data
      const bookmarkData = {
        book: book,
        chapter: parseInt(chapter),
        lineNumber: parseInt(verse),
        text: verseOfDay.text,
        reference: verseOfDay.reference,
        language: selectedLanguage,
        timestamp: new Date()
      };

      // Add to Firebase
      const userDocRef = doc(db, "users", auth.currentUser.uid);
      await setDoc(userDocRef, {
        bookmarks: arrayUnion(bookmarkData)
      }, { merge: true });

      // Call the original addBookmark function for local state update
      if (addBookmark) {
        addBookmark(verseOfDay.reference, verseOfDay.text);
      }

      // Show success feedback
      setBookmarked(true);

      // Reset the bookmarked status after 3 seconds
      setTimeout(() => {
        setBookmarked(false);
      }, 3000);

    } catch (error) {
      console.error("Error bookmarking verse of the day:", error);
    } finally {
      setBookmarkLoading(false);
    }
  };

  // Function to read verse aloud
  const readVerse = () => {
    if (!verseOfDay) return;

    // Cancel any ongoing speech
    speechSynthRef.current.cancel();

    // Create utterance with verse text and reference
    const textToRead = `${verseOfDay.text}. ${verseOfDay.reference}`;
    const utterance = new SpeechSynthesisUtterance(textToRead);

    // Set language based on selection
    utterance.lang = SPEECH_LANGUAGES[selectedLanguage] || 'en-US';

    // Handle speech events
    utterance.onstart = () => setIsPlaying(true);
    utterance.onend = () => setIsPlaying(false);
    utterance.onerror = () => setIsPlaying(false);

    // Speak the verse
    speechSynthRef.current.speak(utterance);
  };

  // Stop reading
  const stopReading = () => {
    speechSynthRef.current.cancel();
    setIsPlaying(false);
  };

  // Function to handle sharing
  const handleShare = () => {
    if (!verseOfDay) return;

    if (navigator.share) {
      navigator.share({
        title: 'Verse of the Day',
        text: `${verseOfDay.text} - ${verseOfDay.reference}`,
      })
      .catch(error => console.error('Error sharing:', error));
    }
  };

  return (
    <div className="min-h-screen flex flex-col bg-gray-50">
      {/* Top Header - Modern with subtle gradient */}
      <div className="bg-gradient-to-r from-red-600 to-red-700 text-white px-6 py-4 flex justify-between items-center shadow-md">
        <h1 className="text-xl font-semibold">
          {selectedLanguage === LANGUAGES.KINYARWANDA ? 'Umurongo w\'Umunsi' : 'Verse of the Day'}
        </h1>

        {/* Language selector - Cleaner dropdown */}
        <div className="relative" ref={dropdownRef}>
          <button 
            className="flex items-center gap-1 px-3 py-1 bg-white bg-opacity-20 rounded-full text-sm hover:bg-opacity-30 transition-all duration-200"
            onClick={toggleDropdown}
          >
            <Globe size={16} />
            <span>{LANGUAGE_NAMES[selectedLanguage]}</span>
            <ChevronDown size={14} className={`transform transition-transform duration-200 ${dropdownOpen ? 'rotate-180' : ''}`} />
          </button>

          {dropdownOpen && (
            <div className="absolute right-0 mt-2 py-2 bg-white rounded-lg shadow-xl border border-gray-100" style={{ zIndex: 1000, minWidth: '180px' }}>
              {Object.values(LANGUAGES).map(lang => (
                <button 
                  key={lang}
                  className={`w-full text-left px-4 py-2 text-gray-700 hover:bg-red-50 transition-colors duration-150 ${selectedLanguage === lang ? 'font-medium text-red-600' : ''}`}
                  onClick={() => handleLanguageChange(lang)}
                >
                  {LANGUAGE_NAMES[lang]}
                </button>
              ))}
            </div>
          )}
        </div>
      </div>

      {/* Main Content - Elegant Verse Card */}
      <div className="flex-grow flex justify-center items-center p-4">
        {verseOfDay ? (
          <div className="verse-card relative rounded-2xl w-full shadow-xl overflow-hidden max-w-lg" style={{ minHeight: '65vh' }}>
            {/* Dynamic gradient background */}
            <div 
              className="absolute inset-0 bg-gradient-to-br from-indigo-900 via-purple-800 to-pink-700 opacity-90"
              style={{ zIndex: 0 }}
            ></div>

            {/* Subtle texture overlay */}
            <div 
              className="absolute inset-0 opacity-10"
              style={{ 
                backgroundImage: `url("data:image/svg+xml,%3Csvg width='100' height='100' viewBox='0 0 100 100' xmlns='http://www.w3.org/2000/svg'%3E%3Cpath d='M11 18c3.866 0 7-3.134 7-7s-3.134-7-7-7-7 3.134-7 7 3.134 7 7 7zm48 25c3.866 0 7-3.134 7-7s-3.134-7-7-7-7 3.134-7 7 3.134 7 7 7zm-43-7c1.657 0 3-1.343 3-3s-1.343-3-3-3-3 1.343-3 3 1.343 3 3 3zm63 31c1.657 0 3-1.343 3-3s-1.343-3-3-3-3 1.343-3 3 1.343 3 3 3zM34 90c1.657 0 3-1.343 3-3s-1.343-3-3-3-3 1.343-3 3 1.343 3 3 3zm56-76c1.657 0 3-1.343 3-3s-1.343-3-3-3-3 1.343-3 3 1.343 3 3 3zM12 86c2.21 0 4-1.79 4-4s-1.79-4-4-4-4 1.79-4 4 1.79 4 4 4zm28-65c2.21 0 4-1.79 4-4s-1.79-4-4-4-4 1.79-4 4 1.79 4 4 4zm23-11c2.76 0 5-2.24 5-5s-2.24-5-5-5-5 2.24-5 5 2.24 5 5 5zm-6 60c2.21 0 4-1.79 4-4s-1.79-4-4-4-4 1.79-4 4 1.79 4 4 4zm29 22c2.76 0 5-2.24 5-5s-2.24-5-5-5-5 2.24-5 5 2.24 5 5 5zM32 63c2.76 0 5-2.24 5-5s-2.24-5-5-5-5 2.24-5 5 2.24 5 5 5zm57-13c2.76 0 5-2.24 5-5s-2.24-5-5-5-5 2.24-5 5 2.24 5 5 5zm-9-21c1.105 0 2-.895 2-2s-.895-2-2-2-2 .895-2 2 .895 2 2 2zM60 91c1.105 0 2-.895 2-2s-.895-2-2-2-2 .895-2 2 .895 2 2 2zM35 41c1.105 0 2-.895 2-2s-.895-2-2-2-2 .895-2 2 .895 2 2 2zM12 60c1.105 0 2-.895 2-2s-.895-2-2-2-2 .895-2 2 .895 2 2 2z' fill='%23ffffff' fill-opacity='0.1' fill-rule='evenodd'/%3E%3C/svg%3E")`,
                zIndex: 1 
              }}
            ></div>

            {/* Decorative light streak */}
            <div 
              className="absolute rotate-45 w-24 h-96 bg-white opacity-10 blur-xl"
              style={{ top: '-20%', left: '60%', zIndex: 1 }}
            ></div>

            {/* Verse Content */}
            <div className="relative h-full flex flex-col justify-center items-center text-center text-white p-8" style={{ zIndex: 2 }}>
              {/* Elegant quote mark */}
              <div className="text-6xl font-serif text-white opacity-50 mb-4">‚ùù</div>

              {/* Verse text with improved typography */}
              <h2 className="text-2xl font-light leading-relaxed tracking-wide mb-8">
                {verseOfDay.text}
              </h2>

              {/* Reference with subtle separator */}
              <div className="mt-2">
                <div className="w-12 h-0.5 bg-white opacity-30 mx-auto mb-4"></div>
                <p className="font-medium text-lg">{verseOfDay.reference}</p>
                <p className="text-sm opacity-75 mt-1">({verseOfDay.translation})</p>
              </div>

              {/* Action Buttons - More elegant and modern */}
              <div className="flex justify-center mt-10 gap-6">
                <button 
                  className="flex justify-center items-center w-12 h-12 rounded-full bg-white bg-opacity-20 backdrop-blur-md hover:bg-opacity-30 transition-all duration-200"
                  onClick={handleShare}
                  aria-label="Share verse"
                >
                  <Share2 size={20} />
                </button>

                <button 
                  className={`flex justify-center items-center w-12 h-12 rounded-full transition-all duration-200 ${
                    bookmarked 
                      ? 'bg-red-500 text-white' 
                      : 'bg-white bg-opacity-20 hover:bg-opacity-30'
                  }`}
                  onClick={bookmarked ? null : handleBookmarkVOTD}
                  disabled={bookmarkLoading}
                  aria-label="Bookmark verse"
                >
                  {bookmarkLoading ? (
                    <div className="w-5 h-5 border-2 border-t-transparent border-white rounded-full animate-spin"></div>
                  ) : (
                    bookmarked ? <Check size={20} /> : <Bookmark size={20} />
                  )}
                </button>

                <button 
                  className={`flex justify-center items-center w-12 h-12 rounded-full bg-white bg-opacity-20 backdrop-blur-md hover:bg-opacity-30 transition-all duration-200 ${
                    isPlaying ? 'bg-opacity-40 ring-2 ring-white ring-opacity-40' : ''
                  }`}
                  onClick={isPlaying ? stopReading : readVerse}
                  aria-label={isPlaying ? "Stop reading" : "Read verse aloud"}
                >
                  <Volume2 size={20} className={isPlaying ? 'animate-pulse' : ''} />
                </button>
              </div>
            </div>
          </div>
        ) : (
          <div className="flex justify-center py-12">
            <div className="relative w-16 h-16">
              <div className="absolute top-0 left-0 w-full h-full border-4 border-red-500 border-t-transparent rounded-full animate-spin"></div>
              <div className="absolute top-2 left-2 w-12 h-12 border-4 border-red-300 border-t-transparent rounded-full animate-spin"></div>
            </div>
          </div>
        )}
      </div>

      {/* Bottom Navigation Bar - Floating modern design */}
      <div className="px-6 py-4">
        <nav className="bg-white rounded-full shadow-lg flex justify-around items-center py-3 px-6 max-w-sm mx-auto">
          <button className="flex flex-col items-center text-red-500">
            <Home size={20} />
            <span className="text-xs mt-1">Home</span>
          </button>

          <button className="flex flex-col items-center text-gray-500 hover:text-red-500 transition-colors">
            <Search size={20} />
            <span className="text-xs mt-1">Search</span>
          </button>

          <button className="flex flex-col items-center text-gray-500 hover:text-red-500 transition-colors">
            <Heart size={20} />
            <span className="text-xs mt-1">Saved</span>
          </button>

          <button className="flex flex-col items-center text-gray-500 hover:text-red-500 transition-colors">
            <User size={20} />
            <span className="text-xs mt-1">Profile</span>
          </button>
        </nav>
      </div>
    </div>
  );
};

export default HomeTab;
