import React, { useEffect, useState } from 'react';
import { Bookmark, Edit, X, Highlighter } from 'lucide-react';
import { 
  doc, 
  updateDoc,
  arrayRemove,
  onSnapshot
} from "firebase/firestore";
import { auth, db } from '../firebase';

const BookmarksTab = ({ theme, darkMode, navigateToPassage }) => {
  const [bookmarks, setBookmarks] = useState([]);
  const [highlights, setHighlights] = useState([]);
  const [userId, setUserId] = useState(null);
  const [activeTab, setActiveTab] = useState('bookmarks');

  useEffect(() => {
    const unsubscribe = auth.onAuthStateChanged(user => {
      if (user) {
        setUserId(user.uid);
        listenToUserData(user.uid);
      }
    });
    return () => unsubscribe();
  }, []);

  const listenToUserData = (uid) => {
    const userDocRef = doc(db, "users", uid);

    return onSnapshot(userDocRef, (doc) => {
      if (doc.exists()) {
        const data = doc.data();
        setBookmarks(data.bookmarks || []);
        setHighlights(data.highlights || []);
      } else {
        setBookmarks([]);
        setHighlights([]);
      }
    });
  };

  const deleteBookmark = async (bookmarkToRemove) => {
    if (!userId) return;

    try {
      const userDocRef = doc(db, "users", userId);
      await updateDoc(userDocRef, {
        bookmarks: arrayRemove(bookmarkToRemove)
      });
    } catch (error) {
      console.error("Error deleting bookmark:", error);
    }
  };

  const deleteHighlight = async (highlightToRemove) => {
    if (!userId) return;

    try {
      const userDocRef = doc(db, "users", userId);
      await updateDoc(userDocRef, {
        highlights: arrayRemove(highlightToRemove)
      });
    } catch (error) {
      console.error("Error deleting highlight:", error);
    }
  };

  const handleReadFullPassage = (item) => {
    // Parse the reference to get book, chapter, and verse
    const parts = item.reference.split(' ');
    let book = '';
    let chapter = 1;
    let verse = 1;

    if (parts.length >= 2) {
      // Handle format like "John 3:16" or "1 Corinthians 13:4"
      if (parts[0].match(/^\d+$/)) {
        // This is a book with a number like "1 Corinthians"
        book = parts[0] + ' ' + parts[1];
        const chapterVerse = parts[2] ? parts[2].split(':') : ['1', '1'];
        chapter = parseInt(chapterVerse[0]) || 1;
        verse = parseInt(chapterVerse[1]) || 1;
      } else {
        // This is a book without a number like "John"
        book = parts[0];
        const chapterVerse = parts[1] ? parts[1].split(':') : ['1', '1'];
        chapter = parseInt(chapterVerse[0]) || 1;
        verse = parseInt(chapterVerse[1]) || 1;
      }
    }

    // Call the navigation function passed from parent
    if (navigateToPassage && typeof navigateToPassage === 'function') {
      navigateToPassage(book, chapter, verse);
    }
  };

  return (
    <div className={`${theme.card} rounded shadow p-4`}>
      <div className="d-flex gap-3 mb-4 border-bottom pb-2">
        <button 
          className={`btn  p-0 ${activeTab === 'bookmarks'}`}
          onClick={() => setActiveTab('bookmarks')}
        >
          <h2 className="h4 fw-semibold mb-0">Your Bookmarks</h2>
        </button>
      </div>

      {activeTab === 'bookmarks' ? (
        <div className="d-flex flex-column gap-3">
          {bookmarks.length > 0 ? (
            bookmarks.map((bookmark, index) => (
              <div 
                key={`${bookmark.reference}-${index}`} 
                className={`p-3 border-start border-4 border-secondary rounded ${darkMode ? 'bg-secondary bg-opacity-25' : 'bg-light'}`}
              >
                <div className="d-flex justify-content-between">
                  <h3 className="fs-6 fw-medium mb-0">{bookmark.reference}</h3>
                  <div className="d-flex gap-2">
                    <button 
                      className={`btn btn-link ${theme.subtleText} p-1`}
                      onClick={() => deleteBookmark(bookmark)}
                    >
                      <X size={16} />
                    </button>
                  </div>
                </div>
                <p className="mt-2 mb-1">{bookmark.text}</p>
                <button 
                  className={`btn btn-link ${theme.accentText} p-0 small`}
                  onClick={() => handleReadFullPassage(bookmark)}
                >
                  Read full passage
                </button>
              </div>
            ))
          ) : (
            <p className="text-center py-4">No bookmarks yet. Add some from the Bible text!</p>
          )}
        </div>
      ) : (
        <div className="d-flex flex-column gap-3">
          {highlights.length > 0 ? (
            highlights.map((highlight, index) => (
              <div 
                key={`${highlight.reference}-${index}`} 
                className={`p-3 border-start border-4 border-warning rounded ${darkMode ? 'bg-secondary bg-opacity-25' : 'bg-light'}`}
              >
                <div className="d-flex justify-content-between">
                  <h3 className="fs-6 fw-medium mb-0">{highlight.reference}</h3>
                  <div className="d-flex gap-2">
                    <button 
                      className={`btn btn-link ${theme.subtleText} p-1`}
                      onClick={() => deleteHighlight(highlight)}
                    >
                      <X size={16} />
                    </button>
                  </div>
                </div>
                <p className="mt-2 mb-1">
                  <Highlighter className="me-2" size={16} />
                  {highlight.text}
                </p>
                <button 
                  className={`btn btn-link ${theme.accentText} p-0 small`}
                  onClick={() => handleReadFullPassage(highlight)}
                >
                  Read full passage
                </button>
              </div>
            ))
          ) : (
            <p className="text-center py-4">No highlights yet. Highlight some text from the Bible!</p>
          )}
        </div>
      )}
    </div>
  );
};

export default BookmarksTab;
