import React, { useState, useEffect } from 'react';
import { Cross } from 'lucide-react';

const DEFAULT_PRAYERS = [
  { 
    name: "Sign of the Cross", 
    text: "In the name of the Father, and of the Son, and of the Holy Spirit. Amen.", 
    repetitions: 1 
  },
  { 
    name: "Our Father", 
    text: "Our Father, who art in heaven, hallowed be thy name; thy kingdom come; thy will be done on earth as it is in heaven. Give us this day our daily bread; and forgive us our trespasses as we forgive those who trespass against us; and lead us not into temptation, but deliver us from evil. Amen.", 
    repetitions: 1 
  },
  { 
    name: "Hail Mary", 
    text: "Hail Mary, full of grace, the Lord is with thee; blessed art thou among women, and blessed is the fruit of thy womb, Jesus. Holy Mary, Mother of God, pray for us sinners, now and at the hour of our death. Amen.", 
    repetitions: 10 
  },
  { 
    name: "Glory Be", 
    text: "Glory be to the Father, and to the Son, and to the Holy Spirit. As it was in the beginning, is now, and ever shall be, world without end. Amen.", 
    repetitions: 1 
  }
];

const Chapulle = ({ theme }) => {
  const [isPraying, setIsPraying] = useState(false);
  const [hasStarted, setHasStarted] = useState(false);
  const [currentPrayerIndex, setCurrentPrayerIndex] = useState(0);
  const [counters, setCounters] = useState(DEFAULT_PRAYERS.map(p => p.repetitions));
  const [message, setMessage] = useState("");
  const [prayers, setPrayers] = useState(DEFAULT_PRAYERS);
  const [isAutoPlaying, setIsAutoPlaying] = useState(false);

  const getCurrentPrayer = () => {
    return prayers[currentPrayerIndex] || prayers[0];
  };

  const startPrayer = () => {
    setHasStarted(true);
    setIsPraying(true);
    setMessage("");
    setCurrentPrayerIndex(0);
    setCounters(prayers.map(p => p.repetitions));
  };

  const toggleAutoPlay = () => {
    setIsAutoPlaying(!isAutoPlaying);
  };

  const handleCounterClick = (prayerIndex) => {
    if (prayerIndex !== currentPrayerIndex) return;

    setCounters(prev => {
      const newCounters = [...prev];
      if (newCounters[prayerIndex] > 0) {
        newCounters[prayerIndex] -= 1;
      }

      // Move to next prayer if current counter reaches 0
      if (newCounters[prayerIndex] === 0 && prayerIndex === currentPrayerIndex) {
        advancePrayer();
      }

      return newCounters;
    });
  };

  const advancePrayer = () => {
    if (currentPrayerIndex < prayers.length - 1) {
      setCurrentPrayerIndex(prev => prev + 1);
    } else {
      completePrayer();
    }
  };

  const completePrayer = () => {
    setIsPraying(false);
    setIsAutoPlaying(false);
    setMessage("Amen! You completed your prayer chaplet. ❤️");
  };

  useEffect(() => {
    let timer;
    if (isPraying && isAutoPlaying) {
      timer = setInterval(() => {
        setCounters(prev => {
          const newCounters = [...prev];
          if (newCounters[currentPrayerIndex] <= 1) {
            advancePrayer();
            return newCounters;
          }
          newCounters[currentPrayerIndex] -= 1;
          return newCounters;
        });
      }, 1000);
    }
    return () => clearInterval(timer);
  }, [isPraying, currentPrayerIndex, prayers, isAutoPlaying]);

  const PrayerInProgress = () => (
    <>
      <div className="d-flex justify-content-between align-items-center mb-3">
        <h3 className="h5 fw-semibold mb-0">
          {getCurrentPrayer().name}
        </h3>
        <div className="d-flex gap-2">
          <button 
            onClick={toggleAutoPlay}
            className={`btn btn-sm ${isAutoPlaying ? theme.secondaryBg : theme.accentBg}`}
          >
            {isAutoPlaying ? 'Pause Auto' : 'Play Auto'}
          </button>
          <button 
            onClick={completePrayer}
            className={`btn btn-sm ${theme.accentBg}`}
          >
            Complete
          </button>
        </div>
      </div>
      <div className={`p-3 rounded ${theme.card} mb-3`}>
        <p className="mb-0 fs-5">{getCurrentPrayer().text}</p>
      </div>
      <div className="d-flex flex-wrap gap-3 mb-4">
        {prayers.map((prayer, index) => (
          <button
            key={index}
            onClick={() => handleCounterClick(index)}
            className={`d-flex flex-column align-items-center justify-content-center rounded-circle ${theme.accentBg}`}
            style={{
              width: '60px',
              height: '60px',
              border: 'none',
              opacity: index === currentPrayerIndex ? 1 : 0.5,
              cursor: index === currentPrayerIndex ? 'pointer' : 'default'
            }}
          >
            <span className="fw-bold fs-5">{counters[index]}</span>
            <span className="small">{prayer.name.split(' ')[0]}</span>
          </button>
        ))}
      </div>
    </>
  );

  const PrayerStartScreen = () => (
    <div className="text-center py-5">
      <h3 className="h4 fw-semibold mb-4">Christian Prayer Chaplet</h3>
      <button
        onClick={startPrayer}
        className={`btn ${theme.accentBg} py-3 px-5 rounded-pill`}
        style={{ fontSize: '1.2rem' }}
      >
        Start Praying
      </button>

      <div className="mt-5">
        <h4 className="h5 fw-semibold mb-3">Prayer Sequence</h4>
        <div className="list-group">
          {prayers.map((prayer, index) => (
            <div 
              key={index}
              className={`list-group-item ${theme.card}`}
            >
              <div className="d-flex justify-content-between">
                <span>{prayer.name}</span>
                <span className={`${theme.subtleText}`}>{prayer.repetitions}x</span>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );

  const PrayerCompletedScreen = () => (
    <div className="text-center py-5">
      <div className={`alert ${theme.accentBg} mb-4`}>
        {message}
      </div>
      <button
        onClick={startPrayer}
        className={`btn ${theme.accentBg} py-3 px-5 rounded-pill`}
      >
        Restart Prayer
      </button>
    </div>
  );

  return (
    <div className="chapulle-container">
      <div className="d-flex align-items-center mb-4">
        <Cross size={28} className={`me-3 ${theme.accentText}`} />
        <h2 className="h3 fw-bold mb-0">Prayer Chaplet</h2>
      </div>

      {!hasStarted ? (
        <PrayerStartScreen />
      ) : isPraying ? (
        <PrayerInProgress />
      ) : (
        <PrayerCompletedScreen />
      )}
    </div>
  );
};

export default Chapulle;
