import React, { useState, useEffect, useRef } from 'react';
import { Cross } from 'lucide-react';

const MYSTERIES = {
  joyful: [
    "The Annunciation (Luke 1:26-38)",
    "The Visitation (Luke 1:39-56)",
    "The Nativity (Luke 2:1-20)",
    "The Presentation (Luke 2:21-38)",
    "The Finding in the Temple (Luke 2:41-52)"
  ],
  sorrowful: [
    "The Agony in the Garden (Matthew 26:36-46)",
    "The Scourging at the Pillar (Matthew 27:26)",
    "The Crowning with Thorns (Matthew 27:29)",
    "The Carrying of the Cross (John 19:17)",
    "The Crucifixion (John 19:18-30)"
  ],
  glorious: [
    "The Resurrection (John 20:1-29)",
    "The Ascension (Acts 1:6-11)",
    "The Descent of the Holy Spirit (Acts 2:1-13)",
    "The Assumption (Revelation 12:1)",
    "The Coronation (Revelation 12:1)"
  ],
  luminous: [
    "The Baptism in the Jordan (Matthew 3:13-17)",
    "The Wedding at Cana (John 2:1-12)",
    "The Proclamation of the Kingdom (Mark 1:15)",
    "The Transfiguration (Luke 9:28-36)",
    "The Institution of the Eucharist (Luke 22:14-20)"
  ]
};

const DEFAULT_PRAYERS = [
