import React, { useState, useEffect } from 'react';
import { Edit, X, Save, Bold, Italic, Underline, List, AlignLeft, AlignCenter, AlignRight } from 'lucide-react';

const NotesTab = ({ theme, notes, setNotes, darkMode }) => {
  const [editingNote, setEditingNote] = useState(null);
  const [editedContent, setEditedContent] = useState('');
  const [editedTitle, setEditedTitle] = useState('');
  const [selectedColor, setSelectedColor] = useState('#ffffff');

  const colorOptions = [
    { name: 'White', value: '#ffffff' },
    { name: 'Pastel Pink', value: '#ffcccb' },
    { name: 'Pastel Blue', value: '#ccf2ff' },
    { name: 'Pastel Yellow', value: '#ffffcc' },
    { name: 'Pastel Green', value: '#ccffcc' },
    { name: 'Pastel Purple', value: '#e6ccff' }
  ];

  // Load notes from localStorage when component mounts
  useEffect(() => {
    const savedNotes = localStorage.getItem('savedNotes');
    if (savedNotes) {
      setNotes(JSON.parse(savedNotes));
    }
  }, []);

  // Save notes to localStorage whenever they change
  useEffect(() => {
    if (notes && notes.length > 0) {
      localStorage.setItem('savedNotes', JSON.stringify(notes));
    }
  }, [notes]);
// Add this to your component after it mounts
useEffect(() => {
  // Force LTR direction on the note editing area
  const notepadElements = document.querySelectorAll('.notepad-container');
  notepadElements.forEach(el => {
    el.style.direction = 'ltr';
    el.style.textAlign = 'left';
    el.setAttribute('dir', 'ltr');

    // Add observers for any dynamic content changes
    const observer = new MutationObserver(() => {
      el.style.direction = 'ltr';
      el.style.textAlign = 'left';
      el.setAttribute('dir', 'ltr');
    });

    observer.observe(el, { childList: true, subtree: true });
  });
}, [editingNote]); // Re-run when editing state changes
  const openNoteEditor = (note, index) => {
    setEditingNote(index);
    setEditedContent(note.content || 'Click to view your notes for this passage...');
    setEditedTitle(note.title);
    setSelectedColor(note.color || '#ffffff');
  };

  const closeNoteEditor = () => {
    setEditingNote(null);
  };

  const saveNote = () => {
    const updatedNotes = [...notes];
    updatedNotes[editingNote] = {
      ...updatedNotes[editingNote],
      title: editedTitle,
      content: editedContent,
      color: selectedColor,
      lastEdited: new Date().toISOString() // Add a timestamp for when the note was last edited
    };
    setNotes(updatedNotes);
    // Explicitly save to localStorage here as well for extra safety
    localStorage.setItem('savedNotes', JSON.stringify(updatedNotes));
    closeNoteEditor();
  };

  const applyFormatting = (command, value = null) => {
    document.execCommand(command, false, value);
    // Focus back on the content editable div after applying formatting
    document.querySelector('.notepad-container').focus();
  };

  return (
    <div className={`${theme.card} rounded shadow p-4`}>
      <h2 className="h4 fw-semibold mb-4">Your Notes</h2>
      <div className="d-flex flex-column gap-3">
        {notes.length > 0 ? (
          notes.map((note, index) => (
            <div 
              key={index} 
              className={`p-3 rounded`} 
              style={{ 
                backgroundColor: note.color || (darkMode ? 'rgba(108, 117, 125, 0.25)' : '#f8f9fa')
              }}
            >
              <div className="d-flex justify-content-between align-items-start">
                <div>
                  <h3 className="fs-6 fw-medium mb-0">{note.title}</h3>
                  <p className={`${theme.subtleText} small mb-0`}>{note.reference}</p>
                </div>
                <div className="d-flex flex-column align-items-end">
                  <p className={`${theme.subtleText} small mb-1`}>{note.date}</p>
                  <div className="d-flex gap-1">
                    <button 
                      className={`btn btn-link ${theme.subtleText} p-1`}
                      onClick={() => openNoteEditor(note, index)}
                    >
                      <Edit size={16} />
                    </button>
                    <button 
                      className={`btn btn-link ${theme.subtleText} p-1`}
                      onClick={() => {
                        const updatedNotes = notes.filter((_, i) => i !== index);
                        setNotes(updatedNotes);
                        // Also update localStorage when deleting
                        localStorage.setItem('savedNotes', JSON.stringify(updatedNotes));
                      }}
                    >
                      <X size={16} />
                    </button>
                  </div>
                </div>
              </div>
              <p className="mt-2 small mb-0" dir="ltr" style={{ textAlign: 'left' }}>
                {note.content || "Click to view your notes for this passage..."}
              </p>
            </div>
          ))
        ) : (
          <p className="text-center py-4">No notes yet. Add some from the home page!</p>
        )}
      </div>

      {/* Note Editor Modal */}
      {editingNote !== null && (
        <div className="position-fixed top-0 start-0 w-100 h-100 d-flex justify-content-center align-items-center" style={{ backgroundColor: 'rgba(0,0,0,0.5)', zIndex: 1050 }}>
          <div className={`${darkMode ? 'bg-dark' : 'bg-white'} rounded-3 shadow-lg p-0 m-3`} style={{ width: '90%', maxWidth: '800px', maxHeight: '90vh', overflow: 'hidden' }}>
            <div className="d-flex justify-content-between align-items-center border-bottom p-3">
              <h3 className="fs-5 fw-semibold mb-0">Edit Note</h3>
              <button 
                className="btn btn-link text-secondary border-0 p-1"
                onClick={closeNoteEditor}
              >
                <X size={20} />
              </button>
            </div>

            <div className="p-3">
              <div className="mb-3">
                <label className="form-label">Title</label>
                <input 
                  type="text" 
                  className={`form-control ${darkMode ? 'bg-dark text-light' : ''}`}
                  value={editedTitle}
                  onChange={(e) => setEditedTitle(e.target.value)}
                />
              </div>

              <div className="mb-3">
                <label className="form-label">Note Color</label>
                <div className="d-flex gap-2 flex-wrap mb-2">
                  {colorOptions.map((color) => (
                    <button
                      key={color.value}
                      className={`btn p-0 rounded-circle ${selectedColor === color.value ? 'border border-3 border-primary' : 'border'}`}
                      style={{ 
                        backgroundColor: color.value, 
                        width: '32px', 
                        height: '32px',
                        cursor: 'pointer'
                      }}
                      onClick={() => setSelectedColor(color.value)}
                      title={color.name}
                    />
                  ))}
                </div>
              </div>

              <div className="mb-3">
                <label className="form-label">Format</label>
                <div className={`btn-group mb-2 ${darkMode ? 'btn-group-dark' : ''}`}>
                  <button type="button" className="btn btn-sm btn-outline-secondary" onClick={() => applyFormatting('bold')}>
                    <Bold size={16} />
                  </button>
                  <button type="button" className="btn btn-sm btn-outline-secondary" onClick={() => applyFormatting('italic')}>
                    <Italic size={16} />
                  </button>
                  <button type="button" className="btn btn-sm btn-outline-secondary" onClick={() => applyFormatting('underline')}>
                    <Underline size={16} />
                  </button>
                  <button type="button" className="btn btn-sm btn-outline-secondary" onClick={() => applyFormatting('insertUnorderedList')}>
                    <List size={16} />
                  </button>
                  <button type="button" className="btn btn-sm btn-outline-secondary" onClick={() => applyFormatting('justifyLeft')}>
                    <AlignLeft size={16} />
                  </button>
                  <button type="button" className="btn btn-sm btn-outline-secondary" onClick={() => applyFormatting('justifyCenter')}>
                    <AlignCenter size={16} />
                  </button>
                  <button type="button" className="btn btn-sm btn-outline-secondary" onClick={() => applyFormatting('justifyRight')}>
                    <AlignRight size={16} />
                  </button>
                </div>
              </div>

              // Replace the content editable div with this
<div 
  className="form-control notepad-container"
  style={{ 
    minHeight: '200px', 
    backgroundColor: selectedColor,
    backgroundImage: 'linear-gradient(transparent, transparent 27px, #ccc 27px, #ccc 28px, transparent 28px)',
    backgroundSize: '100% 28px',
    backgroundPosition: '0 14px',
    lineHeight: '28px',
    paddingTop: '14px',
    paddingLeft: '20px',
    paddingRight: '20px',
    resize: 'vertical',
    border: '1px solid #ddd',
    borderRadius: '8px',
    fontFamily: '"Roboto", sans-serif',
    fontSize: '16px',
    direction: 'ltr !important',
    textAlign: 'left !important',
    unicodeBidi: 'isolate-override'
  }}
  contentEditable
  dir="ltr"
  dangerouslySetInnerHTML={{ __html: editedContent }}
  onInput={(e) => {
    // Force direction on input
    e.currentTarget.style.direction = 'ltr';
    e.currentTarget.style.textAlign = 'left';
    e.currentTarget.setAttribute('dir', 'ltr');
    setEditedContent(e.currentTarget.innerHTML);
  }}
  onKeyDown={(e) => {
    // Also ensure direction is maintained while typing
    e.currentTarget.style.direction = 'ltr';
    e.currentTarget.style.textAlign = 'left';
  }}
/>
            </div>

            <div className="d-flex justify-content-end gap-2 p-3 border-top">
              <button 
                className="btn btn-secondary"
                onClick={closeNoteEditor}
              >
                Cancel
              </button>
              <button 
                className="btn btn-primary"
                onClick={saveNote}
              >
                <Save size={16} className="me-2" />
                Save Note
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default NotesTab;
