import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Search, Book, BookOpen, Bookmark, Edit, Globe, Sun, Moon, Home, Menu, X, ChevronRight, ArrowLeft } from 'lucide-react';
import 'bootstrap/dist/css/bootstrap.min.css';
import ChapullePrayer from './ChapullePrayer';

const BibleApp = () => {
  const [darkMode, setDarkMode] = useState(false);
  const [menuOpen, setMenuOpen] = useState(false);
  const [activeTab, setActiveTab] = useState('home');
  const [verseOfDay, setVerseOfDay] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [selectedBook, setSelectedBook] = useState(null);
  const [selectedChapter, setSelectedChapter] = useState(null);
  const [chapterContent, setChapterContent] = useState(null);
  const [translation, setTranslation] = useState('NIV');
  const [searchQuery, setSearchQuery] = useState('');
  const [searchResults, setSearchResults] = useState([]);
  const [showAllOldTestament, setShowAllOldTestament] = useState(false);
  const [showAllNewTestament, setShowAllNewTestament] = useState(false);

  // Bible books data
  const bibleBooks = {
    oldTestament: [
      'Genesis', 'Exodus', 'Leviticus', 'Numbers', 'Deuteronomy',
      'Joshua', 'Judges', 'Ruth', '1 Samuel', '2 Samuel',
      '1 Kings', '2 Kings', '1 Chronicles', '2 Chronicles',
      'Ezra', 'Nehemiah', 'Esther', 'Job', 'Psalms', 'Proverbs',
      'Ecclesiastes', 'Song of Solomon', 'Isaiah', 'Jeremiah',
      'Lamentations', 'Ezekiel', 'Daniel', 'Hosea', 'Joel',
      'Amos', 'Obadiah', 'Jonah', 'Micah', 'Nahum', 'Habakkuk',
      'Zephaniah', 'Haggai', 'Zechariah', 'Malachi'
    ],
    newTestament: [
      'Matthew', 'Mark', 'Luke', 'John', 'Acts',
      'Romans', '1 Corinthians', '2 Corinthians', 'Galatians',
      'Ephesians', 'Philippians', 'Colossians', '1 Thessalonians',
      '2 Thessalonians', '1 Timothy', '2 Timothy', 'Titus',
      'Philemon', 'Hebrews', 'James', '1 Peter', '2 Peter',
      '1 John', '2 John', '3 John', 'Jude', 'Revelation'
    ]
  };

  // Sample recent books
  const recentBooks = [
    { name: "Psalms", chapter: 23 },
    { name: "John", chapter: 3 },
    { name: "Romans", chapter: 8 }
  ];

  // Sample bookmarks
  const [bookmarks, setBookmarks] = useState([
    { reference: "Philippians 4:13", preview: "I can do all things through Christ who strengthens me." },
    { reference: "Jeremiah 29:11", preview: "For I know the plans I have for you, declares the LORD..." }
  ]);

  // Sample notes
  const [notes, setNotes] = useState([
    { reference: "Matthew 5:3-12", title: "Beatitudes study", date: "2025-03-25" },
    { reference: "Romans 12:1-2", title: "Thoughts on renewal", date: "2025-03-20" }
  ]);

  // Fetch verse of the day
  useEffect(() => {
    const fetchVerseOfDay = async () => {
      try {
        setLoading(true);

        // Using OurManna's official API
        const response = await axios.get(
          `https://beta.ourmanna.com/api/v1/get`,
          {
            params: {
              format: 'json',
              order: 'random', // or use 'votd' for verse of the day
              version: translation || 'kjv' // default to KJV if not specified
            }
          }
        );

        const verseData = response.data?.verse?.details;

        if (verseData) {
          setVerseOfDay({
            text: verseData.text,
            reference: verseData.reference,
            translation: verseData.version || translation || 'KJV'
          });
        } else {
          // Fallback if API response is unexpected
          setVerseOfDay({
            text: "But those who hope in the LORD will renew their strength. They will soar on wings like eagles; they will run and not grow weary, they will walk and not be faint.",
            reference: "Isaiah 40:31",
            translation: "NIV"
          });
        }
      } catch (err) {
        console.error("Error fetching verse of the day:", err);
        setError("Failed to fetch verse of the day. Using local verse.");
        // Fallback verse
        setVerseOfDay({
          text: "Trust in the LORD with all your heart and lean not on your own understanding.",
          reference: "Proverbs 3:5",
          translation: "NIV"
        });
      } finally {
        setLoading(false);
      }
    };

    fetchVerseOfDay();
  }, [translation]);

  // Update your fetchChapterContent function
  const fetchChapterContent = async () => {
    if (!selectedBook || !selectedChapter) return;

    try {
      setLoading(true);
      setError(null);

      // Format the book name for the API (some books need special handling)
      let formattedBook = selectedBook.replace(/ /g, '').replace(/1/g, '1_').replace(/2/g, '2_').replace(/3/g, '3_');

      // Use the ESV API (more reliable)
      const apiUrl = `https://api.esv.org/v3/passage/text/?q=${formattedBook}+${selectedChapter}`;

      const response = await axios.get(apiUrl, {
        headers: {
          'Authorization': 'Token YOUR_API_KEY_HERE' // You'll need to get a free API key from esv.org
        }
      });

      if (response.data && response.data.passages) {
        setChapterContent({
          text: response.data.passages[0],
          reference: `${selectedBook} ${selectedChapter}`,
          translation: translation
        });
      } else {
        setError("No content found for this chapter");
      }
    } catch (err) {
      console.error("Fetch error:", err);
      setError(`Failed to load chapter. Please try another translation.`);

      // Fallback to a simpler API if the primary one fails
      try {
        const fallbackResponse = await axios.get(
          `https://bible-api.com/${selectedBook.replace(/ /g, '')}+${selectedChapter}`
        );
        if (fallbackResponse.data) {
          setChapterContent({
            text: fallbackResponse.data.text,
            reference: fallbackResponse.data.reference,
            translation: 'NIV' // Default for this API
          });
          setError(null); // Clear error if fallback succeeds
        }
      } catch (fallbackError) {
        console.error("Fallback API also failed:", fallbackError);
      }
    } finally {
      setLoading(false);
    }
  };

// Call this effect whenever selectedBook or selectedChapter changes
useEffect(() => {
  fetchChapterContent();
}, [selectedBook, selectedChapter, translation]);

  // Search Bible verses
  const handleSearch = async () => {
    if (!searchQuery.trim()) return;

    try {
      setLoading(true);
      const response = await axios.get(
        `https://www.biblegateway.com/passage/?search=${encodeURIComponent(searchQuery)}&version=${translation}&format=json`
      );

      if (response.data) {
        setSearchResults(response.data.passages || []);
        setActiveTab('search');
      }
    } catch (err) {
      console.error("Error searching verses:", err);
      setError("Failed to search verses. Please try again.");
    } finally {
      setLoading(false);
    }
  };

  // Add bookmark
  const addBookmark = (reference, text) => {
    if (!bookmarks.some(b => b.reference === reference)) {
      setBookmarks([...bookmarks, { reference, preview: text.substring(0, 50) + '...' }]);
    }
  };

  // Toggle dark mode
  const toggleDarkMode = () => setDarkMode(!darkMode);

  // Toggle menu on mobile
  const toggleMenu = () => setMenuOpen(!menuOpen);

  // Define themes based on dark mode
  const theme = {
    body: darkMode ? 'bg-dark text-light' : 'bg-light text-dark',
    header: darkMode ? 'bg-dark' : 'bg-primary',
    card: darkMode ? 'bg-dark text-light border-secondary' : 'bg-white text-dark',
    accentText: darkMode ? 'text-info' : 'text-primary',
    accentBg: darkMode ? 'bg-info text-dark' : 'bg-primary text-white',
    subtleText: darkMode ? 'text-secondary' : 'text-secondary',
    buttonHover: darkMode ? 'btn-hover-light' : 'btn-hover-primary',
    border: darkMode ? 'border-secondary' : 'border-light'
  };

  return (
    <div className={`min-vh-100 d-flex flex-column ${theme.body}`} data-bs-theme={darkMode ? 'dark' : 'light'}>
      {/* Header */}
      <header className={`${theme.header} text-white shadow`}>
        <div className="container-fluid px-4 py-3 d-flex align-items-center justify-content-between">
          <div className="d-flex align-items-center">
            <button onClick={toggleMenu} className="btn btn-link text-white d-md-none me-2 p-0">
              {menuOpen ? <X size={24} /> : <Menu size={24} />}
            </button>
            <h1 className="h5 fw-bold mb-0">Scripture Daily</h1>
          </div>

          <div className="d-flex align-items-center gap-3">
            <button onClick={toggleDarkMode} className="btn btn-link text-white p-1">
              {darkMode ? <Sun size={20} /> : <Moon size={20} />}
            </button>
            <div className="position-relative">
              <input
                type="text"
                placeholder="Search Bible..."
                className={`form-control form-control-sm ${darkMode ? 'bg-secondary text-white' : ''}`}
                style={{ width: '150px' }}
                value={searchQuery}
                onChange={(e) => setSearchQuery(e.target.value)}
                onKeyPress={(e) => e.key === 'Enter' && handleSearch()}
              />
              <button 
                onClick={handleSearch}
                className="btn btn-link text-white position-absolute end-0 top-0 p-1"
                style={{ transform: 'translateY(2px)' }}
              >
                <Search size={16} />
              </button>
            </div>
          </div>
        </div>
      </header>

      {/* Main Content Area with Sidebar */}
      <div className="d-flex flex-grow-1">
        {/* Sidebar - hidden on mobile unless menu is open */}
        <aside className={`${theme.card} shadow position-fixed start-0 top-0 mt-5 pt-4 h-100 z-3 d-md-block ${menuOpen ? 'd-block' : 'd-none'}`} style={{width: '250px', overflowY: 'auto', paddingTop: '60px'}}>
          <nav className="p-3">
            <ul className="list-unstyled mb-0">
              <li className="mb-2">
                <button 
                  onClick={() => {setActiveTab('home'); setMenuOpen(false);}}
                  className={`btn w-100 text-start d-flex align-items-center p-3 rounded ${activeTab === 'home' ? theme.accentBg : 'btn-outline-secondary border-0'}`}
                >
                  <Home size={18} className="me-3" />
                  <span>Home</span>
                </button>
              </li>
              <li className="mb-2">
                <button 
                  onClick={() => {setActiveTab('books'); setMenuOpen(false); setSelectedBook(null);}}
                  className={`btn w-100 text-start d-flex align-items-center p-3 rounded ${activeTab === 'books' ? theme.accentBg : 'btn-outline-secondary border-0'}`}
                >
                  <Book size={18} className="me-3" />
                  <span>Books</span>
                </button>
              </li>
              <li className="mb-2">
                <button 
                  onClick={() => {setActiveTab('bookmarks'); setMenuOpen(false);}}
                  className={`btn w-100 text-start d-flex align-items-center p-3 rounded ${activeTab === 'bookmarks' ? theme.accentBg : 'btn-outline-secondary border-0'}`}
                >
                  <Bookmark size={18} className="me-3" />
                  <span>Bookmarks</span>
                </button>
              </li>
              <li className="mb-2">
  <ChapullePrayer 
    theme={theme} 
    setActiveTab={setActiveTab} 
    setMenuOpen={setMenuOpen}
    activeTab={activeTab}
  />
</li>
              <li className="mb-2">
                <button 
                  onClick={() => {setActiveTab('notes'); setMenuOpen(false);}}
                  className={`btn w-100 text-start d-flex align-items-center p-3 rounded ${activeTab === 'notes' ? theme.accentBg : 'btn-outline-secondary border-0'}`}
                >
                  <Edit size={18} className="me-3" />
                  <span>Notes</span>
                </button>
              </li>
              <li className="mb-2">
                <button 
                  onClick={() => {setActiveTab('language'); setMenuOpen(false);}}
                  className={`btn w-100 text-start d-flex align-items-center p-3 rounded ${activeTab === 'language' ? theme.accentBg : 'btn-outline-secondary border-0'}`}
                >
                  <Globe size={18} className="me-3" />
                  <span>Language</span>
                </button>
              </li>
            </ul>
          </nav>
        </aside>

        {/* Main Content */}
        <main className="flex-grow-1 p-4 overflow-auto pb-5 ms-md-auto" style={{width: 'calc(100% - 250px)', marginLeft: '0', marginRight: '0'}}>
          <div className="container-fluid mx-auto" style={{maxWidth: '1000px'}}>
            {loading && (
              <div className="text-center py-5">
                <div className="spinner-border text-primary" role="status">
                  <span className="visually-hidden">Loading...</span>
                </div>
                <p className="mt-2">Loading content...</p>
              </div>
            )}

            {error && (
              <div className={`alert alert-danger ${loading ? 'd-none' : ''}`}>
                {error}
              </div>
            )}

            {!loading && activeTab === 'home' && (
              <>
                {/* Verse of the Day */}
                <section className={`mb-4 p-4 ${theme.card} rounded shadow`}>
                  <h2 className="h5 fw-semibold mb-1">Verse of the Day</h2>
                  <p className={`${theme.subtleText} small mb-3`}>
                    {new Date().toLocaleDateString('en-US', {weekday: 'long', year: 'numeric', month: 'long', day: 'numeric'})}
                  </p>
                  <div className="border-start border-4 border-primary ps-3 py-1 mb-3">
                    {verseOfDay ? (
                      <>
                        <p className="fs-5 fst-italic mb-2">{verseOfDay.text}</p>
                        <p className={`${theme.accentText} fw-semibold`}>
                          {verseOfDay.reference} ({verseOfDay.translation})
                        </p>
                      </>
                    ) : (
                      <p>Loading verse of the day...</p>
                    )}
                  </div>
                  <div className="d-flex justify-content-end gap-2">
                    <button 
                      className={`btn btn-link ${theme.subtleText} p-1`}
                      onClick={() => verseOfDay && addBookmark(verseOfDay.reference, verseOfDay.text)}
                    >
                      <Bookmark size={18} />
                    </button>
                    <button className={`btn btn-link ${theme.subtleText} p-1`}>
                      <Edit size={18} />
                    </button>
                  </div>
                </section>

                {/* Quick Access */}
                <section className="mb-4">
                  <h2 className="h5 fw-semibold mb-3">Continue Reading</h2>
                  <div className="row row-cols-1 row-cols-md-3 g-3">
                    {recentBooks.map((book, index) => (
                      <div key={index} className="col">
                        <div 
                          className={`${theme.card} p-3 rounded shadow d-flex align-items-center justify-content-between cursor-pointer`}
                          onClick={() => {
                            setSelectedBook(book.name);
                            setSelectedChapter(book.chapter);
                            setActiveTab('books');
                          }}
                        >
                          <div className="d-flex align-items-center">
                            <BookOpen className={theme.accentText} size={20} />
                            <div className="ms-3">
                              <h3 className="fs-6 fw-medium mb-0">{book.name}</h3>
                              <p className={`${theme.subtleText} mb-0 small`}>Chapter {book.chapter}</p>
                            </div>
                          </div>
                          <ChevronRight size={18} className={theme.subtleText} />
                        </div>
                      </div>
                    ))}
                  </div>
                </section>

                {/* Bookmarks and Notes Preview */}
                <div className="row row-cols-1 row-cols-md-2 g-4">
                  <div className="col">
                    <div className="d-flex justify-content-between align-items-center mb-3">
                      <h2 className="h5 fw-semibold mb-0">Bookmarks</h2>
                      <button 
                        className={`btn btn-link ${theme.accentText} p-0 small`}
                        onClick={() => setActiveTab('bookmarks')}
                      >
                        View all
                      </button>
                    </div>
                    <div className={`${theme.card} rounded shadow`}>
                      {bookmarks.slice(0, 2).map((bookmark, index) => (
                        <div key={index} className={`p-3 ${index > 0 ? 'border-top' : ''}`}>
                          <div className="d-flex justify-content-between">
                            <h3 className="fs-6 fw-medium mb-0">{bookmark.reference}</h3>
                            <Bookmark size={16} className={theme.accentText} />
                          </div>
                          <p className={`${theme.subtleText} small mt-1 text-truncate mb-0`}>{bookmark.preview}</p>
                        </div>
                      ))}
                    </div>
                  </div>

                  <div className="col">
                    <div className="d-flex justify-content-between align-items-center mb-3">
                      <h2 className="h5 fw-semibold mb-0">Notes</h2>
                      <button 
                        className={`btn btn-link ${theme.accentText} p-0 small`}
                        onClick={() => setActiveTab('notes')}
                      >
                        View all
                      </button>
                    </div>
                    <div className={`${theme.card} rounded shadow`}>
                      {notes.slice(0, 2).map((note, index) => (
                        <div key={index} className={`p-3 ${index > 0 ? 'border-top' : ''}`}>
                          <div className="d-flex justify-content-between">
                            <h3 className="fs-6 fw-medium mb-0">{note.title}</h3>
                            <p className={`${theme.subtleText} small`}>{note.date}</p>
                          </div>
                          <p className={`${theme.subtleText} small mt-1 mb-0`}>{note.reference}</p>
                        </div>
                      ))}
                    </div>
                  </div>
                </div>
              </>
            )}

            {!loading && activeTab === 'books' && (
              <div className={`${theme.card} rounded shadow p-4`}>
                {!selectedBook ? (
                  <>
                    <h2 className="h4 fw-semibold mb-4">Bible Books</h2>

           {/* Old Testament */}
              <div className="mb-4">
                <h3 className={`${theme.accentText} fw-medium mb-3`}>Old Testament</h3>
                <div className="row row-cols-2 row-cols-sm-3 row-cols-md-4 g-2">
                  {/* Show either all books or first 8 based on state */}
                  {bibleBooks.oldTestament
                    .slice(0, showAllOldTestament ? bibleBooks.oldTestament.length : 8)
                    .map(book => (
                      <div key={book} className="col">
                        <button 
                          className="btn btn-outline-secondary border-0 text-start w-100 p-2 rounded"
                          onClick={() => setSelectedBook(book)}
                        >
                          {book}
                        </button>
                      </div>
                    ))}

                  {/* Always show the button, but change its text and function */}
                  <div className="col">
                    <button 
                      className="btn btn-outline-secondary border-0 text-start w-100 p-2 rounded"
                      onClick={() => setShowAllOldTestament(!showAllOldTestament)}
                    >
                      {showAllOldTestament ? "Show Less" : "View All..."}
                    </button>
                  </div>
                </div>
              </div>         
               {/* New Testament */}
              <div className="mb-4">
                <h3 className={`${theme.accentText} fw-medium mb-3`}>New Testament</h3>
                <div className="row row-cols-2 row-cols-sm-3 row-cols-md-4 g-2">
                  {/* Show either all books or first 8 based on state */}
                  {bibleBooks.newTestament
                    .slice(0, showAllNewTestament ? bibleBooks.newTestament.length : 8)
                    .map(book => (
                      <div key={book} className="col">
                        <button 
                          className="btn btn-outline-secondary border-0 text-start w-100 p-2 rounded"
                          onClick={() => setSelectedBook(book)}
                        >
                          {book}
                        </button>
                      </div>
                    ))}

                  {/* Always show the toggle button */}
                  <div className="col">
                    <button 
                      className="btn btn-outline-secondary border-0 text-start w-100 p-2 rounded"
                      onClick={() => setShowAllNewTestament(!showAllNewTestament)}
                    >
                      {showAllNewTestament ? "Show Less" : "View All..."}
                    </button>
                  </div>
                </div>
              </div>
                  </>
                ) : !selectedChapter ? (
                  <>
                    <div className="d-flex align-items-center mb-4">
                      <button 
                        className="btn btn-link p-0 me-3"
                        onClick={() => setSelectedBook(null)}
                      >
                        <ArrowLeft size={20} />
                      </button>
                      <h2 className="h4 fw-semibold mb-0">{selectedBook}</h2>
                    </div>

                    <div className="row row-cols-3 row-cols-sm-4 row-cols-md-6 g-2">
                      {Array.from({ length: 10 }, (_, i) => i + 1).map(chapter => (
                        <div key={chapter} className="col">
                          <button 
                            className={`btn ${theme.card} w-100 p-2 rounded`}
                            onClick={() => setSelectedChapter(chapter)}
                          >
                            {chapter}
                          </button>
                        </div>
                      ))}
                    </div>
                  </>
                ) : (
                  <>
                    <div className="d-flex align-items-center mb-4">
                      <button 
                        className="btn btn-link p-0 me-3"
                        onClick={() => setSelectedChapter(null)}
                      >
                        <ArrowLeft size={20} />
                      </button>
                      <h2 className="h4 fw-semibold mb-0">
                        {selectedBook} {selectedChapter}
                      </h2>
                    </div>

                    {chapterContent ? (
                      <div className="bible-text">
                        {chapterContent.text.split('\n\n').map((paragraph, index) => (
                          <p key={index} className="mb-3">
                            {paragraph}
                          </p>
                        ))}
                        <p className={`${theme.subtleText} mt-3 small`}>
                          {chapterContent.reference} ({chapterContent.translation})
                        </p>
                      </div>
                    ) : (
                      <p>Loading chapter content...</p>
                    )}
                    <div className="d-flex justify-content-between mt-4">
                      <button 
                        className="btn btn-outline-primary"
                        onClick={() => setSelectedChapter(prev => Math.max(1, prev - 1))}
                        disabled={selectedChapter <= 1}
                      >
                        Previous Chapter
                      </button>
                      <button 
                        className="btn btn-outline-primary"
                        onClick={() => setSelectedChapter(prev => prev + 1)}
                      >
                        Next Chapter
                      </button>
                    </div>
                  </>
                )}
              </div>
            )}

            {!loading && activeTab === 'bookmarks' && (
              <div className={`${theme.card} rounded shadow p-4`}>
                <h2 className="h4 fw-semibold mb-4">Your Bookmarks</h2>
                <div className="d-flex flex-column gap-3">
                  {bookmarks.length > 0 ? (
                    bookmarks.map((bookmark, index) => (
                      <div key={index} className={`p-3 border-start border-4 border-primary rounded ${darkMode ? 'bg-secondary bg-opacity-25' : 'bg-light'}`}>
                        <div className="d-flex justify-content-between">
                          <h3 className="fs-6 fw-medium mb-0">{bookmark.reference}</h3>
                          <div className="d-flex gap-2">
                            <button className={`btn btn-link ${theme.subtleText} p-1`}>
                              <Edit size={16} />
                            </button>
                            <button 
                              className={`btn btn-link ${theme.subtleText} p-1`}
                              onClick={() => setBookmarks(bookmarks.filter((_, i) => i !== index))}
                            >
                              <X size={16} />
                            </button>
                          </div>
                        </div>
                        <p className="mt-2 mb-1">{bookmark.preview}</p>
                        <button className={`btn btn-link ${theme.accentText} p-0 small`}>
                          Read full passage
                        </button>
                      </div>
                    ))
                  ) : (
                    <p className="text-center py-4">No bookmarks yet. Add some from the home page!</p>
                  )}
                </div>
              </div>
            )}

            {!loading && activeTab === 'notes' && (
              <div className={`${theme.card} rounded shadow p-4`}>
                <h2 className="h4 fw-semibold mb-4">Your Notes</h2>
                <div className="d-flex flex-column gap-3">
                  {notes.length > 0 ? (
                    notes.map((note, index) => (
                      <div key={index} className={`p-3 rounded ${darkMode ? 'bg-secondary bg-opacity-25' : 'bg-light'}`}>
                        <div className="d-flex justify-content-between align-items-start">
                          <div>
                            <h3 className="fs-6 fw-medium mb-0">{note.title}</h3>
                            <p className={`${theme.subtleText} small mb-0`}>{note.reference}</p>
                          </div>
                          <div className="d-flex flex-column align-items-end">
                            <p className={`${theme.subtleText} small mb-1`}>{note.date}</p>
                            <div className="d-flex gap-1">
                              <button className={`btn btn-link ${theme.subtleText} p-1`}>
                                <Edit size={16} />
                              </button>
                              <button 
                                className={`btn btn-link ${theme.subtleText} p-1`}
                                onClick={() => setNotes(notes.filter((_, i) => i !== index))}
                              >
                                <X size={16} />
                              </button>
                            </div>
                          </div>
                        </div>
                        <p className="mt-2 small mb-0">Click to view your notes for this passage...</p>
                      </div>
                    ))
                  ) : (
                    <p className="text-center py-4">No notes yet. Add some from the home page!</p>
                  )}
                </div>
              </div>
            )}

            {!loading && activeTab === 'language' && (
              <div className={`${theme.card} rounded shadow p-4`}>
                <h2 className="h4 fw-semibold mb-4">Language Settings</h2>

                <div className="mb-4">
                  <h3 className="fs-5 fw-medium mb-3">App Language</h3>
                  <div className={`${darkMode ? 'bg-secondary bg-opacity-25' : 'bg-light'} p-3 rounded`}>
                    <div className="row row-cols-1 row-cols-sm-2 g-3">
                      {['English', 'Spanish', 'French', 'German', 'Portuguese', 'Chinese', 'Arabic', 'Hindi'].map(lang => (
                        <div key={lang} className="col d-flex align-items-center">
                          <div className="form-check">
                            <input 
                              className="form-check-input" 
                              type="radio" 
                              name="appLanguage" 
                              id={lang} 
                              defaultChecked={lang === 'English'} 
                            />
                            <label className="form-check-label" htmlFor={lang}>
                              {lang}
                            </label>
                          </div>
                        </div>
                      ))}
                    </div>
                  </div>
                </div>

                <div>
                  <h3 className="fs-5 fw-medium mb-3">Bible Translation</h3>
                  <div className={`${darkMode ? 'bg-secondary bg-opacity-25' : 'bg-light'} p-3 rounded`}>
                    <div className="row row-cols-1 row-cols-sm-2 g-3">
                      {['NIV', 'KJV', 'ESV', 'NASB', 'NLT'].map(trans => (
                        <div key={trans} className="col d-flex align-items-center">
                          <div className="form-check">
                            <input 
                              className="form-check-input" 
                              type="radio" 
                              name="bibleTranslation" 
                              id={trans}
                              checked={translation === trans}
                              onChange={() => setTranslation(trans)}
                            />
                            <label className="form-check-label" htmlFor={trans}>
                              {trans}
                            </label>
                          </div>
                        </div>
                      ))}
                    </div>
                    <button className={`btn btn-link ${theme.accentText} p-0 small mt-2`}>
                      View more translations
                    </button>
                  </div>
                </div>
              </div>
            )}

            {!loading && activeTab === 'search' && (
              <div className={`${theme.card} rounded shadow p-4`}>
                <div className="d-flex align-items-center mb-4">
                  <button 
                    className="btn btn-link p-0 me-3"
                    onClick={() => setActiveTab('home')}
                  >
                    <ArrowLeft size={20} />
                  </button>
                  <h2 className="h4 fw-semibold mb-0">
                    Search Results for "{searchQuery}"
                  </h2>
                </div>

                {searchResults.length > 0 ? (
                  <div className="search-results">
                    {searchResults.map((result, index) => (
                      <div key={index} className="mb-4">
                        <h3 className="h5 fw-semibold mb-2">
                          {result.reference} ({result.version})
                        </h3>
                        <div className="bible-text">
                          {result.text.split('\n').map((paragraph, pIndex) => (
                            <p key={pIndex} className="mb-3">
                              {paragraph}
                            </p>
                          ))}
                        </div>
                        <div className="d-flex justify-content-end gap-2">
                          <button 
                            className={`btn btn-link ${theme.subtleText} p-1`}
                            onClick={() => addBookmark(result.reference, result.text)}
                          >
                            <Bookmark size={16} />
                          </button>
                          <button className={`btn btn-link ${theme.subtleText} p-1`}>
                            <Edit size={16} />
                          </button>
                        </div>
                      </div>
                    ))}
                  </div>
                ) : (
                  <p className="text-center py-4">No results found for your search.</p>
                )}
              </div>
            )}
          </div>
        </main>
      </div>

      {/* Bottom Navigation for Mobile */}
      <div className={`d-md-none fixed-bottom ${theme.card} shadow border-top`}>
        <div className="d-flex justify-content-around">
          <button 
            onClick={() => setActiveTab('home')}
            className={`btn btn-link ${activeTab === 'home' ? theme.accentText : theme.subtleText} py-2 px-3 d-flex flex-column align-items-center`}
          >
            <Home size={20} />
            <span className="small mt-1">Home</span>
          </button>
          <button 
            onClick={() => setActiveTab('books')}
            className={`btn btn-link ${activeTab === 'books' ? theme.accentText : theme.subtleText} py-2 px-3 d-flex flex-column align-items-center`}
          >
            <Book size={20} />
            <span className="small mt-1">Books</span>
          </button>
          <button 
            onClick={() => setActiveTab('bookmarks')}
            className={`btn btn-link ${activeTab === 'bookmarks' ? theme.accentText : theme.subtleText} py-2 px-3 d-flex flex-column align-items-center`}
          >
            <Bookmark size={20} />
            <span className="small mt-1">Bookmarks</span>
          </button>
          <button 
            onClick={() => setActiveTab('notes')}
            className={`btn btn-link ${activeTab === 'notes' ? theme.accentText : theme.subtleText} py-2 px-3 d-flex flex-column align-items-center`}
          >
            <Edit size={20} />
            <span className="small mt-1">Notes</span>
          </button>
        </div>
      </div>
    </div>
  );
};

export default BibleApp;
