import React, { useState, useRef } from 'react';
import { Bookmark, Check, Volume2, Globe, ChevronDown, Share2, Home, Search, Heart, User } from 'lucide-react';
import { doc, setDoc, arrayUnion } from "firebase/firestore";
import { auth, db } from '../firebase';

// Language options
const LANGUAGES = {
  ENGLISH: 'en',
  KINYARWANDA: 'rw'
};

// Language display names
const LANGUAGE_NAMES = {
  [LANGUAGES.ENGLISH]: 'English',
  [LANGUAGES.KINYARWANDA]: 'Kinyarwanda'
};

// Speech synthesis language codes
const SPEECH_LANGUAGES = {
  [LANGUAGES.ENGLISH]: 'en-US',
  [LANGUAGES.KINYARWANDA]: 'rw-RW'  // Note: Check if this is supported in the browser
};

const HomeTab = ({ 
  theme,
  verseOfDay, 
  addBookmark,
  fetchVerseOfDay
}) => {
  const [bookmarked, setBookmarked] = useState(false);
  const [bookmarkLoading, setBookmarkLoading] = useState(false);
  const [isPlaying, setIsPlaying] = useState(false);
  const [selectedLanguage, setSelectedLanguage] = useState(LANGUAGES.ENGLISH);
  const [dropdownOpen, setDropdownOpen] = useState(false);
  const speechSynthRef = useRef(window.speechSynthesis);
  const dropdownRef = useRef(null);

  // Toggle dropdown
  const toggleDropdown = () => {
    setDropdownOpen(!dropdownOpen);
  };

  // Close dropdown when clicking outside
  React.useEffect(() => {
    const handleClickOutside = (event) => {
      if (dropdownRef.current && !dropdownRef.current.contains(event.target)) {
        setDropdownOpen(false);
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, []);

  // Handle language change
  const handleLanguageChange = (language) => {
    if (language === selectedLanguage) return;

    setSelectedLanguage(language);
    setDropdownOpen(false);

    // Fetch verse in the selected language
    if (fetchVerseOfDay) {
      fetchVerseOfDay(language);
    }
  };

  // Function to handle bookmarking the verse of the day
  const handleBookmarkVOTD = async () => {
    if (!verseOfDay || bookmarked || !auth.currentUser) return;

    try {
      setBookmarkLoading(true);

      // Parse the reference to extract book, chapter, and verse
      const referenceText = verseOfDay.reference;

      // Parse book, chapter and verse details from the reference
      const matches = referenceText.match(/^((?:\d\s)?[A-Za-z]+)\s+(\d+):(\d+)(?:-\d+)?$/);

      if (!matches) {
        console.error("Could not parse reference:", referenceText);
        return;
      }

      const [_, book, chapter, verse] = matches;

      // Create bookmark data
      const bookmarkData = {
        book: book,
        chapter: parseInt(chapter),
        lineNumber: parseInt(verse),
        text: verseOfDay.text,
        reference: verseOfDay.reference,
        language: selectedLanguage,
        timestamp: new Date()
      };

      // Add to Firebase
      const userDocRef = doc(db, "users", auth.currentUser.uid);
      await setDoc(userDocRef, {
        bookmarks: arrayUnion(bookmarkData)
      }, { merge: true });

      // Call the original addBookmark function for local state update
      if (addBookmark) {
        addBookmark(verseOfDay.reference, verseOfDay.text);
      }

      // Show success feedback
      setBookmarked(true);

      // Reset the bookmarked status after 3 seconds
      setTimeout(() => {
        setBookmarked(false);
      }, 3000);

    } catch (error) {
      console.error("Error bookmarking verse of the day:", error);
    } finally {
      setBookmarkLoading(false);
    }
  };

  // Function to read verse aloud
  const readVerse = () => {
    if (!verseOfDay) return;

    // Cancel any ongoing speech
    speechSynthRef.current.cancel();

    // Create utterance with verse text and reference
    const textToRead = `${verseOfDay.text}. ${verseOfDay.reference}`;
    const utterance = new SpeechSynthesisUtterance(textToRead);

    // Set language based on selection
    utterance.lang = SPEECH_LANGUAGES[selectedLanguage] || 'en-US';

    // Handle speech events
    utterance.onstart = () => setIsPlaying(true);
    utterance.onend = () => setIsPlaying(false);
    utterance.onerror = () => setIsPlaying(false);

    // Speak the verse
    speechSynthRef.current.speak(utterance);
  };

  // Stop reading
  const stopReading = () => {
    speechSynthRef.current.cancel();
    setIsPlaying(false);
  };

  // Function to handle sharing
  const handleShare = () => {
    if (!verseOfDay) return;

    if (navigator.share) {
      navigator.share({
        title: 'Verse of the Day',
        text: `${verseOfDay.text} - ${verseOfDay.reference}`,
      })
      .catch(error => console.error('Error sharing:', error));
    }
  };

  return (
    <div className="min-h-screen flex flex-col bg-slate-50">
      {/* Top Header */}
      <div className="bg-amber-700 text-white px-4 py-3 flex justify-between items-center shadow-md">
        <h1 className="text-xl font-bold">Bible Snap</h1>
        <div className="flex items-center gap-2">
          <button className="p-2 rounded-full hover:bg-amber-600 transition-colors">
            <Volume2 size={18} />
          </button>
          <div className="bg-white rounded-full px-3 py-1.5 flex items-center">
            <input 
              type="text" 
              placeholder="Search Bible..." 
              className="bg-transparent text-gray-800 border-0 outline-none text-sm w-32" 
            />
            <Search size={16} className="text-gray-500" />
          </div>
        </div>
      </div>

      {/* Title Bar with Language Selector */}
      <div className="bg-amber-600 bg-opacity-90 px-4 py-3 flex justify-between items-center">
        <h2 className="text-lg font-semibold text-white">
          {selectedLanguage === LANGUAGES.KINYARWANDA ? 'Umurongo w\'Umunsi' : 'Verse of the Day'}
        </h2>

        {/* Language selector */}
        <div className="relative" ref={dropdownRef}>
          <button 
            className="flex items-center gap-1 bg-white bg-opacity-20 hover:bg-opacity-30 text-white rounded-full px-3 py-1.5 text-sm font-medium transition-colors" 
            onClick={toggleDropdown}
          >
            <Globe size={14} />
            {LANGUAGE_NAMES[selectedLanguage]}
            <ChevronDown size={14} />
          </button>

          {dropdownOpen && (
            <div className="absolute right-0 mt-1 py-1 bg-white rounded-lg shadow-lg z-10 min-w-40">
              {Object.values(LANGUAGES).map(lang => (
                <button 
                  key={lang}
                  className={`w-full text-left px-4 py-2 hover:bg-amber-50 text-sm ${selectedLanguage === lang ? 'font-bold text-amber-700' : 'text-gray-700'}`}
                  onClick={() => handleLanguageChange(lang)}
                >
                  {LANGUAGE_NAMES[lang]}
                </button>
              ))}
            </div>
          )}
        </div>
      </div>

      {/* Main Content - Verse Card */}
      <div className="flex-grow flex justify-center items-center p-4">
        {verseOfDay ? (
          <div className="w-full max-w-md rounded-2xl shadow-xl overflow-hidden">
            {/* Gradient Background */}
            <div className="relative h-full min-h-96 bg-gradient-to-br from-purple-600 via-indigo-500 to-blue-500 p-6 flex flex-col items-center justify-center text-center">
              {/* Quote Mark */}
              <div className="text-white text-opacity-30 text-6xl font-serif mb-6">‚ùù</div>

              {/* Verse Text */}
              <h2 className="text-2xl font-light text-white mb-6 leading-relaxed">
                {verseOfDay.text}
              </h2>

              {/* Verse Reference */}
              <p className="font-bold text-white mb-1">{verseOfDay.reference}</p>
              <p className="text-xs text-white text-opacity-70 mb-8">({verseOfDay.translation})</p>

              {/* Action Buttons */}
              <div className="flex justify-center gap-4 mt-4">
                <button 
                  className="bg-white bg-opacity-20 hover:bg-opacity-30 rounded-full p-3 transition-all transform hover:scale-105"
                  onClick={handleShare}
                  aria-label="Share verse"
                >
                  <Share2 size={20} className="text-white" />
                </button>

                <button 
                  className={`${bookmarked ? 'bg-green-500' : 'bg-red-500'} rounded-full p-3 transition-all transform hover:scale-105`}
                  onClick={bookmarked ? null : handleBookmarkVOTD}
                  disabled={bookmarkLoading}
                  aria-label="Bookmark verse"
                >
                  {bookmarked ? 
                    <Check size={20} className="text-white" /> : 
                    <Bookmark size={20} className="text-white" />
                  }
                </button>

                <button 
                  className="bg-white bg-opacity-20 hover:bg-opacity-30 rounded-full p-3 transition-all transform hover:scale-105"
                  onClick={isPlaying ? stopReading : readVerse}
                  aria-label={isPlaying ? "Stop reading" : "Read verse aloud"}
                >
                  <Volume2 size={20} className={`text-white ${isPlaying ? 'animate-pulse' : ''}`} />
                </button>
              </div>
            </div>
          </div>
        ) : (
          <div className="flex justify-center py-12">
            <div className="w-12 h-12 border-4 border-amber-600 border-t-transparent rounded-full animate-spin"></div>
          </div>
        )}
      </div>

      {/* Bottom Navigation Bar */}
      <div className="px-6 pb-6">
        <div className="bg-amber-700 text-white rounded-full shadow-lg py-4 px-8 flex justify-around">
          <button className="flex flex-col items-center text-white">
            <Home size={20} className="mb-1" />
            <span className="text-xs">Home</span>
          </button>
          <button className="flex flex-col items-center text-white opacity-70 hover:opacity-100">
            <Search size={20} className="mb-1" />
            <span className="text-xs">Search</span>
          </button>
          <button className="flex flex-col items-center text-white opacity-70 hover:opacity-100">
            <Heart size={20} className="mb-1" />
            <span className="text-xs">Saved</span>
          </button>
          <button className="flex flex-col items-center text-white opacity-70 hover:opacity-100">
            <User size={20} className="mb-1" />
            <span className="text-xs">Profile</span>
          </button>
        </div>
      </div>
    </div>
  );
};

export default HomeTab;
