import React, { useState, useRef, useEffect } from 'react';
import { Bookmark, Check, Volume2, Globe, ChevronDown } from 'lucide-react';
import { doc, setDoc, arrayUnion } from "firebase/firestore";
import { auth, db } from '../firebase';

const LANGUAGES = {
  ENGLISH: 'en',
  KINYARWANDA: 'rw'
};

const LANGUAGE_NAMES = {
  [LANGUAGES.ENGLISH]: 'Icyongereza',
  [LANGUAGES.KINYARWANDA]: 'Kinyarwanda'
};

const SPEECH_LANGUAGES = {
  [LANGUAGES.ENGLISH]: 'en-US',
  [LANGUAGES.KINYARWANDA]: 'rw-RW'
};

const HomeTab = ({ 
  theme,
  verseOfDay, 
  addBookmark,
  fetchVerseOfDay
}) => {
  const [bookmarked, setBookmarked] = useState(false);
  const [bookmarkLoading, setBookmarkLoading] = useState(false);
  const [isPlaying, setIsPlaying] = useState(false);
  const [selectedLanguage, setSelectedLanguage] = useState(LANGUAGES.KINYARWANDA);
  const [dropdownOpen, setDropdownOpen] = useState(false);
  const speechSynthRef = useRef(window.speechSynthesis);
  const dropdownRef = useRef(null);

  useEffect(() => {
    if (fetchVerseOfDay) {
      fetchVerseOfDay(LANGUAGES.KINYARWANDA);
    }
  }, [fetchVerseOfDay]);

  useEffect(() => {
    const handleClickOutside = (event) => {
      if (dropdownRef.current && !dropdownRef.current.contains(event.target)) {
        setDropdownOpen(false);
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, []);

  const toggleDropdown = () => setDropdownOpen(!dropdownOpen);

  const handleLanguageChange = (language) => {
    if (language === selectedLanguage) return;
    setSelectedLanguage(language);
    setDropdownOpen(false);
    if (fetchVerseOfDay) {
      fetchVerseOfDay(language);
    }
  };

  const handleBookmarkVOTD = async () => {
    if (!verseOfDay || bookmarked || !auth.currentUser) return;

    try {
      setBookmarkLoading(true);
      const referenceText = verseOfDay.reference;
      const matches = referenceText.match(/^((?:\d\s)?[A-Za-z]+)\s+(\d+):(\d+)(?:-\d+)?$/);

      if (!matches) {
        console.error("Could not parse reference:", referenceText);
        return;
      }

      const [_, book, chapter, verse] = matches;

      const bookmarkData = {
        book,
        chapter: parseInt(chapter),
        lineNumber: parseInt(verse),
        text: verseOfDay.text,
        reference: verseOfDay.reference,
        language: selectedLanguage,
        timestamp: new Date()
      };

      const userDocRef = doc(db, "users", auth.currentUser.uid);
      await setDoc(userDocRef, {
        bookmarks: arrayUnion(bookmarkData)
      }, { merge: true });

      if (addBookmark) {
        addBookmark(verseOfDay.reference, verseOfDay.text);
      }

      setBookmarked(true);
      setTimeout(() => setBookmarked(false), 3000);
    } catch (error) {
      console.error("Error bookmarking verse:", error);
    } finally {
      setBookmarkLoading(false);
    }
  };

  const readVerse = () => {
    if (!verseOfDay) return;

    speechSynthRef.current.cancel();
    const textToRead = `${verseOfDay.text}. ${verseOfDay.reference}`;
    const utterance = new SpeechSynthesisUtterance(textToRead);
    utterance.lang = SPEECH_LANGUAGES[selectedLanguage] || 'rw-RW';
    utterance.onstart = () => setIsPlaying(true);
    utterance.onend = () => setIsPlaying(false);
    utterance.onerror = () => setIsPlaying(false);
    speechSynthRef.current.speak(utterance);
  };

  const stopReading = () => {
    speechSynthRef.current.cancel();
    setIsPlaying(false);
  };

  return (
    <div className={`${theme.card} rounded shadow p-4`}>
      <div className="verse-of-day-container d-flex flex-column justify-content-center align-items-center">
        <div className="d-flex justify-content-end w-100 mb-3">
          <div className="position-relative" ref={dropdownRef}>
            <button 
              className={`btn btn-sm ${theme.subtleBtn || 'btn-outline-secondary'} d-flex align-items-center`} 
              onClick={toggleDropdown}
            >
              <Globe size={16} className="me-1" />
              {LANGUAGE_NAMES[selectedLanguage]}
              <ChevronDown size={14} className="ms-1" />
            </button>
            {dropdownOpen && (
              <div className="position-absolute end-0 mt-1 py-1 bg-white border rounded shadow-sm" style={{ zIndex: 1000, minWidth: '160px' }}>
                {Object.values(LANGUAGES).map(lang => (
                  <button 
                    key={lang}
                    className={`btn btn-link text-decoration-none text-start w-100 px-3 py-1 ${selectedLanguage === lang ? 'fw-bold' : ''}`}
                    onClick={() => handleLanguageChange(lang)}
                  >
                    {LANGUAGE_NAMES[lang]}
                  </button>
                ))}
              </div>
            )}
          </div>
        </div>

        <div className="text-center w-100">
          {verseOfDay ? (
            <>
              <h2 className="h5 fw-semibold mb-2">Umurongo w'Umunsi</h2>
              <p className={`${theme.subtleText} small mb-3`}>
                {new Date().toLocaleDateString('rw-RW', {
                  weekday: 'long', 
                  year: 'numeric', 
                  month: 'long', 
                  day: 'numeric'
                })}
              </p>
              <div className="border-start border-4 border-secondary ps-3 py-2 mb-3">
                <p className="fs-5 fst-italic mb-2">{verseOfDay.text}</p>
                <p className={`${theme.accentText} fw-semibold`}>
                  {verseOfDay.reference}
                </p>
              </div>
              <div className="d-flex justify-content-end mt-3">
                <button 
                  className={`btn btn-link ${theme.subtleText} p-1 me-2`}
                  onClick={isPlaying ? stopReading : readVerse}
                  aria-label={isPlaying ? "Hagarika gusoma" : "Soma umurongo"}
                >
                  <Volume2 size={18} />
                </button>
                <button 
                  className={`btn btn-link ${bookmarked ? 'text-success' : theme.subtleText} p-1`}
                  onClick={handleBookmarkVOTD}
                  disabled={bookmarkLoading}
                  aria-label="Shirako umurongo"
                >
                  {bookmarked ? <Check size={18} /> : <Bookmark size={18} />}
                </button>
              </div>
              <p className="small mt-4 text-center text-muted">
                Kanda kugira ngo usome
              </p>
            </>
          ) : (
            <div className="d-flex justify-content-center py-5">
              <div className="spinner-border text-primary" role="status">
                <span className="visually-hidden">Kuramba Umurongo w'Umunsi...</span>
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default HomeTab;
