// Optimized NotesTab with Firebase + Add Note Feature
import React, { useState, useEffect, useRef } from 'react';
import { Edit, X, Save, Plus, Bold, Italic, Underline, List, AlignLeft, AlignCenter, AlignRight } from 'lucide-react';
import { doc, onSnapshot, updateDoc, arrayUnion, arrayRemove } from 'firebase/firestore';
import { auth, db } from '../firebase';

const NotesTab = ({ theme, darkMode }) => {
  const [notes, setNotes] = useState([]);
  const [editingNoteIndex, setEditingNoteIndex] = useState(null);
  const [editedTitle, setEditedTitle] = useState('');
  const [selectedColor, setSelectedColor] = useState('#ffffff');
  const [userId, setUserId] = useState(null);
  const [showModal, setShowModal] = useState(false);
  const editorRef = useRef(null);
  const modalRef = useRef(null);

  const colorOptions = [
    { name: 'White', value: '#ffffff' },
    { name: 'Pastel Pink', value: '#ffcccb' },
    { name: 'Pastel Blue', value: '#D0A280' },
    { name: 'Pastel Yellow', value: '#ffffcc' },
    { name: 'Pastel Green', value: '#ccffcc' },
    { name: 'Pastel Purple', value: '#e6ccff' }
  ];

  useEffect(() => {
    const unsubscribe = auth.onAuthStateChanged(user => {
      if (user) {
        setUserId(user.uid);
        const userDoc = doc(db, 'users', user.uid);
        onSnapshot(userDoc, snapshot => {
          const data = snapshot.data();
          setNotes(data?.notes || []);
        });
      }
    });
    return () => unsubscribe();
  }, []);

  useEffect(() => {
    const handleClickOutside = (e) => {
      if (showModal && modalRef.current && !modalRef.current.contains(e.target)) {
        closeEditor();
      }
    };

    if (showModal) {
      document.addEventListener('mousedown', handleClickOutside);
    }

    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, [showModal]);

  const openNoteEditor = (note = {}, index = null) => {
    setEditingNoteIndex(index);
    setEditedTitle(note.title || '');
    setSelectedColor(note.color || '#ffffff');

    // Set modal to visible first
    setShowModal(true);

    // Wait for modal to be rendered before setting innerHTML
    setTimeout(() => {
      if (editorRef.current) {
        editorRef.current.innerHTML = note.content || '';
      }
    }, 10);
  };

  const closeEditor = () => {
    // Force modal to close immediately
    setShowModal(false);

    // Reset other states after a small delay
    setTimeout(() => {
      setEditingNoteIndex(null);
      setEditedTitle('');
      if (editorRef.current) editorRef.current.innerHTML = '';
    }, 100);
  };

  const saveNote = async () => {
    if (!userId || !editorRef.current) return;

    try {
      const content = editorRef.current.innerHTML.replace(/<div><br><\/div>/g, '<br>');
      const note = {
        title: editedTitle || 'Untitled Note',
        content,
        color: selectedColor,
        date: new Date().toLocaleDateString(),
        lastEdited: new Date().toISOString()
      };

      const userDoc = doc(db, 'users', userId);

      if (editingNoteIndex === null) {
        await updateDoc(userDoc, {
          notes: arrayUnion(note)
        });
      } else {
        const newNotes = [...notes];
        newNotes[editingNoteIndex] = note;

        await updateDoc(userDoc, {
          notes: newNotes
        });
      }

      // Close the modal immediately after save
      closeEditor();
    } catch (error) {
      console.error("Error saving note:", error);
      alert("Failed to save note. Please try again.");
    }
  };

  const deleteNote = async (index) => {
    if (!userId) return;
    try {
      const userDoc = doc(db, 'users', userId);
      const noteToDelete = notes[index];
      await updateDoc(userDoc, {
        notes: arrayRemove(noteToDelete)
      });
    } catch (error) {
      console.error("Error deleting note:", error);
      alert("Failed to delete note. Please try again.");
    }
  };

  const applyFormatting = (command, value = null) => {
    document.execCommand(command, false, value);
    editorRef.current.focus();
  };

  return (
    <div className={`${theme.card} rounded shadow p-4`}>
      <div className="d-flex justify-content-between align-items-center mb-3">
        <h2 className="h4 fw-semibold">Your Notes</h2>
        <button className="btn btn-primary btn-sm d-flex align-items-center" onClick={() => openNoteEditor()}> 
          <Plus size={16} className="me-1" /> Add Note 
        </button>
      </div>

      <div className="d-flex flex-column gap-3">
        {notes.length ? notes.map((note, index) => (
          <div key={index} className="p-3 rounded" style={{ backgroundColor: note.color }}>
            <div className="d-flex justify-content-between">
              <div>
                <h5 className="fw-bold mb-0">{note.title}</h5>
                <p className="small text-muted">{note.date}</p>
              </div>
              <div className="d-flex gap-2">
                <button className="btn btn-sm" onClick={() => openNoteEditor(note, index)}><Edit size={16} /></button>
                <button className="btn btn-sm" onClick={() => deleteNote(index)}><X size={16} /></button>
              </div>
            </div>
            {/* Full note content display - preserves formatting */}
            <div 
              dangerouslySetInnerHTML={{ __html: note.content }} 
              className="mt-2 note-content-display" 
              style={{ 
                textAlign: 'left',
                maxHeight: '250px',
                overflowY: 'auto',
                wordBreak: 'break-word'
              }} 
            />
          </div>
        )) : (
          <p className="text-center">No notes yet.</p>
        )}
      </div>

      {showModal && (
        <div className="position-fixed top-0 start-0 w-100 h-100 d-flex justify-content-center align-items-center" 
             style={{ backgroundColor: 'rgba(0,0,0,0.5)', zIndex: 1050 }}>
          <div className="bg-white rounded p-4" style={{ width: '90%', maxWidth: '700px' }} ref={modalRef}>
            <div className="d-flex justify-content-between align-items-center mb-3">
              <h5 className="m-0">Edit Note</h5>
              <button className="btn btn-sm" onClick={closeEditor}><X size={16} /></button>
            </div>
            <div className="mb-3">
              <input 
                type="text" 
                className="form-control" 
                value={editedTitle} 
                onChange={(e) => setEditedTitle(e.target.value)} 
                placeholder="Note Title" 
              />
            </div>
            <div className="mb-3 d-flex gap-2">
              {colorOptions.map(color => (
                <div 
                  key={color.value} 
                  onClick={() => setSelectedColor(color.value)} 
                  style={{ 
                    backgroundColor: color.value, 
                    width: 30, 
                    height: 30, 
                    borderRadius: '50%', 
                    border: selectedColor === color.value ? '2px solid black' : '1px solid gray', 
                    cursor: 'pointer' 
                  }} 
                />
              ))}
            </div>
            <div className="btn-group mb-2">
              <button className="btn btn-sm btn-outline-secondary" onClick={() => applyFormatting('bold')}>
                <Bold size={16} />
              </button>
              <button className="btn btn-sm btn-outline-secondary" onClick={() => applyFormatting('italic')}>
                <Italic size={16} />
              </button>
              <button className="btn btn-sm btn-outline-secondary" onClick={() => applyFormatting('underline')}>
                <Underline size={16} />
              </button>
              <button className="btn btn-sm btn-outline-secondary" onClick={() => applyFormatting('insertUnorderedList')}>
                <List size={16} />
              </button>
              <button className="btn btn-sm btn-outline-secondary" onClick={() => applyFormatting('justifyLeft')}>
                <AlignLeft size={16} />
              </button>
              <button className="btn btn-sm btn-outline-secondary" onClick={() => applyFormatting('justifyCenter')}>
                <AlignCenter size={16} />
              </button>
              <button className="btn btn-sm btn-outline-secondary" onClick={() => applyFormatting('justifyRight')}>
                <AlignRight size={16} />
              </button>
            </div>
            <div 
              className="form-control notepad-container mb-3" 
              contentEditable 
              ref={editorRef} 
              style={{ minHeight: 150, border: '1px solid #ddd', padding: 10 }} 
            />
            <div className="d-flex justify-content-end gap-2">
              <button className="btn btn-secondary" onClick={closeEditor}>Cancel</button>
              <button className="btn btn-primary" onClick={saveNote}>
                <Save size={16} className="me-1" /> Save
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default NotesTab;
