import React, { useState, useEffect } from 'react';
import { Cross } from 'lucide-react';

const DEFAULT_PRAYERS = [
  { 
    name: "Sign of the Cross", 
    text: "In the name of the Father, and of the Son, and of the Holy Spirit. Amen.", 
    repetitions: 1 
  },
  { 
    name: "Our Father", 
    text: "Our Father, who art in heaven, hallowed be thy name; thy kingdom come; thy will be done on earth as it is in heaven...", 
    repetitions: 1 
  },
  { 
    name: "Hail Mary", 
    text: "Hail Mary, full of grace, the Lord is with thee; blessed art thou among women, and blessed is the fruit of thy womb, Jesus...", 
    repetitions: 10 
  },
  { 
    name: "Glory Be", 
    text: "Glory be to the Father, and to the Son, and to the Holy Spirit. As it was in the beginning, is now, and ever shall be, world without end. Amen.", 
    repetitions: 1 
  }
];

const Chapulle = ({ theme }) => {
  const [isPraying, setIsPraying] = useState(false);
  const [hasStarted, setHasStarted] = useState(false);
  const [currentPrayerIndex, setCurrentPrayerIndex] = useState(0);
  const [counters, setCounters] = useState(DEFAULT_PRAYERS.map(p => p.repetitions));
  const [message, setMessage] = useState("");
  const [isAutoPlaying, setIsAutoPlaying] = useState(false);

  const getCurrentPrayer = () => {
    return DEFAULT_PRAYERS[currentPrayerIndex] || DEFAULT_PRAYERS[0];
  };

  const startPrayer = () => {
    setHasStarted(true);
    setIsPraying(true);
    setMessage("");
    setCurrentPrayerIndex(0);
    setCounters(DEFAULT_PRAYERS.map(p => p.repetitions));
  };

  const toggleAutoPlay = () => {
    setIsAutoPlaying(!isAutoPlaying);
  };

  const handleCounterClick = () => {
    setCounters(prev => {
      const newCounters = [...prev];
      if (newCounters[currentPrayerIndex] > 0) {
        newCounters[currentPrayerIndex] -= 1;
      }

      if (newCounters[currentPrayerIndex] === 0) {
        advancePrayer();
      }

      return newCounters;
    });
  };

  const advancePrayer = () => {
    if (currentPrayerIndex < DEFAULT_PRAYERS.length - 1) {
      setCurrentPrayerIndex(prev => prev + 1);
    } else {
      completePrayer();
    }
  };

  const completePrayer = () => {
    setIsPraying(false);
    setIsAutoPlaying(false);
    setMessage("Amen! You completed your prayer chaplet. ❤️");
  };

  useEffect(() => {
    let timer;
    if (isPraying && isAutoPlaying) {
      timer = setInterval(() => {
        setCounters(prev => {
          const newCounters = [...prev];
          if (newCounters[currentPrayerIndex] <= 1) {
            advancePrayer();
            return newCounters;
          }
          newCounters[currentPrayerIndex] -= 1;
          return newCounters;
        });
      }, 1000);
    }
    return () => clearInterval(timer);
  }, [isPraying, currentPrayerIndex, isAutoPlaying]);

  return (
    <div className="chaplet-container" style={{
      maxWidth: '600px',
      margin: '0 auto',
      padding: '20px',
      textAlign: 'center'
    }}>
      {!hasStarted ? (
        // Start Screen
        <div>
          <h2 style={{ marginBottom: '30px', color: theme.accentText }}>Prayer Chaplet</h2>
          <button
            onClick={startPrayer}
            style={{
              background: theme.accentBg,
              color: 'white',
              border: 'none',
              padding: '15px 40px',
              borderRadius: '50px',
              fontSize: '18px',
              cursor: 'pointer',
              marginBottom: '40px'
            }}
          >
            Start Praying
          </button>

          <div style={{ textAlign: 'left' }}>
            <h3 style={{ marginBottom: '15px', color: theme.accentText }}>Prayer Sequence</h3>
            {DEFAULT_PRAYERS.map((prayer, index) => (
              <div key={index} style={{
                padding: '15px',
                marginBottom: '10px',
                background: theme.card,
                borderRadius: '8px',
                display: 'flex',
                justifyContent: 'space-between'
              }}>
                <span>{prayer.name}</span>
                <span style={{ color: theme.subtleText }}>{prayer.repetitions}x</span>
              </div>
            ))}
          </div>
        </div>
      ) : isPraying ? (
        // Prayer in Progress
        <div>
          <div style={{
            display: 'flex',
            justifyContent: 'space-between',
            alignItems: 'center',
            marginBottom: '25px'
          }}>
            <h3 style={{ margin: 0, color: theme.accentText }}>
              {getCurrentPrayer().name}
            </h3>
            <div style={{ display: 'flex', gap: '10px' }}>
              <button 
                onClick={toggleAutoPlay}
                style={{
                  background: isAutoPlaying ? theme.secondaryBg : theme.accentBg,
                  color: 'white',
                  border: 'none',
                  padding: '8px 15px',
                  borderRadius: '5px',
                  cursor: 'pointer'
                }}
              >
                {isAutoPlaying ? 'Pause' : 'Auto'}
              </button>
              <button 
                onClick={completePrayer}
                style={{
                  background: theme.accentBg,
                  color: 'white',
                  border: 'none',
                  padding: '8px 15px',
                  borderRadius: '5px',
                  cursor: 'pointer'
                }}
              >
                Complete
              </button>
            </div>
          </div>

          {/* Prayer Text - Centered */}
          <div style={{
            background: theme.card,
            padding: '30px',
            borderRadius: '15px',
            marginBottom: '30px',
            minHeight: '200px',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center'
          }}>
            <p style={{ fontSize: '18px', lineHeight: '1.6', margin: 0 }}>
              {getCurrentPrayer().text}
            </p>
          </div>

          {/* Counter - Large and Centered */}
          <div 
            onClick={handleCounterClick}
            style={{
              width: '120px',
              height: '120px',
              borderRadius: '50%',
              background: theme.accentBg,
              color: 'white',
              display: 'flex',
              flexDirection: 'column',
              alignItems: 'center',
              justifyContent: 'center',
              margin: '0 auto',
              cursor: 'pointer',
              fontSize: '24px',
              fontWeight: 'bold'
            }}
          >
            <span style={{ fontSize: '36px' }}>{counters[currentPrayerIndex]}</span>
            <span style={{ fontSize: '14px' }}>Remaining</span>
          </div>

          {/* Progress Indicator */}
          <div style={{
            marginTop: '30px',
            display: 'flex',
            justifyContent: 'center',
            gap: '10px'
          }}>
            {DEFAULT_PRAYERS.map((_, index) => (
              <div 
                key={index}
                style={{
                  width: '12px',
                  height: '12px',
                  borderRadius: '50%',
                  background: index === currentPrayerIndex ? theme.accentBg : '#ddd'
                }}
              />
            ))}
          </div>
        </div>
      ) : (
        // Completed Screen
        <div>
          <div style={{
            background: theme.accentBg,
            color: 'white',
            padding: '20px',
            borderRadius: '10px',
            marginBottom: '30px'
          }}>
            {message}
          </div>
          <button
            onClick={startPrayer}
            style={{
              background: theme.accentBg,
              color: 'white',
              border: 'none',
              padding: '15px 40px',
              borderRadius: '50px',
              fontSize: '18px',
              cursor: 'pointer'
            }}
          >
            Restart Prayer
          </button>
        </div>
      )}
    </div>
  );
};

export default Chapulle;
