import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Fetch  = () => {
  const [darkMode, setDarkMode] = useState(false);
  const [verseOfTheDay, setVerseOfTheDay] = useState(null);

  useEffect(() => {
    const fetchVerseOfTheDay = async () => {
      try {
        // Fetch data from the Bible API
        const response = await axios.get('https://bibleapi.co/api/verse_of_the_day');
        setVerseOfTheDay(response.data);
      } catch (error) {
        console.error('Error fetching verse of the day:', error);
      }
    };

    fetchVerseOfTheDay();
  }, []);

  const toggleDarkMode = () => setDarkMode(!darkMode);

  return (
    <div className={`min-vh-100 d-flex flex-column ${darkMode ? 'bg-dark text-light' : 'bg-light text-dark'}`}>
      <header className="bg-primary text-white shadow">
        <h1>Scripture Daily</h1>
        <button onClick={toggleDarkMode}>Toggle Dark Mode</button>
      </header>

      <main className="flex-grow-1 p-4">
        {verseOfTheDay ? (
          <section className="verse-of-the-day">
            <h2>Verse of the Day</h2>
            <p><strong>{verseOfTheDay.text}</strong></p>
            <p>{verseOfTheDay.reference} ({verseOfTheDay.translation})</p>
          </section>
        ) : (
          <p>Loading Verse of the Day...</p>
        )}
      </main>
    </div>
  );
}

export default fetch ;
