import React, { useState, useRef, useEffect } from 'react';
import { Bookmark, Check, Volume2, Globe, ChevronDown, Share2 } from 'lucide-react';
import { doc, setDoc, arrayUnion } from "firebase/firestore";
import { auth, db } from '../firebase';

// Language options
const LANGUAGES = {
  ENGLISH: 'en',
  KINYARWANDA: 'rw'
};

// Language display names
const LANGUAGE_NAMES = {
  [LANGUAGES.ENGLISH]: 'English',
  [LANGUAGES.KINYARWANDA]: 'Kinyarwanda'
};

// Speech synthesis language codes
const SPEECH_LANGUAGES = {
  [LANGUAGES.ENGLISH]: 'en-US',
  [LANGUAGES.KINYARWANDA]: 'rw-RW'
};

const HomeTab = ({ 
  theme,
  verseOfDay, 
  addBookmark,
  fetchVerseOfDay
}) => {
  const [bookmarked, setBookmarked] = useState(false);
  const [bookmarkLoading, setBookmarkLoading] = useState(false);
  const [isPlaying, setIsPlaying] = useState(false);
  const [selectedLanguage, setSelectedLanguage] = useState(LANGUAGES.ENGLISH);
  const [dropdownOpen, setDropdownOpen] = useState(false);
  const speechSynthRef = useRef(window.speechSynthesis);
  const dropdownRef = useRef(null);

  // Toggle dropdown
  const toggleDropdown = () => {
    setDropdownOpen(!dropdownOpen);
  };

  // Close dropdown when clicking outside
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (dropdownRef.current && !dropdownRef.current.contains(event.target)) {
        setDropdownOpen(false);
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, []);

  // Handle language change
  const handleLanguageChange = (language) => {
    if (language === selectedLanguage) return;

    setSelectedLanguage(language);
    setDropdownOpen(false);

    // Fetch verse in the selected language
    if (fetchVerseOfDay) {
      fetchVerseOfDay(language);
    }
  };

  // Function to handle bookmarking the verse of the day
  const handleBookmarkVOTD = async () => {
    if (!verseOfDay || bookmarked || !auth.currentUser) return;

    try {
      setBookmarkLoading(true);

      // Parse the reference to extract book, chapter, and verse
      const referenceText = verseOfDay.reference;

      // Parse book, chapter and verse details from the reference
      const matches = referenceText.match(/^((?:\d\s)?[A-Za-z]+)\s+(\d+):(\d+)(?:-\d+)?$/);

      if (!matches) {
        console.error("Could not parse reference:", referenceText);
        return;
      }

      const [_, book, chapter, verse] = matches;

      // Create bookmark data
      const bookmarkData = {
        book: book,
        chapter: parseInt(chapter),
        lineNumber: parseInt(verse),
        text: verseOfDay.text,
        reference: verseOfDay.reference,
        language: selectedLanguage,
        timestamp: new Date()
      };

      // Add to Firebase
      const userDocRef = doc(db, "users", auth.currentUser.uid);
      await setDoc(userDocRef, {
        bookmarks: arrayUnion(bookmarkData)
      }, { merge: true });

      // Call the original addBookmark function for local state update
      if (addBookmark) {
        addBookmark(verseOfDay.reference, verseOfDay.text);
      }

      // Show success feedback
      setBookmarked(true);

      // Reset the bookmarked status after 3 seconds
      setTimeout(() => {
        setBookmarked(false);
      }, 3000);

    } catch (error) {
      console.error("Error bookmarking verse of the day:", error);
    } finally {
      setBookmarkLoading(false);
    }
  };

  // Function to read verse aloud
  const readVerse = () => {
    if (!verseOfDay) return;

    // Cancel any ongoing speech
    speechSynthRef.current.cancel();

    // Create utterance with verse text and reference
    const textToRead = `${verseOfDay.text}. ${verseOfDay.reference}`;
    const utterance = new SpeechSynthesisUtterance(textToRead);

    // Set language based on selection
    utterance.lang = SPEECH_LANGUAGES[selectedLanguage] || 'en-US';

    // Handle speech events
    utterance.onstart = () => setIsPlaying(true);
    utterance.onend = () => setIsPlaying(false);
    utterance.onerror = () => setIsPlaying(false);

    // Speak the verse
    speechSynthRef.current.speak(utterance);
  };

  // Stop reading
  const stopReading = () => {
    speechSynthRef.current.cancel();
    setIsPlaying(false);
  };

  // Function to handle sharing
  const handleShare = () => {
    if (!verseOfDay) return;

    if (navigator.share) {
      navigator.share({
        title: 'Verse of the Day',
        text: `${verseOfDay.text} - ${verseOfDay.reference}`,
      })
      .catch(error => console.error('Error sharing:', error));
    }
  };

  return (
    <div className="min-h-screen flex flex-col bg-gradient-to-b from-slate-50 to-slate-100">
      {/* Top Header */}
      <div className="sticky top-0 z-10 bg-white bg-opacity-95 backdrop-blur-sm shadow-sm px-4 py-3 flex justify-between items-center">
        <h1 className="text-xl font-semibold text-gray-800">
          {selectedLanguage === LANGUAGES.KINYARWANDA ? 'Umurongo w\'Umunsi' : 'Verse of the Day'}
        </h1>

        {/* Language selector */}
        <div className="relative" ref={dropdownRef}>
          <button 
            className="flex items-center gap-1 px-3 py-1.5 rounded-full bg-gray-100 hover:bg-gray-200 text-gray-700 text-sm font-medium transition-colors"
            type="button"
            onClick={toggleDropdown}
            aria-expanded={dropdownOpen}
            aria-haspopup="true"
          >
            <Globe size={16} />
            <span>{LANGUAGE_NAMES[selectedLanguage]}</span>
            <ChevronDown size={14} className={`transition-transform ${dropdownOpen ? 'rotate-180' : ''}`} />
          </button>

          {dropdownOpen && (
            <div className="absolute right-0 mt-1 py-1 bg-white border rounded-lg shadow-lg" style={{ zIndex: 1000, minWidth: '160px' }}>
              {Object.values(LANGUAGES).map(lang => (
                <button 
                  key={lang}
                  className={`w-full text-left px-4 py-2 hover:bg-gray-100 transition-colors ${
                    selectedLanguage === lang ? 'font-semibold text-blue-600' : 'text-gray-700'
                  }`}
                  onClick={() => handleLanguageChange(lang)}
                >
                  {LANGUAGE_NAMES[lang]}
                </button>
              ))}
            </div>
          )}
        </div>
      </div>

      {/* Main Content */}
      <div className="flex-grow flex justify-center items-center p-4">
        {verseOfDay ? (
          <div className="w-full max-w-md mx-auto">
            <div className="verse-card overflow-hidden rounded-3xl shadow-2xl bg-gradient-to-br from-blue-600 to-violet-700">
              {/* Card Content */}
              <div className="relative p-6 pt-12 pb-16 h-full min-h-96 flex flex-col justify-between">
                {/* Background Pattern */}
                <div className="absolute inset-0 overflow-hidden opacity-10">
                  <svg className="absolute -top-24 -right-24 w-96 h-96 text-white" viewBox="0 0 24 24">
                    <path fill="currentColor" d="M5,3H19A2,2 0 0,1 21,5V19A2,2 0 0,1 19,21H5A2,2 0 0,1 3,19V5A2,2 0 0,1 5,3M7,7V9H9V7H7M11,7V9H13V7H11M15,7V9H17V7H15M7,11V13H9V11H7M11,11V13H13V11H11M15,11V13H17V11H15M7,15V17H9V15H7M11,15V17H13V15H11M15,15V17H17V15H15Z" />
                  </svg>
                </div>

                {/* Quote Icon */}
                <div className="text-white text-opacity-40 text-6xl font-serif mb-4">‚ùù</div>

                {/* Verse Text */}
                <div className="flex-grow flex flex-col justify-center">
                  <h2 className="text-white text-2xl font-light leading-relaxed mb-6">
                    {verseOfDay.text}
                  </h2>

                  <div className="text-white text-opacity-90 font-semibold text-lg">
                    {verseOfDay.reference}
                  </div>

                  <p className="text-white text-opacity-70 text-sm mt-1">
                    ({verseOfDay.translation})
                  </p>
                </div>

                {/* Action Buttons */}
                <div className="flex justify-center gap-6 mt-8">
                  <button 
                    className="w-10 h-10 flex items-center justify-center rounded-full bg-white bg-opacity-20 hover:bg-opacity-30 text-white transition-all transform hover:scale-105"
                    onClick={handleShare}
                    aria-label="Share verse"
                  >
                    <Share2 size={18} />
                  </button>

                  <button 
                    className={`w-12 h-12 flex items-center justify-center rounded-full ${
                      bookmarked ? 'bg-green-500' : 'bg-white text-blue-600'
                    } shadow-lg transition-all transform hover:scale-105`}
                    onClick={bookmarked ? null : handleBookmarkVOTD}
                    disabled={bookmarkLoading}
                    aria-label="Bookmark verse"
                  >
                    {bookmarkLoading ? (
                      <div className="w-5 h-5 border-2 border-t-transparent border-blue-600 rounded-full animate-spin"></div>
                    ) : (
                      bookmarked ? <Check size={20} className="text-white" /> : <Bookmark size={20} />
                    )}
                  </button>

                  <button 
                    className={`w-10 h-10 flex items-center justify-center rounded-full ${
                      isPlaying ? 'bg-red-500 text-white' : 'bg-white bg-opacity-20 hover:bg-opacity-30 text-white'
                    } transition-all transform hover:scale-105`}
                    onClick={isPlaying ? stopReading : readVerse}
                    aria-label={isPlaying ? "Stop reading" : "Read verse aloud"}
                  >
                    <Volume2 size={18} />
                  </button>
                </div>
              </div>
            </div>
          </div>
        ) : (
          <div className="flex justify-center items-center py-12">
            <div className="relative">
              <div className="w-12 h-12 rounded-full border-4 border-blue-200 border-t-blue-600 animate-spin"></div>
              <span className="sr-only">Loading verse of the day...</span>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default HomeTab;
