import React, { useState } from 'react';
import { ArrowLeft, BookOpen, Highlighter, Bookmark, X } from 'lucide-react';

const BooksTab = ({ 
  theme, 
  selectedBook, 
  setSelectedBook, 
  selectedChapter, 
  setSelectedChapter, 
  chapterContent, 
  bibleBooks, 
  showAllOldTestament, 
  setShowAllOldTestament, 
  showAllNewTestament, 
  setShowAllNewTestament 
}) => {
  const [selectedLine, setSelectedLine] = useState(null);
  const [highlightedLines, setHighlightedLines] = useState({});
  const [savedLines, setSavedLines] = useState({});

  const handleLineClick = (lineNumber) => {
    setSelectedLine(selectedLine === lineNumber ? null : lineNumber);
  };

  const handleHighlight = () => {
    if (selectedLine !== null) {
      setHighlightedLines(prev => ({
        ...prev,
        [selectedLine]: !prev[selectedLine]
      }));
      setSelectedLine(null);
    }
  };

  const handleSave = () => {
    if (selectedLine !== null) {
      setSavedLines(prev => ({
        ...prev,
        [selectedLine]: !prev[selectedLine]
      }));
      setSelectedLine(null);
    }
  };

  return (
    <div className={`${theme.card} rounded-lg shadow-sm p-4 max-w-4xl mx-auto relative`}>
      {!selectedBook ? (
        <>
          <div className="flex items-center mb-6">
            <BookOpen className={`${theme.accentText} mr-2`} size={24} />
            <h2 className="text-2xl font-bold">Bible Books</h2>
          </div>

          {/* Old Testament */}
          <div className="mb-8">
            <h3 className={`${theme.accentText} text-lg font-semibold mb-4 pb-2 border-b ${theme.border}`}>Old Testament</h3>
            <div className="grid grid-cols-2 sm:grid-cols-3 md:grid-cols-4 gap-3">
              {bibleBooks.oldTestament
                .slice(0, showAllOldTestament ? bibleBooks.oldTestament.length : 8)
                .map(book => (
                  <button 
                    key={book}
                    className={`px-3 py-2 rounded-md text-left transition-all hover:${theme.accentBg} hover:text-white`}
                    onClick={() => setSelectedBook(book)}
                  >
                    {book}
                  </button>
                ))}

              <button 
                className={`px-3 py-2 rounded-md text-left italic ${theme.subtleText}`}
                onClick={() => setShowAllOldTestament(!showAllOldTestament)}
              >
                {showAllOldTestament ? "Show Less" : "View All..."}
              </button>
            </div>
          </div>         

          {/* New Testament */}
          <div className="mb-4">
            <h3 className={`${theme.accentText} text-lg font-semibold mb-4 pb-2 border-b ${theme.border}`}>New Testament</h3>
            <div className="grid grid-cols-2 sm:grid-cols-3 md:grid-cols-4 gap-3">
              {bibleBooks.newTestament
                .slice(0, showAllNewTestament ? bibleBooks.newTestament.length : 8)
                .map(book => (
                  <button 
                    key={book}
                    className={`px-3 py-2 rounded-md text-left transition-all hover:${theme.accentBg} hover:text-white`}
                    onClick={() => setSelectedBook(book)}
                  >
                    {book}
                  </button>
                ))}

              <button 
                className={`px-3 py-2 rounded-md text-left italic ${theme.subtleText}`}
                onClick={() => setShowAllNewTestament(!showAllNewTestament)}
              >
                {showAllNewTestament ? "Show Less" : "View All..."}
              </button>
            </div>
          </div>
        </>
      ) : !selectedChapter ? (
        <>
          <div className="flex items-center mb-6">
            <button 
              className="p-1 mr-3 rounded-full hover:bg-gray-100 dark:hover:bg-gray-700"
              onClick={() => setSelectedBook(null)}
            >
              <ArrowLeft size={20} />
            </button>
            <h2 className="text-2xl font-bold">{selectedBook}</h2>
          </div>

          <div className="grid grid-cols-3 sm:grid-cols-4 md:grid-cols-6 gap-3">
            {Array.from({ length: 10 }, (_, i) => i + 1).map(chapter => (
              <button 
                key={chapter}
                className={`py-2 rounded-md transition-all hover:${theme.accentBg} hover:text-white`}
                onClick={() => setSelectedChapter(chapter)}
              >
                {chapter}
              </button>
            ))}
          </div>
        </>
      ) : (
        <>
          <div className="flex items-center mb-6">
            <button 
              className="p-1 mr-3 rounded-full hover:bg-gray-100 dark:hover:bg-gray-700"
              onClick={() => setSelectedChapter(null)}
            >
              <ArrowLeft size={20} />
            </button>
            <h2 className="text-2xl font-bold">
              {selectedBook} {selectedChapter}
            </h2>
          </div>

          {chapterContent ? (
            <div className="bible-text bg-white dark:bg-gray-800 p-4 rounded-lg shadow-inner">
              {chapterContent.text.split('\n').map((line, index) => (
                line.trim() ? (
                  <div 
                    key={index} 
                    className={`flex mb-2 group relative
                      ${selectedLine === index + 1 ? 'bg-blue-50 dark:bg-blue-900' : ''}
                      ${highlightedLines[index + 1] ? 'bg-yellow-100 dark:bg-yellow-900' : ''}
                      ${savedLines[index + 1] ? 'border-l-4 border-blue-500 pl-2' : ''}
                    `}
                    onClick={() => handleLineClick(index + 1)}
                  >
                    <span className={`text-gray-500 dark:text-gray-400 mr-3 text-right w-8 flex-shrink-0 
                      ${selectedLine === index + 1 ? 'font-bold text-blue-600 dark:text-blue-300' : ''}
                    `}>
                      {index + 1}
                    </span>
                    <span className="flex-grow font-serif text-gray-800 dark:text-gray-200">
                      {line}
                    </span>
                    {savedLines[index + 1] && (
                      <Bookmark 
                        size={16} 
                        className="absolute right-2 top-1/2 transform -translate-y-1/2 text-blue-500" 
                        fill="currentColor"
                      />
                    )}
                  </div>
                ) : null
              ))}
              <p className={`${theme.subtleText} mt-4 text-sm italic text-center`}>
                {chapterContent.reference} ({chapterContent.translation})
              </p>
            </div>
          ) : (
            <p className="text-center py-8">Loading chapter content...</p>
          )}

          {/* Selection Toolbar */}
          {selectedLine !== null && (
            <div className={`fixed bottom-6 left-1/2 transform -translate-x-1/2 bg-white dark:bg-gray-800 
              shadow-lg rounded-full px-4 py-2 flex items-center border ${theme.border} z-10`}
            >
              <button 
                className="p-2 rounded-full hover:bg-gray-100 dark:hover:bg-gray-700 mr-2"
                onClick={() => setSelectedLine(null)}
              >
                <X size={18} />
              </button>
              <button 
                className={`p-2 rounded-full hover:bg-yellow-100 dark:hover:bg-yellow-900 mr-2
                  ${highlightedLines[selectedLine] ? 'text-yellow-600 dark:text-yellow-300' : ''}
                `}
                onClick={handleHighlight}
                title="Highlight"
              >
                <Highlighter size={18} />
              </button>
              <button 
                className={`p-2 rounded-full hover:bg-blue-100 dark:hover:bg-blue-900
                  ${savedLines[selectedLine] ? 'text-blue-600 dark:text-blue-300' : ''}
                `}
                onClick={handleSave}
                title="Save"
              >
                <Bookmark size={18} />
              </button>
              <span className="ml-2 text-sm font-medium px-2">
                Verse {selectedLine}
              </span>
            </div>
          )}

          <div className="flex justify-between mt-6">
            <button 
              className={`px-4 py-2 rounded-md ${selectedChapter <= 1 ? 'opacity-50 cursor-not-allowed' : ''}`}
              onClick={() => {
                setSelectedChapter(prev => Math.max(1, prev - 1));
                setSelectedLine(null);
              }}
              disabled={selectedChapter <= 1}
            >
              ← Previous Chapter
            </button>
            <button 
              className="px-4 py-2 rounded-md bg-blue-600 text-white hover:bg-blue-700"
              onClick={() => {
                setSelectedChapter(prev => prev + 1);
                setSelectedLine(null);
              }}
            >
              Next Chapter →
            </button>
          </div>
        </>
      )}
    </div>
  );
};

export default BooksTab;
