import React, { useEffect, useState } from 'react';
import { Bookmark, Edit, X } from 'lucide-react';
import { 
  collection, 
  doc, 
  setDoc, 
  getDoc,
  deleteDoc,
  onSnapshot 
} from "firebase/firestore";
import { auth, db } from './../firebase';

const BookmarksTab = ({ theme, darkMode }) => {
  const [bookmarks, setBookmarks] = useState([]);
  const [userId, setUserId] = useState(null);

  // Initialize anonymous auth and listen for bookmarks
  useEffect(() => {
    const initializeAuth = async () => {
      try {
        const { user } = await signInAnonymously(auth);
        setUserId(user.uid);
        listenToBookmarks(user.uid);
      } catch (error) {
        console.error("Anonymous auth failed:", error);
      }
    };

    initializeAuth();
  }, []);

  const listenToBookmarks = (uid) => {
    const userDocRef = doc(db, "users", uid);

    return onSnapshot(userDocRef, (doc) => {
      if (doc.exists()) {
        setBookmarks(doc.data().bookmarks || []);
      } else {
        setBookmarks([]);
      }
    });
  };

  const deleteBookmark = async (bookmarkId) => {
    if (!userId) return;

    const userDocRef = doc(db, "users", userId);
    const docSnap = await getDoc(userDocRef);

    if (docSnap.exists()) {
      const currentBookmarks = docSnap.data().bookmarks || [];
      const updatedBookmarks = currentBookmarks.filter(b => b.id !== bookmarkId);

      await setDoc(userDocRef, { bookmarks: updatedBookmarks }, { merge: true });
    }
  };

  return (
    <div className={`${theme.card} rounded shadow p-4`}>
      <h2 className="h4 fw-semibold mb-4">Your Bookmarks</h2>
      <div className="d-flex flex-column gap-3">
        {bookmarks.length > 0 ? (
          bookmarks.map((bookmark) => (
            <div 
              key={bookmark.id} 
              className={`p-3 border-start border-4 border-primary rounded ${darkMode ? 'bg-secondary bg-opacity-25' : 'bg-light'}`}
            >
              <div className="d-flex justify-content-between">
                <h3 className="fs-6 fw-medium mb-0">{bookmark.reference}</h3>
                <div className="d-flex gap-2">
                  <button className={`btn btn-link ${theme.subtleText} p-1`}>
                    <Edit size={16} />
                  </button>
                  <button 
                    className={`btn btn-link ${theme.subtleText} p-1`}
                    onClick={() => deleteBookmark(bookmark.id)}
                  >
                    <X size={16} />
                  </button>
                </div>
              </div>
              <p className="mt-2 mb-1">{bookmark.preview}</p>
              <button className={`btn btn-link ${theme.accentText} p-0 small`}>
                Read full passage
              </button>
            </div>
          ))
        ) : (
          <p className="text-center py-4">No bookmarks yet. Add some from the home page!</p>
        )}
      </div>
    </div>
  );
};

export default BookmarksTab;
