import React, { useState } from 'react';
import { Cross } from 'lucide-react';

const DEFAULT_PRAYERS = [
  { 
    name: "Sign of the Cross", 
    text: "In the name of the Father, and of the Son, and of the Holy Spirit. Amen.", 
    arabic: "بِسْمِ الآبِ وَالاِبْنِ وَالرُّوحِ الْقُدُسِ، آمِين" 
  },
  { 
    name: "Our Father", 
    text: "Our Father, who art in heaven, hallowed be thy name...", 
    arabic: "أَبَانَا الَّذِي فِي السَّمَاوَاتِ، لِيُقَدَّسِ اسْمُكَ..." 
  },
  { 
    name: "Hail Mary", 
    text: "Hail Mary, full of grace, the Lord is with thee...", 
    arabic: "السَّلامُ عَلَيْكِ يَا مَرْيَمُ، يَا مُمْتَلِئَةً نِعْمَةً، الرَّبُّ مَعَكِ..." 
  },
  { 
    name: "Glory Be", 
    text: "Glory be to the Father, and to the Son, and to the Holy Spirit...", 
    arabic: "المَجْدُ لِلآبِ وَالاِبْنِ وَالرُّوحِ الْقُدُسِ..." 
  }
];

const Chapulle = ({ theme }) => {
  const [currentPrayerIndex, setCurrentPrayerIndex] = useState(0);
  const [isPlaying, setIsPlaying] = useState(false);

  const currentPrayer = DEFAULT_PRAYERS[currentPrayerIndex];

  const nextPrayer = () => {
    if (currentPrayerIndex < DEFAULT_PRAYERS.length - 1) {
      setCurrentPrayerIndex(currentPrayerIndex + 1);
    } else {
      // Loop back to the first prayer
      setCurrentPrayerIndex(0);
    }
  };

  const prevPrayer = () => {
    if (currentPrayerIndex > 0) {
      setCurrentPrayerIndex(currentPrayerIndex - 1);
    }
  };

  const togglePlay = () => {
    setIsPlaying(!isPlaying);
  };

  return (
    <div className="flex flex-col items-center justify-center min-h-screen p-4" style={{ backgroundColor: '#f8f5f0' }}>
      {/* Header with Cross */}
      <div className="flex items-center mb-8">
        <Cross size={28} className="mr-3 text-amber-700" />
        <h1 className="text-2xl font-bold text-amber-900">Prayer Chaplet</h1>
      </div>

      {/* Prayer Card */}
      <div className="w-full max-w-md bg-white rounded-lg shadow-lg overflow-hidden mb-8">
        {/* Prayer Title */}
        <div className="bg-amber-700 p-4">
          <h2 className="text-xl font-semibold text-white text-center">
            {currentPrayer.name}
          </h2>
        </div>

        {/* Arabic Text */}
        <div className="p-6">
          <p className="text-3xl text-amber-900 text-center font-arabic leading-loose mb-6" dir="rtl">
            {currentPrayer.arabic}
          </p>

          {/* Divider */}
          <div className="border-t border-amber-200 my-4"></div>

          {/* English Text */}
          <p className="text-lg text-gray-700 text-center mb-6">
            {currentPrayer.text}
          </p>
        </div>
      </div>

      {/* Navigation Controls */}
      <div className="flex justify-center items-center gap-4 mb-8">
        <button 
          onClick={prevPrayer}
          className="px-6 py-2 bg-amber-100 text-amber-800 rounded-full hover:bg-amber-200 transition"
        >
          Previous
        </button>

        <button 
          onClick={togglePlay}
          className="px-8 py-3 bg-amber-600 text-white rounded-full hover:bg-amber-700 transition"
        >
          {isPlaying ? 'Pause' : 'Play'}
        </button>

        <button 
          onClick={nextPrayer}
          className="px-6 py-2 bg-amber-100 text-amber-800 rounded-full hover:bg-amber-200 transition"
        >
          Next
        </button>
      </div>

      {/* Prayer Progress */}
      <div className="flex gap-2">
        {DEFAULT_PRAYERS.map((_, index) => (
          <div 
            key={index}
            className={`w-3 h-3 rounded-full ${index === currentPrayerIndex ? 'bg-amber-600' : 'bg-amber-200'}`}
          />
        ))}
      </div>
    </div>
  );
};

export default Chapulle;
