import React, { useState, useRef, useEffect } from 'react';
import { Bookmark, Check, Volume2, Globe, ChevronDown, Share2, Calendar } from 'lucide-react';

const HomeTab = ({ theme = {}, verseOfDay, addBookmark, fetchVerseOfDay }) => {
  const [bookmarked, setBookmarked] = useState(false);
  const [bookmarkLoading, setBookmarkLoading] = useState(false);
  const [isPlaying, setIsPlaying] = useState(false);
  const [selectedLanguage, setSelectedLanguage] = useState('en');
  const [dropdownOpen, setDropdownOpen] = useState(false);
  const [currentDate, setCurrentDate] = useState('');
  const [currentDay, setCurrentDay] = useState('');
  const speechSynthRef = useRef(window.speechSynthesis);
  const dropdownRef = useRef(null);

  // Sample verse data for demo if none provided
  const sampleVerse = {
    text: "Come to me, all you who are weary and burdened, and I will give you rest.",
    reference: "Matthew 11:28",
    translation: "NIV"
  };

  const effectiveVerse = verseOfDay || sampleVerse;

  // Get current date and day
  useEffect(() => {
    const now = new Date();
    const options = { weekday: 'long', year: 'numeric', month: 'long', day: 'numeric' };
    setCurrentDate(now.toLocaleDateString('en-US', { year: 'numeric', month: 'long', day: 'numeric' }));
    setCurrentDay(now.toLocaleDateString('en-US', { weekday: 'long' }));
  }, []);

  // Toggle dropdown
  const toggleDropdown = () => {
    setDropdownOpen(!dropdownOpen);
  };

  // Toggle bookmark
  const handleBookmark = () => {
    setBookmarked(!bookmarked);
    setTimeout(() => {
      setBookmarked(false);
    }, 3000);
  };

  // Read verse aloud
  const toggleReading = () => {
    if (isPlaying) {
      speechSynthRef.current.cancel();
      setIsPlaying(false);
    } else {
      setIsPlaying(true);
      // Simulate reading
      setTimeout(() => setIsPlaying(false), 5000);
    }
  };

  return (
    <div className="min-h-screen flex flex-col bg-gray-100">
      {/* Top Header with Brand */}
      <header className="bg-amber-700 text-white shadow-md">
        <div className="container mx-auto p-4 flex justify-between items-center">
          <div className="flex items-center">
            <h1 className="text-xl font-bold">Bible Snap</h1>
          </div>
          <div className="relative" ref={dropdownRef}>
            <button
              className="flex items-center gap-1 bg-amber-800 hover:bg-amber-900 px-3 py-1 rounded-full text-sm"
              onClick={toggleDropdown}
            >
              <Globe size={16} />
              <span>{selectedLanguage === 'en' ? 'English' : 'Kinyarwanda'}</span>
              <ChevronDown size={14} />
            </button>

            {dropdownOpen && (
              <div className="absolute right-0 mt-1 bg-white rounded shadow-lg z-10 w-36">
                <button 
                  className="w-full text-left px-4 py-2 hover:bg-gray-100 text-gray-800"
                  onClick={() => setSelectedLanguage('en')}
                >
                  English
                </button>
                <button 
                  className="w-full text-left px-4 py-2 hover:bg-gray-100 text-gray-800"
                  onClick={() => setSelectedLanguage('rw')}
                >
                  Kinyarwanda
                </button>
              </div>
            )}
          </div>
        </div>
      </header>

      {/* Search Input */}
      <div className="bg-amber-700 pb-4">
        <div className="container mx-auto px-4">
          <div className="relative">
            <input 
              type="text" 
              placeholder="Search Bible..." 
              className="w-full py-2 px-4 rounded-full pr-10 shadow-sm"
            />
          </div>
        </div>
      </div>

      {/* Main Content Area */}
      <main className="flex-grow container mx-auto p-4 flex flex-col items-center">
        {/* Date Display */}
        <div className="w-full max-w-md flex items-center justify-between mb-3 px-2">
          <div className="flex items-center text-gray-600">
            <Calendar size={18} className="mr-2" />
            <span className="text-sm">{currentDay}, {currentDate}</span>
          </div>
        </div>

        {/* Verse Card */}
        <div className="w-full max-w-md rounded-2xl overflow-hidden shadow-lg">
          {/* Card Header */}
          <div className="bg-amber-600 text-white px-5 py-3 flex justify-between items-center">
            <h2 className="font-semibold text-lg">Verse of the Day</h2>
            <div className="bg-amber-700 px-3 py-1 rounded-full text-xs flex items-center">
              <span>{selectedLanguage === 'en' ? 'English' : 'Kinyarwanda'}</span>
            </div>
          </div>

          {/* Card Content */}
          <div className="bg-gradient-to-b from-gray-200 to-gray-300 p-6 flex flex-col items-center text-center min-h-64">
            <div className="text-gray-400 text-4xl mb-3">❝</div>
            <p className="text-gray-800 text-xl font-light mb-5 leading-relaxed">
              {effectiveVerse.text}
            </p>
            <div className="mt-auto">
              <p className="font-semibold text-gray-700">{effectiveVerse.reference}</p>
              <p className="text-sm text-gray-500 mt-1">({effectiveVerse.translation})</p>
            </div>

            {/* Action Buttons */}
            <div className="mt-6 flex justify-center space-x-3">
              <button 
                className="bg-white hover:bg-gray-100 text-gray-600 p-3 rounded-full shadow-md"
                onClick={() => {}}
              >
                <Share2 size={20} />
              </button>
              <button 
                className={`${bookmarked ? 'bg-red-500 text-white' : 'bg-white text-red-500 hover:bg-gray-100'} p-3 rounded-full shadow-md`}
                onClick={handleBookmark}
              >
                {bookmarked ? <Check size={20} /> : <Bookmark size={20} />}
              </button>
              <button 
                className={`bg-white hover:bg-gray-100 text-gray-600 p-3 rounded-full shadow-md ${isPlaying ? 'bg-gray-200' : ''}`}
                onClick={toggleReading}
              >
                <Volume2 size={20} />
              </button>
            </div>
          </div>
        </div>
      </main>

      {/* Bottom Navigation */}
      <nav className="bg-white shadow-md">
        <div className="container mx-auto flex justify-around py-3">
          <button className="flex flex-col items-center text-amber-700">
            <div className="p-1">
              <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M3 12l2-2m0 0l7-7 7 7M5 10v10a1 1 0 001 1h3m10-11l2 2m-2-2v10a1 1 0 01-1 1h-3m-6 0a1 1 0 001-1v-4a1 1 0 011-1h2a1 1 0 011 1v4a1 1 0 001 1m-6 0h6" />
              </svg>
            </div>
            <span className="text-xs">Home</span>
          </button>
          <button className="flex flex-col items-center text-gray-500">
            <div className="p-1">
              <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 6.253v13m0-13C10.832 5.477 9.246 5 7.5 5S4.168 5.477 3 6.253v13C4.168 18.477 5.754 18 7.5 18s3.332.477 4.5 1.253m0-13C13.168 5.477 14.754 5 16.5 5c1.747 0 3.332.477 4.5 1.253v13C19.832 18.477 18.247 18 16.5 18c-1.746 0-3.332.477-4.5 1.253" />
              </svg>
            </div>
            <span className="text-xs">Books</span>
          </button>
          <button className="flex flex-col items-center text-gray-500">
            <div className="p-1">
              <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M5 5a2 2 0 012-2h10a2 2 0 012 2v16l-7-3.5L5 21V5z" />
              </svg>
            </div>
            <span className="text-xs">Saved</span>
          </button>
          <button className="flex flex-col items-center text-gray-500">
            <div className="p-1">
              <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4.318 6.318a4.5 4.5 0 000 6.364L12 20.364l7.682-7.682a4.5 4.5 0 00-6.364-6.364L12 7.636l-1.318-1.318a4.5 4.5 0 00-6.364 0z" />
              </svg>
            </div>
            <span className="text-xs">Pray</span>
          </button>
          <button className="flex flex-col items-center text-gray-500">
            <div className="p-1">
              <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M11 5H6a2 2 0 00-2 2v11a2 2 0 002 2h11a2 2 0 002-2v-5m-1.414-9.414a2 2 0 112.828 2.828L11.828 15H9v-2.828l8.586-8.586z" />
              </svg>
            </div>
            <span className="text-xs">Notes</span>
          </button>
        </div>
      </nav>
    </div>
  );
};

export default HomeTab;
