import React, { useState, useEffect } from 'react';
import { Cross } from 'lucide-react';

const ChapullePrayer = ({ theme, setActiveTab, setMenuOpen, activeTab }) => {
  // Prayer sequence (Catholic Rosary structure)
  const prayers = [
    { 
      name: "Sign of the Cross", 
      text: "In the name of the Father, and of the Son, and of the Holy Spirit. Amen.", 
      repetitions: 1 
    },
    { 
      name: "Apostles' Creed", 
      text: "I believe in God, the Father Almighty...", 
      repetitions: 1 
    },
    // Add other prayers as needed
  ];

  // State management
  const [isPraying, setIsPraying] = useState(false);
  const [currentPrayerIndex, setCurrentPrayerIndex] = useState(0);
  const [counter, setCounter] = useState(prayers[0].repetitions);
  const [message, setMessage] = useState("");

  const togglePrayer = () => {
    if (isPraying) {
      setIsPraying(false);
      setMessage("Prayer paused. God bless you! üôè");
    } else {
      startPrayer();
    }
  };

  const startPrayer = () => {
    setIsPraying(true);
    setMessage("");
    setCurrentPrayerIndex(0);
    setCounter(prayers[0].repetitions);
  };

  useEffect(() => {
    if (!isPraying) return;

    const timer = setInterval(() => {
      setCounter(prev => {
        if (prev <= 1) {
          return advanceToNextPrayer();
        }
        return prev - 1;
      });
    }, 3000);

    return () => clearInterval(timer);
  }, [isPraying, currentPrayerIndex]);

  const advanceToNextPrayer = () => {
    if (currentPrayerIndex < prayers.length - 1) {
      setCurrentPrayerIndex(prev => prev + 1);
      return prayers[currentPrayerIndex + 1].repetitions;
    } else {
      finishPrayer();
      return 0;
    }
  };

  const finishPrayer = () => {
    setIsPraying(false);
    setMessage("Amen! You completed your prayer chaplet. ‚ù§Ô∏è");
  };

  return (
    <li className="mb-2">
      <button
        onClick={() => {
          setActiveTab('chapulle');
          setMenuOpen(false);
          if (activeTab !== 'chapulle') togglePrayer();
        }}
        className={`btn w-100 text-start d-flex align-items-center p-3 rounded ${
          activeTab === 'chapulle' ? theme.accentBg : 'btn-outline-secondary border-0'
        }`}
      >
        <Cross size={18} className="me-3" />
        <span>Pray Chaplet</span>
      </button>

      {activeTab === 'chapulle' && (
        <div className={`mt-3 p-3 ${theme.card} rounded shadow`}>
          {isPraying ? (
            <PrayerInProgress 
              prayer={prayers[currentPrayerIndex]} 
              counter={counter}
              theme={theme}
              onPause={togglePrayer}
            />
          ) : (
            <PrayerControls 
              message={message}
              theme={theme}
              onStart={startPrayer}
            />
          )}
        </div>
      )}
    </li>
  );
};

// Sub-components for better organization
const PrayerInProgress = ({ prayer, counter, theme, onPause }) => (
  <>
    <div className="d-flex justify-content-between align-items-center mb-2">
      <h3 className="h6 fw-semibold mb-0">
        {prayer.name} 
        <span className={`${theme.subtleText} ms-2`}>
          ({counter}x left)
        </span>
      </h3>
      <button 
        onClick={onPause}
        className={`btn btn-sm ${theme.accentBg}`}
      >
        Pause
      </button>
    </div>
    <p className="mb-0">{prayer.text}</p>
  </>
);

const PrayerControls = ({ message, theme, onStart }) => (
  <>
    <h3 className="h6 fw-semibold mb-3">Christian Prayer Chaplet</h3>
    {message ? (
      <div className={`alert ${theme.accentBg}`}>
        {message}
      </div>
    ) : (
      <p className="mb-3">Begin your prayer journey with the Holy Rosary.</p>
    )}
    <button
      onClick={onStart}
      className={`btn ${theme.accentBg} w-100`}
    >
      {message ? "Restart" : "Begin Prayer"}
    </button>
  </>
);

export default ChapullePrayer;
