import React, { useState, useRef } from 'react';
import { Bookmark, Check, Volume2, Globe, ChevronDown, Share2 } from 'lucide-react';
import { doc, setDoc, arrayUnion } from "firebase/firestore";
import { auth, db } from '../firebase';

// Language options
const LANGUAGES = {
  ENGLISH: 'en',
  KINYARWANDA: 'rw'
};

// Language display names
const LANGUAGE_NAMES = {
  [LANGUAGES.ENGLISH]: 'English',
  [LANGUAGES.KINYARWANDA]: 'Kinyarwanda'
};

// Speech synthesis language codes
const SPEECH_LANGUAGES = {
  [LANGUAGES.ENGLISH]: 'en-US',
  [LANGUAGES.KINYARWANDA]: 'rw-RW'  // Note: Check if this is supported in the browser
};

const HomeTab = ({ 
  theme,
  verseOfDay, 
  addBookmark,
  fetchVerseOfDay
}) => {
  const [bookmarked, setBookmarked] = useState(false);
  const [bookmarkLoading, setBookmarkLoading] = useState(false);
  const [isPlaying, setIsPlaying] = useState(false);
  const [selectedLanguage, setSelectedLanguage] = useState(LANGUAGES.ENGLISH);
  const [dropdownOpen, setDropdownOpen] = useState(false);
  const speechSynthRef = useRef(window.speechSynthesis);
  const dropdownRef = useRef(null);

  // Toggle dropdown
  const toggleDropdown = () => {
    setDropdownOpen(!dropdownOpen);
  };

  // Close dropdown when clicking outside
  React.useEffect(() => {
    const handleClickOutside = (event) => {
      if (dropdownRef.current && !dropdownRef.current.contains(event.target)) {
        setDropdownOpen(false);
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, []);

  // Handle language change
  const handleLanguageChange = (language) => {
    if (language === selectedLanguage) return;

    setSelectedLanguage(language);
    setDropdownOpen(false);

    // Fetch verse in the selected language
    if (fetchVerseOfDay) {
      fetchVerseOfDay(language);
    }
  };

  // Function to handle bookmarking the verse of the day
  const handleBookmarkVOTD = async () => {
    if (!verseOfDay || bookmarked || !auth.currentUser) return;

    try {
      setBookmarkLoading(true);

      // Parse the reference to extract book, chapter, and verse
      const referenceText = verseOfDay.reference;

      // Parse book, chapter and verse details from the reference
      const matches = referenceText.match(/^((?:\d\s)?[A-Za-z]+)\s+(\d+):(\d+)(?:-\d+)?$/);

      if (!matches) {
        console.error("Could not parse reference:", referenceText);
        return;
      }

      const [_, book, chapter, verse] = matches;

      // Create bookmark data
      const bookmarkData = {
        book: book,
        chapter: parseInt(chapter),
        lineNumber: parseInt(verse),
        text: verseOfDay.text,
        reference: verseOfDay.reference,
        language: selectedLanguage,
        timestamp: new Date()
      };

      // Add to Firebase
      const userDocRef = doc(db, "users", auth.currentUser.uid);
      await setDoc(userDocRef, {
        bookmarks: arrayUnion(bookmarkData)
      }, { merge: true });

      // Call the original addBookmark function for local state update
      if (addBookmark) {
        addBookmark(verseOfDay.reference, verseOfDay.text);
      }

      // Show success feedback
      setBookmarked(true);

      // Reset the bookmarked status after 3 seconds
      setTimeout(() => {
        setBookmarked(false);
      }, 3000);

    } catch (error) {
      console.error("Error bookmarking verse of the day:", error);
    } finally {
      setBookmarkLoading(false);
    }
  };

  // Function to read verse aloud
  const readVerse = () => {
    if (!verseOfDay) return;

    // Cancel any ongoing speech
    speechSynthRef.current.cancel();

    // Create utterance with verse text and reference
    const textToRead = `${verseOfDay.text}. ${verseOfDay.reference}`;
    const utterance = new SpeechSynthesisUtterance(textToRead);

    // Set language based on selection
    utterance.lang = SPEECH_LANGUAGES[selectedLanguage] || 'en-US';

    // Handle speech events
    utterance.onstart = () => setIsPlaying(true);
    utterance.onend = () => setIsPlaying(false);
    utterance.onerror = () => setIsPlaying(false);

    // Speak the verse
    speechSynthRef.current.speak(utterance);
  };

  // Stop reading
  const stopReading = () => {
    speechSynthRef.current.cancel();
    setIsPlaying(false);
  };

  // Function to handle sharing
  const handleShare = () => {
    if (!verseOfDay) return;

    if (navigator.share) {
      navigator.share({
        title: 'Verse of the Day',
        text: `${verseOfDay.text} - ${verseOfDay.reference}`,
      })
      .catch(error => console.error('Error sharing:', error));
    }
  };

  return (
    <div className="h-100 d-flex flex-column" style={{ minHeight: '100vh' }}>
      {/* Top Header */}
      <div className={`${theme.header} px-4 py-3 d-flex justify-content-between align-items-center`}>
        <h1 className="h5 mb-0 fw-semibold">
          {selectedLanguage === LANGUAGES.KINYARWANDA ? 'Umurongo w\'Umunsi' : 'Verse of the Day'}
        </h1>

        {/* Language selector */}
        <div className="position-relative" ref={dropdownRef}>
          <button 
            className={`btn btn-sm ${theme.subtleBtn || 'btn-outline-secondary'} d-flex align-items-center`} 
            type="button"
            onClick={toggleDropdown}
          >
            <Globe size={16} className="me-1" />
            {LANGUAGE_NAMES[selectedLanguage]}
            <ChevronDown size={14} className="ms-1" />
          </button>

          {dropdownOpen && (
            <div className="position-absolute end-0 mt-1 py-1 bg-white border rounded shadow-sm" style={{ zIndex: 1000, minWidth: '160px' }}>
              {Object.values(LANGUAGES).map(lang => (
                <button 
                  key={lang}
                  className={`btn btn-link text-decoration-none text-start w-100 px-3 py-1 ${selectedLanguage === lang ? 'fw-bold' : ''}`}
                  onClick={() => handleLanguageChange(lang)}
                >
                  {LANGUAGE_NAMES[lang]}
                </button>
              ))}
            </div>
          )}
        </div>
      </div>

      {/* Main Content - Full-Height Verse Card */}
      <div className="flex-grow-1 d-flex justify-content-center align-items-center p-3">
        {verseOfDay ? (
          <div className="verse-card position-relative rounded-4 w-100 shadow-lg overflow-hidden" style={{ maxWidth: '500px', minHeight: '75vh' }}>
            {/* Background Image - You can replace with your own or use a color gradient */}
            <div 
              className="position-absolute w-100 h-100" 
              style={{
                backgroundImage: 'linear-gradient(rgba(0,0,0,0.3), rgba(0,0,0,0.5)), url("/path/to/background-image.jpg")',
                backgroundSize: 'cover',
                backgroundPosition: 'center',
                zIndex: 0
              }}
            ></div>

            {/* Verse Content */}
            <div className="position-relative h-100 d-flex flex-column justify-content-center align-items-center text-center text-white p-4" style={{ zIndex: 1 }}>
              <div className="quote-mark display-3 mb-2">❝</div>
              <h2 className="fs-2 fw-light mb-4">{verseOfDay.text}</h2>
              <p className="fw-semibold">{verseOfDay.reference}</p>
              <p className="small text-white-50">({verseOfDay.translation})</p>

              {/* Action Buttons */}
              <div className="action-buttons d-flex justify-content-center mt-4 gap-3">
                <button 
                  className="btn btn-light btn-sm rounded-circle p-2" 
                  onClick={handleShare}
                  aria-label="Share verse"
                >
                  <Share2 size={18} />
                </button>

                <button 
                  className="btn btn-danger btn-sm rounded-circle p-2" 
                  onClick={bookmarked ? null : handleBookmarkVOTD}
                  disabled={bookmarkLoading}
                  aria-label="Bookmark verse"
                >
                  {bookmarked ? <Check size={18} /> : <Bookmark size={18} />}
                </button>

                <button 
                  className="btn btn-light btn-sm rounded-circle p-2"
                  onClick={isPlaying ? stopReading : readVerse}
                  aria-label={isPlaying ? "Stop reading" : "Read verse aloud"}
                >
                  <Volume2 size={18} />
                </button>
              </div>
            </div>
          </div>
        ) : (
          <div className="d-flex justify-content-center py-5">
            <div className="spinner-border text-primary" role="status">
              <span className="visually-hidden">Loading verse of the day...</span>
            </div>
          </div>
        )}
      </div>

      {/* Bottom Navigation Bar - Styled like in your image */}
      <div className="nav-container px-3 pb-3">
        <div className="nav-bar bg-danger text-white d-flex justify-content-around align-items-center py-3 px-4 rounded-pill shadow-lg">
          <button className="btn btn-link text-white p-0">
            <i className="bi bi-house-fill"></i>
          </button>
          <button className="btn btn-link text-white p-0">
            <i className="bi bi-search"></i>
          </button>
          <button className="btn btn-link text-white p-0">
            <i className="bi bi-heart"></i>
          </button>
          <button className="btn btn-link text-white p-0">
            <i className="bi bi-person"></i>
          </button>
