import React, { useState, useEffect } from 'react';
import { Cross } from 'lucide-react';

const DEFAULT_PRAYERS = [
  { 
    name: "Sign of the Cross", 
    text: "In the name of the Father, and of the Son, and of the Holy Spirit. Amen.", 
    repetitions: 1 
  },
  { 
    name: "Our Father", 
    text: "Our Father, who art in heaven, hallowed be thy name; thy kingdom come; thy will be done on earth as it is in heaven. Give us this day our daily bread; and forgive us our trespasses as we forgive those who trespass against us; and lead us not into temptation, but deliver us from evil. Amen.", 
    repetitions: 1 
  },
  { 
    name: "Hail Mary", 
    text: "Hail Mary, full of grace, the Lord is with thee; blessed art thou among women, and blessed is the fruit of thy womb, Jesus. Holy Mary, Mother of God, pray for us sinners, now and at the hour of our death. Amen.", 
    repetitions: 10 
  },
  { 
    name: "Glory Be", 
    text: "Glory be to the Father, and to the Son, and to the Holy Spirit. As it was in the beginning, is now, and ever shall be, world without end. Amen.", 
    repetitions: 1 
  }
];

const Chapulle = ({ theme }) => {
  const [isPraying, setIsPraying] = useState(false);
  const [currentPrayerIndex, setCurrentPrayerIndex] = useState(0);
  const [counter, setCounter] = useState(DEFAULT_PRAYERS[0]?.repetitions || 1);
  const [message, setMessage] = useState("");
  const [prayers, setPrayers] = useState(DEFAULT_PRAYERS);

  const getCurrentPrayer = () => {
    return prayers[currentPrayerIndex] || prayers[0];
  };

  const startPrayer = () => {
    setIsPraying(true);
    setMessage("");
    setCurrentPrayerIndex(0);
    setCounter(getCurrentPrayer().repetitions);
  };

  const togglePrayer = () => {
    if (isPraying) {
      pausePrayer();
    } else {
      startPrayer();
    }
  };

  const pausePrayer = () => {
    setIsPraying(false);
    setMessage("Prayer paused. God bless you! 🙏");
  };

  const completePrayer = () => {
    setIsPraying(false);
    setMessage("Amen! You completed your prayer chaplet. ❤️");
  };

  const advancePrayer = () => {
    if (currentPrayerIndex < prayers.length - 1) {
      setCurrentPrayerIndex(prev => prev + 1);
      setCounter(getCurrentPrayer().repetitions);
    } else {
      completePrayer();
    }
  };

  useEffect(() => {
    let timer;
    if (isPraying) {
      timer = setInterval(() => {
        setCounter(prev => {
          if (prev <= 1) {
            advancePrayer();
            return 0;
          }
          return prev - 1;
        });
      }, 1000);
    }
    return () => clearInterval(timer);
  }, [isPraying, currentPrayerIndex, prayers]);

  const PrayerInProgress = () => (
    <>
      <div className="d-flex justify-content-between align-items-center mb-3">
        <h3 className="h5 fw-semibold mb-0">
          {getCurrentPrayer().name} 
          <span className={`${theme.subtleText} ms-2`}>
            ({counter}x left)
          </span>
        </h3>
        <button 
          onClick={togglePrayer}
          className={`btn btn-sm ${theme.accentBg}`}
        >
          Pause
        </button>
      </div>
      <div className={`p-3 rounded ${theme.card}`}>
        <p className="mb-0 fs-5">{getCurrentPrayer().text}</p>
      </div>
    </>
  );

  const PrayerControls = () => (
    <>
      <h3 className="h5 fw-semibold mb-3">Christian Prayer Chaplet</h3>
      {message && (
        <div className={`alert ${theme.accentBg} mb-3`}>
          {message}
        </div>
      )}
      <div className="d-grid">
        <button
          onClick={startPrayer}
          className={`btn ${theme.accentBg} py-3`}
        >
          {message ? "Restart Prayer" : "Begin Prayer"}
        </button>
      </div>
    </>
  );

  return (
    <div className="chapulle-container">
      <div className="d-flex align-items-center mb-4">
        <Cross size={28} className={`me-3 ${theme.accentText}`} />
        <h2 className="h3 fw-bold mb-0">Prayer Chaplet</h2>
      </div>

      {isPraying ? <PrayerInProgress /> : <PrayerControls />}

      <div className="mt-4">
        <h4 className="h5 fw-semibold mb-3">Prayer Sequence</h4>
        <div className="list-group">
          {prayers.map((prayer, index) => (
            <div 
              key={index}
              className={`list-group-item ${currentPrayerIndex === index && isPraying ? theme.accentBg : ''} ${theme.card}`}
            >
              <div className="d-flex justify-content-between">
                <span>{prayer.name}</span>
                <span className={`${theme.subtleText}`}>{prayer.repetitions}x</span>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default Chapulle;
