import React from 'react';

const Notes = ({ theme }) => {
  // Sample notes
  const notes = [
    { title: "Isaiah 40:31 Reflection", content: "Hope in the Lord brings renewal and strength." },
    { title: "Jeremiah 29:11 Reflection", content: "God has a plan for each of us, filled with hope." }
  ];

  return (
    <div className={`${theme.card} rounded shadow p-4`}>
      <h2 className="h5 fw-semibold">Notes</h2>
      <ul className="list-unstyled">
        {notes.map((note, index) => (
          <li key={index} className="p-3 border-bottom">
            <div className="fw-semibold">{note.title}</div>
            <div>{note.content}</div>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Notes;
