import React, { useState, useEffect } from 'react';
import { Cross } from 'lucide-react';

const DEFAULT_PRAYERS = [
  { 
    name: "Sign of the Cross", 
    text: "In the name of the Father, and of the Son, and of the Holy Spirit. Amen.", 
    repetitions: 1 
  },
  { 
    name: "Our Father", 
    text: "Our Father, who art in heaven, hallowed be thy name...", 
    repetitions: 1 
  },
  { 
    name: "Hail Mary", 
    text: "Hail Mary, full of grace, the Lord is with thee...", 
    repetitions: 10 
  },
  { 
    name: "Glory Be", 
    text: "Glory be to the Father, and to the Son, and to the Holy Spirit...", 
    repetitions: 1 
  }
];

const ChapullePrayer = ({ theme, setActiveTab, setMenuOpen, activeTab }) => {
  const [isPraying, setIsPraying] = useState(false);
  const [currentPrayerIndex, setCurrentPrayerIndex] = useState(0);
  const [counter, setCounter] = useState(DEFAULT_PRAYERS[0]?.repetitions || 1);
  const [message, setMessage] = useState("");
  const [prayers, setPrayers] = useState(DEFAULT_PRAYERS);

  // Safe current prayer getter
  const getCurrentPrayer = () => {
    return prayers[currentPrayerIndex] || prayers[0];
  };

  const startPrayer = () => {
    setIsPraying(true);
    setMessage("");
    setCurrentPrayerIndex(0);
    setCounter(getCurrentPrayer().repetitions);
  };

  const togglePrayer = () => {
    if (isPraying) {
      pausePrayer();
    } else {
      startPrayer();
    }
  };

  const pausePrayer = () => {
    setIsPraying(false);
    setMessage("Prayer paused. God bless you! 🙏");
  };

  const completePrayer = () => {
    setIsPraying(false);
    setMessage("Amen! You completed your prayer chaplet. ❤️");
  };

  const advancePrayer = () => {
    if (currentPrayerIndex < prayers.length - 1) {
      setCurrentPrayerIndex(prev => prev + 1);
      setCounter(getCurrentPrayer().repetitions);
    } else {
      completePrayer();
    }
  };

  useEffect(() => {
    let timer;
    if (isPraying) {
      timer = setInterval(() => {
        setCounter(prev => {
          if (prev <= 1) {
            advancePrayer();
            return 0;
          }
          return prev - 1;
        });
      }, 1000); // 1 second per count
    }
    return () => clearInterval(timer);
  }, [isPraying, currentPrayerIndex, prayers]);

  return (
    <li className="mb-2">
      <button
        onClick={() => {
          setActiveTab('chapulle');
          setMenuOpen(false);
          if (activeTab !== 'chapulle') togglePrayer();
        }}
        className={`btn w-100 text-start d-flex align-items-center p-3 rounded ${
          activeTab === 'chapulle' ? theme.accentBg : 'btn-outline-secondary border-0'
        }`}
      >
        <Cross size={18} className="me-3" />
        <span>Pray Chaplet</span>
      </button>

      {activeTab === 'chapulle' && (
        <div className={`mt-3 p-3 ${theme.card} rounded shadow`}>
          {isPraying ? (
            <PrayerInProgress 
              prayer={getCurrentPrayer()} 
              counter={counter}
              theme={theme}
              onPause={togglePrayer}
            />
          ) : (
            <PrayerControls 
              message={message}
              theme={theme}
              onStart={startPrayer}
            />
          )}
        </div>
      )}
    </li>
  );
};

// Safe sub-components with prop validation
const PrayerInProgress = ({ prayer = {}, counter = 0, theme, onPause }) => (
  <>
    <div className="d-flex justify-content-between align-items-center mb-2">
      <h3 className="h6 fw-semibold mb-0">
        {prayer.name || "Current Prayer"} 
        <span className={`${theme.subtleText} ms-2`}>
          ({counter}x left)
        </span>
      </h3>
      <button 
        onClick={onPause}
        className={`btn btn-sm ${theme.accentBg}`}
      >
        Pause
      </button>
    </div>
    <p className="mb-0">{prayer.text || "Prayer text"}</p>
  </>
);

const PrayerControls = ({ message = "", theme, onStart }) => (
  <>
    <h3 className="h6 fw-semibold mb-3">Christian Prayer Chaplet</h3>
    {message && (
      <div className={`alert ${theme.accentBg} mb-3`}>
        {message}
      </div>
    )}
    <button
      onClick={onStart}
      className={`btn ${theme.accentBg} w-100`}
    >
      {message ? "Restart Prayer" : "Begin Prayer"}
    </button>
  </>
);

export default ChapullePrayer;
