import React from 'react';

const Sidebar = ({ activeTab, setActiveTab, menuOpen, setMenuOpen, theme }) => {
  return (
    <nav className={`d-flex flex-column p-3 ${theme.header}`} style={{ width: '250px', minHeight: '100vh' }}>
      <button className={`btn ${theme.accentBg} mb-2`} onClick={() => setActiveTab('home')}>
        Home
      </button>
      {/* Add more navigation buttons as needed */}
    </nav>
  );
};

export default Sidebar;
