import React, { useState, useEffect, useCallback } from 'react';
import { ArrowLeft, BookOpen, Highlighter, Bookmark, X } from 'lucide-react';
import { doc, setDoc, getDoc, arrayUnion, arrayRemove, updateDoc } from "firebase/firestore";
import { auth, db } from '../firebase';

const BooksTab = ({
  theme,
  selectedBook,
  setSelectedBook,
  selectedChapter,
  setSelectedChapter,
  chapterContent,
  bibleBooks,
  showAllOldTestament,
  setShowAllOldTestament,
  showAllNewTestament,
  setShowAllNewTestament
}) => {
  const [selectedLine, setSelectedLine] = useState(null);
  const [highlightedLines, setHighlightedLines] = useState({});
  const [savedLines, setSavedLines] = useState({});
  const [linePosition, setLinePosition] = useState({ top: 0, left: 0 });
  const [userId, setUserId] = useState(null);
  const [activeTab, setActiveTab] = useState('Old Testament'); // Changed default tab

  const loadUserData = useCallback(async (uid) => {
    const userDocRef = doc(db, "users", uid);
    const docSnap = await getDoc(userDocRef);

    if (docSnap.exists()) {
      const data = docSnap.data();

      // Reset the state first
      setHighlightedLines({});
      setSavedLines({});

      // Process highlights
      if (data.highlights && Array.isArray(data.highlights)) {
        const highlightsMap = {};
        data.highlights.forEach(hl => {
          // Only add highlights for the current book and chapter
          if (hl.book === selectedBook.name && hl.chapter === selectedChapter) {
            highlightsMap[hl.lineNumber] = true;
          }
        });
        setHighlightedLines(highlightsMap);
      }

      // Process bookmarks
      if (data.bookmarks && Array.isArray(data.bookmarks)) {
        const bookmarksMap = {};
        data.bookmarks.forEach(bm => {
          // Only add bookmarks for the current book and chapter
          if (bm.book === selectedBook.name && bm.chapter === selectedChapter) {
            bookmarksMap[bm.lineNumber] = true;
          }
        });
        setSavedLines(bookmarksMap);
      }
    }
  }, [selectedBook?.name, selectedChapter]);

  useEffect(() => {
    if (userId && selectedBook.name && selectedChapter) {
      loadUserData(userId);
    }
  }, [userId, selectedBook.name, selectedChapter, loadUserData]);

  useEffect(() => {
    const unsubscribe = auth.onAuthStateChanged(user => {
      if (user) {
        setUserId(user.uid);
        loadUserData(user.uid);
      }
    });
    return () => unsubscribe();
  }, [loadUserData]);

  const handleLineClick = (lineNumber, event) => {
    const rect = event.currentTarget.getBoundingClientRect();
    setLinePosition({
      top: rect.top - 50,
      left: rect.left
    });
    setSelectedLine(selectedLine === lineNumber ? null : lineNumber);
  };

  const handleHighlight = async () => {
    if (selectedLine === null || !userId || !selectedBook.name || !selectedChapter) return;

    const isHighlighted = !highlightedLines[selectedLine];
    const userDocRef = doc(db, "users", userId);
    const highlightData = {
      book: selectedBook.name,
      chapter: selectedChapter,
      lineNumber: selectedLine,
      text: chapterContent.text.split('\n')[selectedLine - 1],
      reference: `${selectedBook.name} ${selectedChapter}:${selectedLine}`,
      timestamp: new Date()
    };

    try {
      if (isHighlighted) {
        // Add the highlight
        await setDoc(userDocRef, {
          highlights: arrayUnion(highlightData)
        }, { merge: true });

        // Update local state
        setHighlightedLines(prev => ({
          ...prev,
          [selectedLine]: true
        }));
      } else {
        // Remove the highlight
        await updateDoc(userDocRef, {
          highlights: arrayRemove(highlightData)
        });

        // Update local state
        setHighlightedLines(prev => {
          const newState = { ...prev };
          delete newState[selectedLine];
          return newState;
        });
      }

      setSelectedLine(null);
    } catch (error) {
      console.error("Error updating highlight:", error);
    }
  };

  const handleSave = async () => {
    if (selectedLine === null || !userId || !selectedBook.name || !selectedChapter || !chapterContent) return;

    const isSaved = !savedLines[selectedLine];
    const userDocRef = doc(db, "users", userId);
    const bookmarkData = {
      book: selectedBook.name,
      chapter: selectedChapter,
      lineNumber: selectedLine,
      text: chapterContent.text.split('\n')[selectedLine - 1] || '',
      reference: `${selectedBook.name} ${selectedChapter}:${selectedLine}`,
      timestamp: new Date()
    };

    try {
      if (isSaved) {
        // Add the bookmark
        await setDoc(userDocRef, {
          bookmarks: arrayUnion(bookmarkData)
        }, { merge: true });

        // Update local state
        setSavedLines(prev => ({
          ...prev,
          [selectedLine]: true
        }));
      } else {
        // Remove the bookmark
        await updateDoc(userDocRef, {
          bookmarks: arrayRemove(bookmarkData)
        });

        // Update local state
        setSavedLines(prev => {
          const newState = { ...prev };
          delete newState[selectedLine];
          return newState;
        });
      }

      setSelectedLine(null);
    } catch (error) {
      console.error("Error updating bookmark:", error);
    }
  };

  // Render header with navigation
  const renderHeader = () => {
    if (!selectedBook.name) {
      return (
        <div className="text-center p-3 fw-bold fs-5 border-bottom">
          Go to Passage
        </div>
      );
    } else if (!selectedChapter) {
      return (
        <div className="d-flex align-items-center p-3 border-bottom">
          <button 
            className="btn btn-link p-0 me-3 text-dark"
            onClick={() => setSelectedBook({ name: null, chapters: 0 })}
          >
            <ArrowLeft size={20} />
          </button>
          <div className="fw-bold fs-5 flex-grow-1 text-center">Go to Passage</div>
          <button className="btn btn-link text-dark">Go</button>
        </div>
      );
    } else {
      return (
        <div className="d-flex align-items-center p-3 border-bottom">
          <button 
            className="btn btn-link p-0 me-3 text-dark"
            onClick={() => setSelectedChapter(null)}
          >
            <ArrowLeft size={20} />
          </button>
          <div className="fw-bold fs-5 flex-grow-1 text-center">{selectedBook.name} {selectedChapter}</div>
          <button className="btn btn-link text-dark">Go</button>
        </div>
      );
    }
  };

  // Render tabs (Old Testament/New Testament)
  const renderTabs = () => {
    return (
      <div className="d-flex mb-3">
        <button 
          className={`flex-grow-1 py-2 border-0 rounded-0 ${activeTab === 'Old Testament' ? 'bg-danger bg-opacity-75 text-white' : 'bg-light'}`}
          onClick={() => setActiveTab('Old Testament')}
        >
          Old Testament
        </button>
        <button 
          className={`flex-grow-1 py-2 border-0 rounded-0 ${activeTab === 'New Testament' ? 'bg-danger bg-opacity-75 text-white' : 'bg-light'}`}
          onClick={() => setActiveTab('New Testament')}
        >
          New Testament
        </button>
      </div>
    );
  };

  return (
    <div className="position-relative h-100 d-flex flex-column" style={{ backgroundColor: '#f8f9fa' }}>
      {renderHeader()}

      {!selectedBook.name && renderTabs()}

      <div className="flex-grow-1 overflow-auto">
        {!selectedBook.name ? (
          <>
            {/* Display books based on active tab */}
            {activeTab === 'Old Testament' ? (
              <div className="book-list">
                {bibleBooks.oldTestament.map((book, index) => (
                  <div 
                    key={book.name}
                    className="d-flex justify-content-between align-items-center py-3 px-4 border-bottom"
                    onClick={() => setSelectedBook(book)}
                  >
                    <div>{book.name}</div>
                    <div className="text-muted">{index + 1}</div>
                  </div>
                ))}
              </div>
            ) : (
              <div className="book-list">
                {bibleBooks.newTestament.map((book, index) => (
                  <div 
                    key={book.name}
                    className={`d-flex justify-content-between align-items-center py-3 px-4 border-bottom ${book.name === 'Matthew' ? 'bg-light' : ''}`}
                    onClick={() => setSelectedBook(book)}
                  >
                    <div>{book.name}</div>
                    <div className="text-muted">{index + 1}</div>
                  </div>
                ))}
              </div>
            )}
          </>
        ) : !selectedChapter ? (
          <>
            {/* Chapter selection - full width buttons */}
            <div className="chapter-grid p-2">
              {Array.from({ length: selectedBook.chapters }, (_, i) => i + 1).map(chapter => (
                <div 
                  key={chapter} 
                  className="d-flex justify-content-between align-items-center py-3 px-4 border-bottom"
                  onClick={() => setSelectedChapter(chapter)}
                >
                  <div>Chapter {chapter}</div>
                  <div className="text-muted">{chapter}</div>
                </div>
              ))}
            </div>
          </>
        ) : (
          <>
            {/* Chapter content */}
            {chapterContent ? (
              <div className="bible-text bg-white p-3">
                {chapterContent.text.split('\n').map((line, index) => (
                  line.trim() ? (
                    <div 
                      key={index} 
                      className={`d-flex mb-2 position-relative
                        ${selectedLine === index + 1 ? 'bg-primary bg-opacity-10' : ''}
                        ${highlightedLines[index + 1] ? 'bg-warning bg-opacity-10' : ''}
                        ${savedLines[index + 1] ? 'border-start border-primary border-3 ps-2' : ''}
                      `}
                      onClick={(e) => handleLineClick(index + 1, e)}
                    >
                      <span className={`text-muted me-3 text-end ${selectedLine === index + 1 ? 'fw-bold text-primary' : ''}`} style={{ width: '30px' }}>
                        {index + 1}
                      </span>
                      <span className="flex-grow-1">
                        {line}
                      </span>
                      {savedLines[index + 1] && (
                        <Bookmark 
                          size={16} 
                          className="position-absolute end-0 top-50 translate-middle-y text-primary" 
                          fill="currentColor"
                        />
                      )}
                    </div>
                  ) : null
                ))}
                <p className="mt-3 small text-center text-muted">
                  {chapterContent.reference} ({chapterContent.translation})
                </p>
              </div>
            ) : (
              <p className="text-center py-4">Loading chapter content...</p>
            )}

            {/* Selection Toolbar */}
            {selectedLine !== null && (
              <div 
                className="position-absolute bg-white p-2 rounded shadow d-flex align-items-center z-3"
                style={{
                  top: `${linePosition.top}px`,
                  left: `${linePosition.left}px`,
                  transform: 'translateX(-50%)'
                }}
              >
                <button 
                  className="btn btn-sm btn-outline-secondary me-2"
                  onClick={() => setSelectedLine(null)}
                >
                  <X size={16} />
                </button>
                <button 
                  className={`btn btn-sm me-2 ${highlightedLines[selectedLine] ? 'btn-warning' : 'btn-outline-warning'}`}
                  onClick={handleHighlight}
                  title="Highlight"
                >
                  <Highlighter size={16} />
                </button>
                <button 
                  className={`btn btn-sm ${savedLines[selectedLine] ? 'btn-primary' : 'btn-outline-primary'}`}
                  onClick={handleSave}
                  title="Save"
                >
                  <Bookmark size={16} />
                </button>
                <span className="ms-2 small fw-bold">
                  Verse {selectedLine}
                </span>
              </div>
            )}

            {/* Navigation buttons - Fixed to bottom */}
            <div className="d-flex justify-content-between p-3 border-top bg-white">
              <button 
                className="btn btn-outline-secondary"
                onClick={() => {
                  setSelectedChapter(prev => Math.max(1, prev - 1));
                  setSelectedLine(null);
                }}
                disabled={selectedChapter <= 1}
              >
                Previous
              </button>
              <button 
                className="btn btn-outline-secondary"
                onClick={() => {
                  setSelectedChapter(prev => prev + 1);
                  setSelectedLine(null);
                }}
                disabled={selectedChapter >= selectedBook.chapters}
              >
                Next
              </button>
            </div>
          </>
        )}
      </div>
    </div>
  );
};

export default BooksTab;
