import React from 'react';
import { Menu, Sun, Moon, X } from 'lucide-react';

const Header = ({ toggleMenu, toggleDarkMode, darkMode, menuOpen }) => {
  return (
    <header className={`d-flex justify-content-between align-items-center p-3 bg-primary text-white`}>
      <button className="btn btn-outline-light" onClick={toggleMenu}>
        {menuOpen ? <X size={24} /> : <Menu size={24} />}
      </button>
      <h1 className="m-0">Bible App</h1>
      <button className="btn btn-outline-light" onClick={toggleDarkMode}>
        {darkMode ? <Sun size={24} /> : <Moon size={24} />}
      </button>
    </header>
  );
};

export default Header;
