import React, { useState } from 'react';
import { Bookmark, Edit, BookOpen, ChevronRight, Check } from 'lucide-react';
import { doc, setDoc, arrayUnion } from "firebase/firestore";
import { auth, db } from '../firebase';

const HomeTab = ({ 
  theme, 
  verseOfDay, 
  addBookmark, 
  recentBooks, 
  setSelectedBook, 
  setSelectedChapter, 
  setActiveTab, 
  bookmarks, 
  notes 
}) => {
  const [bookmarked, setBookmarked] = useState(false);
  const [bookmarkLoading, setBookmarkLoading] = useState(false);

  // Function to handle bookmarking the verse of the day to Firebase
  const handleBookmarkVOTD = async () => {
    if (!verseOfDay || bookmarked || !auth.currentUser) return;

    try {
      setBookmarkLoading(true);

      // Parse the reference to extract book, chapter, and verse
      const referenceText = verseOfDay.reference;

      // Parse book, chapter and verse details from the reference
      // This handles complex references like "1 Corinthians 13:4-7"
      const matches = referenceText.match(/^((?:\d\s)?[A-Za-z]+)\s+(\d+):(\d+)(?:-\d+)?$/);

      if (!matches) {
        console.error("Could not parse reference:", referenceText);
        return;
      }

      const [_, book, chapter, verse] = matches;

      // Create bookmark data following the same format as in BooksTab
      const bookmarkData = {
        book: book,
        chapter: parseInt(chapter),
        lineNumber: parseInt(verse),
        text: verseOfDay.text,
        reference: verseOfDay.reference,
        timestamp: new Date()
      };

      // Add to Firebase
      const userDocRef = doc(db, "users", auth.currentUser.uid);
      await setDoc(userDocRef, {
        bookmarks: arrayUnion(bookmarkData)
      }, { merge: true });

      // Call the original addBookmark function for local state update
      if (addBookmark) {
        addBookmark(verseOfDay.reference, verseOfDay.text);
      }

      // Show success feedback
      setBookmarked(true);

      // Reset the bookmarked status after 3 seconds
      setTimeout(() => {
        setBookmarked(false);
      }, 3000);

    } catch (error) {
      console.error("Error bookmarking verse of the day:", error);
    } finally {
      setBookmarkLoading(false);
    }
  };

  return (
    <>
      {/* Verse of the Day */}
      <section className={`mb-4 p-4 ${theme.card} rounded shadow`}>
        <h2 className="h5 fw-semibold mb-1">Verse of the Day</h2>
        <p className={`${theme.subtleText} small mb-3`}>
          {new Date().toLocaleDateString('en-US', {weekday: 'long', year: 'numeric', month: 'long', day: 'numeric'})}
        </p>
        <div className="border-start border-4 border-secondary ps-3 py-1 mb-3">
          {verseOfDay ? (
            <>
              <p className="fs-5 fst-italic mb-2">{verseOfDay.text}</p>
              <p className={`${theme.accentText} fw-semibold`}>
                {verseOfDay.reference} ({verseOfDay.translation})
              </p>
            </>
          ) : (
            <p>Loading verse of the day...</p>
          )}
        </div>
        <div className="d-flex justify-content-end gap-2">
          <button 
            className={`btn btn-link ${bookmarked ? 'text-success' : theme.subtleText} p-1`}
            onClick={handleBookmarkVOTD}
            disabled={bookmarkLoading || !verseOfDay}
          >
            {bookmarked ? <Check size={18} /> : <Bookmark size={18} />}
          </button>
          <button className={`btn btn-link ${theme.subtleText} p-1`}>
            <Edit size={18} />
          </button>
        </div>
      </section>

      {/* Quick Access */}
      <section className="mb-4">
        <h2 className="h5 fw-semibold mb-3">Continue Reading</h2>
        <div className="row row-cols-1 row-cols-md-3 g-3">
          {recentBooks.map((book, index) => (
            <div key={index} className="col">
              <div 
                className={`${theme.card} p-3 rounded shadow d-flex align-items-center justify-content-between cursor-pointer`}
                onClick={() => {
                  setSelectedBook({ name: book.name, chapters: book.chapters || 50 });
                  setSelectedChapter(book.chapter);
                  setActiveTab('books');
                }}
              >
                <div className="d-flex align-items-center">
                  <BookOpen className={theme.accentText} size={20} />
                  <div className="ms-3">
                    <h3 className="fs-6 fw-medium mb-0">{book.name}</h3>
                    <p className={`${theme.subtleText} mb-0 small`}>Chapter {book.chapter}</p>
                  </div>
                </div>
                <ChevronRight size={18} className={theme.subtleText} />
              </div>
            </div>
          ))}
        </div>
      </section>

      {/* Bookmarks and Notes Preview */}
      <div className="row row-cols-1 row-cols-md-2 g-4">
        <div className="col">
          <div className="d-flex justify-content-between align-items-center mb-3">
            <h2 className="h5 fw-semibold mb-0">Bookmarks</h2>
            <button 
              className={`btn btn-link ${theme.accentText} p-0 small`}
              onClick={() => setActiveTab('bookmarks')}
            >
              View all
            </button>
          </div>
          <div className={`${theme.card} rounded shadow`}>
            {bookmarks.length > 0 ? (
              bookmarks.slice(0, 2).map((bookmark, index) => (
                <div key={index} className={`p-3 ${index > 0 ? 'border-top' : ''}`}>
                  <div className="d-flex justify-content-between">
                    <h3 className="fs-6 fw-medium mb-0">{bookmark.reference}</h3>
                    <Bookmark size={16} className={theme.accentText} />
                  </div>
                  <p className={`${theme.subtleText} small mt-1 text-truncate mb-0`}>{bookmark.preview}</p>
                </div>
              ))
            ) : (
              <div className="p-3 text-center">
                <p className={`${theme.subtleText} mb-0`}>No bookmarks yet</p>
              </div>
            )}
          </div>
        </div>

        <div className="col">
          <div className="d-flex justify-content-between align-items-center mb-3">
            <h2 className="h5 fw-semibold mb-0">Notes</h2>
            <button 
              className={`btn btn-link ${theme.accentText} p-0 small`}
              onClick={() => setActiveTab('notes')}
            >
              View all
            </button>
          </div>
          <div className={`${theme.card} rounded shadow`}>
            {notes.length > 0 ? (
              notes.slice(0, 2).map((note, index) => (
                <div key={index} className={`p-3 ${index > 0 ? 'border-top' : ''}`}>
                  <div className="d-flex justify-content-between">
                    <h3 className="fs-6 fw-medium mb-0">{note.title}</h3>
                    <p className={`${theme.subtleText} small`}>{note.date}</p>
                  </div>
                  <p className={`${theme.subtleText} small mt-1 mb-0`}>{note.reference}</p>
                </div>
              ))
            ) : (
              <div className="p-3 text-center">
                <p className={`${theme.subtleText} mb-0`}>No notes yet</p>
              </div>
            )}
          </div>
        </div>
      </div>
    </>
  );
};

export default HomeTab;
