import React, { useEffect, useState } from 'react';
import { Bookmark, Edit, X, Highlighter } from 'lucide-react';
import { 
  collection, 
  doc, 
  setDoc, 
  getDoc,
  deleteDoc,
  onSnapshot,
  arrayRemove 
} from "firebase/firestore";
import { auth, db, signInAnonymously } from '../firebase';

const BookmarksTab = ({ theme, darkMode }) => {
  const [bookmarks, setBookmarks] = useState([]);
  const [highlights, setHighlights] = useState([]);
  const [userId, setUserId] = useState(null);
  const [activeTab, setActiveTab] = useState('bookmarks');

  useEffect(() => {
    const initializeAuth = async () => {
      try {
        const { user } = await signInAnonymously(auth);
        setUserId(user.uid);
        listenToUserData(user.uid);
      } catch (error) {
        console.error("Anonymous auth failed:", error);
      }
    };

    initializeAuth();
  }, []);

  const listenToUserData = (uid) => {
    const userDocRef = doc(db, "users", uid);

    return onSnapshot(userDocRef, (doc) => {
      if (doc.exists()) {
        setBookmarks(doc.data().bookmarks || []);
        setHighlights(doc.data().highlights || []);
      } else {
        setBookmarks([]);
        setHighlights([]);
      }
    });
  };

  const deleteBookmark = async (bookmarkId) => {
    if (!userId) return;

    const userDocRef = doc(db, "users", userId);
    const docSnap = await getDoc(userDocRef);

    if (docSnap.exists()) {
      const currentBookmarks = docSnap.data().bookmarks || [];
      const updatedBookmarks = currentBookmarks.filter(b => b.id !== bookmarkId);

      await setDoc(userDocRef, { bookmarks: updatedBookmarks }, { merge: true });
    }
  };

  const deleteHighlight = async (highlight) => {
    if (!userId) return;

    const userDocRef = doc(db, "users", userId);
    await setDoc(userDocRef, {
      highlights: arrayRemove(highlight)
    }, { merge: true });
  };

  return (
    <div className={`${theme.card} rounded shadow p-4`}>
      <div className="d-flex gap-3 mb-4 border-bottom pb-2">
        <button 
          className={`btn btn-link p-0 ${activeTab === 'bookmarks' ? theme.accentText : theme.subtleText}`}
          onClick={() => setActiveTab('bookmarks')}
        >
          <h2 className="h4 fw-semibold mb-0">Your Bookmarks</h2>
        </button>
        <button 
          className={`btn btn-link p-0 ${activeTab === 'highlights' ? theme.accentText : theme.subtleText}`}
          onClick={() => setActiveTab('highlights')}
        >
          <h2 className="h4 fw-semibold mb-0">Your Highlights</h2>
        </button>
      </div>

      {activeTab === 'bookmarks' ? (
        <div className="d-flex flex-column gap-3">
          {bookmarks.length > 0 ? (
            bookmarks.map((bookmark) => (
              <div 
                key={bookmark.id} 
                className={`p-3 border-start border-4 border-primary rounded ${darkMode ? 'bg-secondary bg-opacity-25' : 'bg-light'}`}
              >
                <div className="d-flex justify-content-between">
                  <h3 className="fs-6 fw-medium mb-0">{bookmark.reference}</h3>
                  <div className="d-flex gap-2">
                    <button 
                      className={`btn btn-link ${theme.subtleText} p-1`}
                      onClick={() => deleteBookmark(bookmark.id)}
                    >
                      <X size={16} />
                    </button>
                  </div>
                </div>
                <p className="mt-2 mb-1">{bookmark.text}</p>
                <button className={`btn btn-link ${theme.accentText} p-0 small`}>
                  Read full passage
                </button>
              </div>
            ))
          ) : (
            <p className="text-center py-4">No bookmarks yet. Add some from the Bible text!</p>
          )}
        </div>
      ) : (
        <div className="d-flex flex-column gap-3">
          {highlights.length > 0 ? (
            highlights.map((highlight, index) => (
              <div 
                key={index} 
                className={`p-3 border-start border-4 border-warning rounded ${darkMode ? 'bg-secondary bg-opacity-25' : 'bg-light'}`}
              >
                <div className="d-flex justify-content-between">
                  <h3 className="fs-6 fw-medium mb-0">{highlight.reference}</h3>
                  <div className="d-flex gap-2">
                    <button 
                      className={`btn btn-link ${theme.subtleText} p-1`}
                      onClick={() => deleteHighlight(highlight)}
                    >
                      <X size={16} />
                    </button>
                  </div>
                </div>
                <p className="mt-2 mb-1">
                  <Highlighter className="me-2" size={16} />
                  {highlight.text}
                </p>
                <button className={`btn btn-link ${theme.accentText} p-0 small`}>
                  Read full passage
                </button>
              </div>
            ))
          ) : (
            <p className="text-center py-4">No highlights yet. Highlight some text from the Bible!</p>
          )}
        </div>
      )}
    </div>
  );
};

export default BookmarksTab;
