import React, { useState, useEffect } from 'react';
import { Cross } from 'lucide-react';

const DEFAULT_PRAYERS = [
  { 
    name: "Sign of the Cross", 
    text: "In the name of the Father, and of the Son, and of the Holy Spirit. Amen.", 
    repetitions: 1 
  },
  { 
    name: "Our Father", 
    text: "Our Father, who art in heaven, hallowed be thy name; thy kingdom come; thy will be done on earth as it is in heaven...", 
    repetitions: 1 
  },
  { 
    name: "Hail Mary", 
    text: "Hail Mary, full of grace, the Lord is with thee; blessed art thou among women, and blessed is the fruit of thy womb, Jesus...", 
    repetitions: 10 
  },
  { 
    name: "Glory Be", 
    text: "Glory be to the Father, and to the Son, and to the Holy Spirit. As it was in the beginning, is now, and ever shall be, world without end. Amen.", 
    repetitions: 1 
  }
];

const Chapulle = ({ theme }) => {
  const [isPlaying, setIsPlaying] = useState(false);
  const [currentPrayerIndex, setCurrentPrayerIndex] = useState(0);
  const [counters, setCounters] = useState(DEFAULT_PRAYERS.map(p => p.repetitions));
  const [hasStarted, setHasStarted] = useState(false);

  const currentPrayer = DEFAULT_PRAYERS[currentPrayerIndex];

  // Start the prayer sequence
  const startPrayer = () => {
    setHasStarted(true);
    setIsPlaying(true);
    setCurrentPrayerIndex(0);
    setCounters(DEFAULT_PRAYERS.map(p => p.repetitions));
  };

  // Handle counter click
  const handleCounterClick = () => {
    if (!isPlaying) return;

    setCounters(prev => {
      const newCounters = [...prev];
      if (newCounters[currentPrayerIndex] > 0) {
        newCounters[currentPrayerIndex] -= 1;
      }

      if (newCounters[currentPrayerIndex] === 0) {
        if (currentPrayerIndex < DEFAULT_PRAYERS.length - 1) {
          setTimeout(() => {
            setCurrentPrayerIndex(prev => prev + 1);
          }, 500);
        } else {
          setIsPlaying(false);
        }
      }

      return newCounters;
    });
  };

  // Auto-play effect
  useEffect(() => {
    let timer;
    if (isPlaying && hasStarted) {
      timer = setInterval(() => {
        handleCounterClick();
      }, 1000);
    }
    return () => clearInterval(timer);
  }, [isPlaying, currentPrayerIndex, hasStarted]);

  // Reset when reaching the end
  useEffect(() => {
    if (currentPrayerIndex === DEFAULT_PRAYERS.length - 1 && counters[currentPrayerIndex] === 0) {
      setTimeout(() => {
        setIsPlaying(false);
      }, 1000);
    }
  }, [counters, currentPrayerIndex]);

  return (
    <div className="flex flex-col items-center justify-center min-h-screen p-4 bg-amber-50">
      {/* Header */}
      <div className="flex items-center mb-8">
        <Cross size={28} className="mr-3 text-amber-700" />
        <h1 className="text-2xl font-bold text-amber-900">Prayer Chaplet</h1>
      </div>

      {!hasStarted ? (
        /* Start Screen */
        <div className="text-center">
          <div className="w-full max-w-md bg-white rounded-lg shadow-lg p-8 mb-8">
            <h2 className="text-xl font-semibold text-amber-800 mb-6">Christian Prayer Chaplet</h2>
            <button
              onClick={startPrayer}
              className="px-8 py-4 bg-amber-600 text-white text-lg rounded-full hover:bg-amber-700 transition"
            >
              Begin Prayer
            </button>
          </div>

          <div className="w-full max-w-md bg-white rounded-lg shadow-lg p-6">
            <h3 className="text-lg font-semibold text-amber-800 mb-4">Prayer Sequence</h3>
            <ul className="space-y-3">
              {DEFAULT_PRAYERS.map((prayer, index) => (
                <li key={index} className="flex justify-between items-center p-3 bg-amber-50 rounded">
                  <span className="text-amber-900">{prayer.name}</span>
                  <span className="text-amber-700 font-medium">{prayer.repetitions}x</span>
                </li>
              ))}
            </ul>
          </div>
        </div>
      ) : (
        /* Prayer Screen */
        <div className="w-full max-w-md bg-white rounded-lg shadow-lg overflow-hidden">
          {/* Prayer Header */}
          <div className="bg-amber-700 p-4">
            <h2 className="text-xl font-semibold text-white text-center">
              {currentPrayer.name}
              <span className="block text-sm font-normal mt-1">
                {counters[currentPrayerIndex]}x remaining
              </span>
            </h2>
          </div>

          {/* Prayer Content */}
          <div className="p-6">
            <div 
              onClick={handleCounterClick}
              className="flex items-center justify-center mb-6 cursor-pointer"
            >
              <div className="w-24 h-24 rounded-full bg-amber-100 border-4 border-amber-300 flex items-center justify-center">
                <span className="text-3xl font-bold text-amber-800">
                  {counters[currentPrayerIndex]}
                </span>
              </div>
            </div>

            <p className="text-lg text-gray-700 text-center mb-6">
              {currentPrayer.text}
            </p>
          </div>

          {/* Controls */}
          <div className="flex justify-center items-center gap-4 p-4 bg-amber-50">
            <button 
              onClick={() => setIsPlaying(!isPlaying)}
              className={`px-6 py-2 ${isPlaying ? 'bg-amber-600' : 'bg-amber-800'} text-white rounded-full`}
            >
              {isPlaying ? 'Pause' : 'Resume'}
            </button>
          </div>
        </div>
      )}

      {/* Progress Dots */}
      {hasStarted && (
        <div className="flex gap-2 mt-6">
          {DEFAULT_PRAYERS.map((_, index) => (
            <div 
              key={index}
              className={`w-3 h-3 rounded-full ${index === currentPrayerIndex ? 'bg-amber-600' : 'bg-amber-200'}`}
            />
          ))}
        </div>
      )}
    </div>
  );
};

export default Chapulle;
