import React, { useState, useEffect, useRef } from 'react';

const PRAYERS = [
  { 
    name: "Sign of the Cross", 
    text: "In the name of the Father, and of the Son, and of the Holy Spirit. Amen.", 
    repetitions: 1 
  },
  { 
    name: "Apostles' Creed", 
    text: "I believe in God, the Father almighty...", 
    repetitions: 1 
  },
  { 
    name: "Our Father", 
    text: "Our Father, who art in heaven...", 
    repetitions: 1 
  },
  { 
    name: "Hail Mary", 
    text: "Hail Mary, full of grace...", 
    repetitions: 3 
  },
  { 
    name: "Glory Be", 
    text: "Glory be to the Father...", 
    repetitions: 1 
  },
  // ... (include all your prayers in order)
];

const Chaplet = () => {
  const [currentPrayerIndex, setCurrentPrayerIndex] = useState(0);
  const [counters, setCounters] = useState(PRAYERS.map(p => p.repetitions));
  const [isAutoPlaying, setIsAutoPlaying] = useState(false);
  const [isAudioPlaying, setIsAudioPlaying] = useState(false);
  const audioRef = useRef(null);

  // Auto-play effect
  useEffect(() => {
    let timer;

    if (isAutoPlaying && !isAudioPlaying) {
      timer = setTimeout(() => {
        handleNextPrayer();
      }, 3000); // 3 second delay between prayers
    }

    return () => clearTimeout(timer);
  }, [currentPrayerIndex, isAutoPlaying, isAudioPlaying]);

  const handleNextPrayer = () => {
    setCounters(prev => {
      const newCounters = [...prev];
      newCounters[currentPrayerIndex] -= 1;

      if (newCounters[currentPrayerIndex] <= 0) {
        if (currentPrayerIndex < PRAYERS.length - 1) {
          setCurrentPrayerIndex(prev => prev + 1);
          // Reset counter for next prayer
          newCounters[currentPrayerIndex + 1] = PRAYERS[currentPrayerIndex + 1].repetitions;
        } else {
          // End of prayers
          setIsAutoPlaying(false);
        }
      }

      return newCounters;
    });
  };

  const toggleAutoPlay = () => {
    setIsAutoPlaying(!isAutoPlaying);
    if (audioRef.current && isAudioPlaying) {
      audioRef.current.pause();
      setIsAudioPlaying(false);
    }
  };

  const handleManualNext = () => {
    if (isAutoPlaying) {
      setIsAutoPlaying(false);
    }
    handleNextPrayer();
  };

  const handlePrevious = () => {
    if (currentPrayerIndex > 0) {
      setCurrentPrayerIndex(prev => prev - 1);
      setCounters(prev => {
        const newCounters = [...prev];
        newCounters[currentPrayerIndex - 1] = PRAYERS[currentPrayerIndex - 1].repetitions;
        return newCounters;
      });
    }
  };

  return (
    <div style={{ 
      maxWidth: '500px', 
      margin: '0 auto', 
      padding: '20px',
      textAlign: 'center'
    }}>
      <h2>{PRAYERS[currentPrayerIndex].name}</h2>

      <div style={{
        backgroundColor: '#f5f5f5',
        padding: '20px',
        borderRadius: '10px',
        margin: '20px 0',
        minHeight: '200px'
      }}>
        {PRAYERS[currentPrayerIndex].text}
      </div>

      <div style={{ 
        fontSize: '24px',
        margin: '20px 0',
        cursor: 'pointer'
      }} onClick={handleManualNext}>
        {counters[currentPrayerIndex]}
      </div>

      <div style={{ display: 'flex', justifyContent: 'space-between' }}>
        <button onClick={handlePrevious} disabled={currentPrayerIndex === 0}>
          Previous
        </button>

        <button 
          onClick={toggleAutoPlay}
          style={{ 
            background: isAutoPlaying ? '#4CAF50' : '#f5f5f5',
            color: isAutoPlaying ? 'white' : 'black'
          }}
        >
          {isAutoPlaying ? 'Stop Auto' : 'Auto Play'}
        </button>

        <button 
          onClick={handleManualNext}
          disabled={currentPrayerIndex === PRAYERS.length - 1 && counters[currentPrayerIndex] <= 0}
        >
          Next
        </button>
      </div>

      {isAutoPlaying && (
        <div style={{ marginTop: '10px', color: '#4CAF50' }}>
          Auto-play is active
        </div>
      )}
    </div>
  );
};

export default Chaplet;
