import React, { useState, useRef } from 'react';
import { Bookmark, Check, Volume2, Globe, ChevronDown, Share2, Home, Book, Heart, Settings } from 'lucide-react';
import { doc, setDoc, arrayUnion } from "firebase/firestore";
import { auth, db } from '../firebase';

// Language options
const LANGUAGES = {
  ENGLISH: 'en',
  KINYARWANDA: 'rw'
};

// Language display names
const LANGUAGE_NAMES = {
  [LANGUAGES.ENGLISH]: 'English',
  [LANGUAGES.KINYARWANDA]: 'Kinyarwanda'
};

// Speech synthesis language codes
const SPEECH_LANGUAGES = {
  [LANGUAGES.ENGLISH]: 'en-US',
  [LANGUAGES.KINYARWANDA]: 'rw-RW'  // Note: Check if this is supported in the browser
};

const HomeTab = ({ 
  theme,
  verseOfDay, 
  addBookmark,
  fetchVerseOfDay
}) => {
  const [bookmarked, setBookmarked] = useState(false);
  const [bookmarkLoading, setBookmarkLoading] = useState(false);
  const [isPlaying, setIsPlaying] = useState(false);
  const [selectedLanguage, setSelectedLanguage] = useState(LANGUAGES.ENGLISH);
  const [dropdownOpen, setDropdownOpen] = useState(false);
  const speechSynthRef = useRef(window.speechSynthesis);
  const dropdownRef = useRef(null);

  // Toggle dropdown
  const toggleDropdown = () => {
    setDropdownOpen(!dropdownOpen);
  };

  // Close dropdown when clicking outside
  React.useEffect(() => {
    const handleClickOutside = (event) => {
      if (dropdownRef.current && !dropdownRef.current.contains(event.target)) {
        setDropdownOpen(false);
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, []);

  // Handle language change
  const handleLanguageChange = (language) => {
    if (language === selectedLanguage) return;

    setSelectedLanguage(language);
    setDropdownOpen(false);

    // Fetch verse in the selected language
    if (fetchVerseOfDay) {
      fetchVerseOfDay(language);
    }
  };

  // Function to handle bookmarking the verse of the day
  const handleBookmarkVOTD = async () => {
    if (!verseOfDay || bookmarked || !auth.currentUser) return;

    try {
      setBookmarkLoading(true);

      // Parse the reference to extract book, chapter, and verse
      const referenceText = verseOfDay.reference;

      // Parse book, chapter and verse details from the reference
      const matches = referenceText.match(/^((?:\d\s)?[A-Za-z]+)\s+(\d+):(\d+)(?:-\d+)?$/);

      if (!matches) {
        console.error("Could not parse reference:", referenceText);
        return;
      }

      const [_, book, chapter, verse] = matches;

      // Create bookmark data
      const bookmarkData = {
        book: book,
        chapter: parseInt(chapter),
        lineNumber: parseInt(verse),
        text: verseOfDay.text,
        reference: verseOfDay.reference,
        language: selectedLanguage,
        timestamp: new Date()
      };

      // Add to Firebase
      const userDocRef = doc(db, "users", auth.currentUser.uid);
      await setDoc(userDocRef, {
        bookmarks: arrayUnion(bookmarkData)
      }, { merge: true });

      // Call the original addBookmark function for local state update
      if (addBookmark) {
        addBookmark(verseOfDay.reference, verseOfDay.text);
      }

      // Show success feedback
      setBookmarked(true);

      // Reset the bookmarked status after 3 seconds
      setTimeout(() => {
        setBookmarked(false);
      }, 3000);

    } catch (error) {
      console.error("Error bookmarking verse of the day:", error);
    } finally {
      setBookmarkLoading(false);
    }
  };

  // Function to read verse aloud
  const readVerse = () => {
    if (!verseOfDay) return;

    // Cancel any ongoing speech
    speechSynthRef.current.cancel();

    // Create utterance with verse text and reference
    const textToRead = `${verseOfDay.text}. ${verseOfDay.reference}`;
    const utterance = new SpeechSynthesisUtterance(textToRead);

    // Set language based on selection
    utterance.lang = SPEECH_LANGUAGES[selectedLanguage] || 'en-US';

    // Handle speech events
    utterance.onstart = () => setIsPlaying(true);
    utterance.onend = () => setIsPlaying(false);
    utterance.onerror = () => setIsPlaying(false);

    // Speak the verse
    speechSynthRef.current.speak(utterance);
  };

  // Stop reading
  const stopReading = () => {
    speechSynthRef.current.cancel();
    setIsPlaying(false);
  };

  // Function to handle sharing
  const handleShare = () => {
    if (!verseOfDay) return;

    if (navigator.share) {
      navigator.share({
        title: 'Verse of the Day',
        text: `${verseOfDay.text} - ${verseOfDay.reference}`,
      })
      .catch(error => console.error('Error sharing:', error));
    }
  };

  // Background gradients for verse card
  const bgStyles = {
    gradient1: "linear-gradient(135deg, #3b82f6 0%, #1e40af 100%)",
    gradient2: "linear-gradient(135deg, #6366f1 0%, #4338ca 100%)",
    gradient3: "linear-gradient(135deg, #8b5cf6 0%, #5b21b6 100%)",
    gradient4: "linear-gradient(135deg, #ec4899 0%, #be185d 100%)",
    gradient5: "linear-gradient(135deg, #2dd4bf 10%, #0f766e 90%)"
  };

  // Choose a random gradient
  const randomGradient = Object.values(bgStyles)[Math.floor(Math.random() * 5)];

  return (
    <div className="min-h-screen flex flex-col bg-gray-50">
      {/* Top Header with logo, dark mode toggle, and search */}
      <header className="bg-amber-800 text-white px-4 py-3 flex justify-between items-center">
        <h1 className="text-xl font-bold">Bible Snap</h1>
        <div className="flex items-center gap-3">
          <button className="text-white opacity-80 hover:opacity-100 transition-opacity">
            <Settings size={18} />
          </button>
          <div className="relative">
            <input 
              type="text" 
              placeholder="Search Bible..." 
              className="bg-white bg-opacity-20 text-white placeholder-white placeholder-opacity-70 rounded-full px-4 py-1.5 text-sm w-40 focus:outline-none focus:ring-2 focus:ring-white focus:ring-opacity-30"
            />
          </div>
        </div>
      </header>

      {/* Verse title bar with language selector */}
      <div className="bg-amber-700 px-4 py-3 flex justify-between items-center shadow-md">
        <h2 className="text-white font-medium">
          {selectedLanguage === LANGUAGES.KINYARWANDA ? 'Umurongo w\'Umunsi' : 'Verse of the Day'}
        </h2>

        {/* Language selector */}
        <div className="relative" ref={dropdownRef}>
          <button 
            className="flex items-center gap-1 bg-white bg-opacity-20 text-white rounded-full px-3 py-1 text-sm transition-all hover:bg-opacity-30"
            onClick={toggleDropdown}
          >
            <Globe size={14} />
            {LANGUAGE_NAMES[selectedLanguage]}
            <ChevronDown size={12} className="ml-1" />
          </button>

          {dropdownOpen && (
            <div className="absolute right-0 mt-1 py-1 bg-white rounded-lg shadow-lg z-10 min-w-32">
              {Object.values(LANGUAGES).map(lang => (
                <button 
                  key={lang}
                  className={`w-full text-left px-3 py-2 hover:bg-gray-100 text-sm ${selectedLanguage === lang ? 'font-bold text-amber-700' : 'text-gray-700'}`}
                  onClick={() => handleLanguageChange(lang)}
                >
                  {LANGUAGE_NAMES[lang]}
                </button>
              ))}
            </div>
          )}
        </div>
      </div>

      {/* Main Content - Verse Card */}
      <div className="flex-grow flex justify-center items-center p-4">
        {verseOfDay ? (
          <div className="w-full max-w-md overflow-hidden rounded-2xl shadow-xl">
            <div 
              className="relative py-12 px-8 flex flex-col items-center justify-center text-center"
              style={{ 
                background: randomGradient,
                minHeight: "420px"
              }}
            >
              {/* Decorative quote mark */}
              <div className="text-white text-opacity-20 text-7xl font-serif absolute top-4 left-6">❝</div>
              <div className="text-white text-opacity-20 text-7xl font-serif absolute bottom-4 right-6 rotate-180">❝</div>

              {/* Verse text */}
              <div className="z-10">
                <h2 className="text-2xl text-white font-light leading-relaxed mb-8">
                  {verseOfDay.text}
                </h2>

                <p className="font-bold text-white text-lg mb-1">{verseOfDay.reference}</p>
                <p className="text-xs text-white text-opacity-80 mb-8">({verseOfDay.translation})</p>
              </div>

              {/* Action buttons */}
              <div className="flex justify-center gap-5 mt-6">
                <button 
                  className="bg-white bg-opacity-20 hover:bg-opacity-30 rounded-full p-3 transition-all hover:shadow-lg"
                  onClick={handleShare}
                >
                  <Share2 size={20} className="text-white" />
                </button>

                <button 
                  className={`${bookmarked ? 'bg-green-500' : 'bg-red-500'} rounded-full p-3 transition-all hover:shadow-lg`}
                  onClick={bookmarked ? null : handleBookmarkVOTD}
                  disabled={bookmarkLoading}
                >
                  {bookmarked ? 
                    <Check size={20} className="text-white" /> : 
                    <Bookmark size={20} className="text-white" />
                  }
                </button>

                <button 
                  className="bg-white bg-opacity-20 hover:bg-opacity-30 rounded-full p-3 transition-all hover:shadow-lg"
                  onClick={isPlaying ? stopReading : readVerse}
                >
                  <Volume2 size={20} className={`text-white ${isPlaying ? 'animate-pulse' : ''}`} />
                </button>
              </div>
            </div>
          </div>
        ) : (
          <div className="flex justify-center py-12">
            <div className="w-12 h-12 border-4 border-amber-600 border-t-transparent rounded-full animate-spin"></div>
          </div>
        )}
      </div>

      {/* Bottom Navigation Bar */}
      <div className="px-6 pb-6">
        <nav className="bg-white rounded-full shadow-lg flex justify-around py-3">
          <button className="flex flex-col items-center text-amber-700">
            <Home size={20} />
            <span className="text-xs mt-1 font-medium">Home</span>
          </button>
          <button className="flex flex-col items-center text-gray-400">
            <Book size={20} />
            <span className="text-xs mt-1">Books</span>
          </button>
          <button className="flex flex-col items-center text-gray-400">
            <Heart size={20} />
            <span className="text-xs mt-1">Saved</span>
          </button>
          <button className="flex flex-col items-center text-gray-400">
            <Settings size={20} />
            <span className="text-xs mt-1">Settings</span>
          </button>
        </nav>
      </div>
    </div>
  );
};

export default HomeTab;
