import React, { useState, useRef } from 'react';
import { Bookmark, Check, Volume2, Globe, ChevronDown, Share2 } from 'lucide-react';
import { doc, setDoc, arrayUnion } from "firebase/firestore";
import { auth, db } from '../firebase';

const LANGUAGES = {
  ENGLISH: 'en',
  KINYARWANDA: 'rw'
};

const LANGUAGE_NAMES = {
  [LANGUAGES.ENGLISH]: 'English',
  [LANGUAGES.KINYARWANDA]: 'Kinyarwanda'
};

const SPEECH_LANGUAGES = {
  [LANGUAGES.ENGLISH]: 'en-US',
  [LANGUAGES.KINYARWANDA]: 'rw-RW'
};

const HomeTab = ({ 
  theme,
  verseOfDay, 
  addBookmark,
  fetchVerseOfDay
}) => {
  const [bookmarked, setBookmarked] = useState(false);
  const [bookmarkLoading, setBookmarkLoading] = useState(false);
  const [isPlaying, setIsPlaying] = useState(false);
  const [selectedLanguage, setSelectedLanguage] = useState(LANGUAGES.ENGLISH);
  const [dropdownOpen, setDropdownOpen] = useState(false);
  const speechSynthRef = useRef(window.speechSynthesis);
  const dropdownRef = useRef(null);

  const toggleDropdown = () => {
    setDropdownOpen(!dropdownOpen);
  };

  React.useEffect(() => {
    const handleClickOutside = (event) => {
      if (dropdownRef.current && !dropdownRef.current.contains(event.target)) {
        setDropdownOpen(false);
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, []);

  const handleLanguageChange = (language) => {
    if (language === selectedLanguage) return;

    setSelectedLanguage(language);
    setDropdownOpen(false);

    if (fetchVerseOfDay) {
      fetchVerseOfDay(language);
    }
  };

  const handleBookmarkVOTD = async () => {
    if (!verseOfDay || bookmarked || !auth.currentUser) return;

    try {
      setBookmarkLoading(true);

      const referenceText = verseOfDay.reference;
      const matches = referenceText.match(/^((?:\d\s)?[A-Za-z]+)\s+(\d+):(\d+)(?:-\d+)?$/);

      if (!matches) {
        console.error("Could not parse reference:", referenceText);
        return;
      }

      const [_, book, chapter, verse] = matches;

      const bookmarkData = {
        book: book,
        chapter: parseInt(chapter),
        lineNumber: parseInt(verse),
        text: verseOfDay.text,
        reference: verseOfDay.reference,
        language: selectedLanguage,
        timestamp: new Date()
      };

      const userDocRef = doc(db, "users", auth.currentUser.uid);
      await setDoc(userDocRef, {
        bookmarks: arrayUnion(bookmarkData)
      }, { merge: true });

      if (addBookmark) {
        addBookmark(verseOfDay.reference, verseOfDay.text);
      }

      setBookmarked(true);
      setTimeout(() => {
        setBookmarked(false);
      }, 3000);

    } catch (error) {
      console.error("Error bookmarking verse of the day:", error);
    } finally {
      setBookmarkLoading(false);
    }
  };

  const readVerse = () => {
    if (!verseOfDay) return;

    speechSynthRef.current.cancel();

    const textToRead = `${verseOfDay.text}. ${verseOfDay.reference}`;
    const utterance = new SpeechSynthesisUtterance(textToRead);

    utterance.lang = SPEECH_LANGUAGES[selectedLanguage] || 'en-US';
    utterance.onstart = () => setIsPlaying(true);
    utterance.onend = () => setIsPlaying(false);
    utterance.onerror = () => setIsPlaying(false);

    speechSynthRef.current.speak(utterance);
  };

  const stopReading = () => {
    speechSynthRef.current.cancel();
    setIsPlaying(false);
  };

  const handleShare = () => {
    if (!verseOfDay) return;

    if (navigator.share) {
      navigator.share({
        title: 'Verse of the Day',
        text: `${verseOfDay.text} - ${verseOfDay.reference}`,
      })
      .catch(error => console.error('Error sharing:', error));
    }
  };

  return (
    <div className="home-tab-container d-flex flex-column" style={{ minHeight: '100vh' }}>
      {/* Modern Header with Search */}
      <div className={`${theme.header} px-4 py-3 d-flex flex-column`}>
        <div className="d-flex justify-content-between align-items-center mb-3">
          <h1 className="h4 mb-0 fw-bold">
            {selectedLanguage === LANGUAGES.KINYARWANDA ? 'Bible Snap' : 'Bible Snap'}
          </h1>

          <div className="position-relative" ref={dropdownRef}>
            <button 
              className={`btn btn-sm ${theme.subtleBtn || 'btn-outline-light'} d-flex align-items-center`} 
              type="button"
              onClick={toggleDropdown}
            >
              <Globe size={16} className="me-1" />
              {LANGUAGE_NAMES[selectedLanguage]}
              <ChevronDown size={14} className="ms-1" />
            </button>

            {dropdownOpen && (
              <div className="position-absolute end-0 mt-1 py-1 bg-white border rounded shadow-sm" style={{ zIndex: 1000, minWidth: '160px' }}>
                {Object.values(LANGUAGES).map(lang => (
                  <button 
                    key={lang}
                    className={`btn btn-link text-decoration-none text-start w-100 px-3 py-1 ${selectedLanguage === lang ? 'fw-bold' : ''}`}
                    onClick={() => handleLanguageChange(lang)}
                  >
                    {LANGUAGE_NAMES[lang]}
                  </button>
                ))}
              </div>
            )}
          </div>
        </div>

        {/* Search Bar */}
        <div className="search-container position-relative">
          <input 
            type="text" 
            className="form-control ps-5 py-2 rounded-pill" 
            placeholder={selectedLanguage === LANGUAGES.KINYARWANDA ? 'Shakisha Bibiliya...' : 'Search Bible...'}
          />
          <svg 
            xmlns="http://www.w3.org/2000/svg" 
            width="16" 
            height="16" 
            fill="#6c757d" 
            className="position-absolute top-50 start-0 translate-middle-y ms-3" 
            viewBox="0 0 16 16"
          >
            <path d="M11.742 10.344a6.5 6.5 0 1 0-1.397 1.398h-.001c.03.04.062.078.098.115l3.85 3.85a1 1 0 0 0 1.415-1.414l-3.85-3.85a1.007 1.007 0 0 0-.115-.1zM12 6.5a5.5 5.5 0 1 1-11 0 5.5 5.5 0 0 1 11 0z"/>
          </svg>
        </div>
      </div>

      {/* Main Content - Modern Verse Card */}
      <div className="flex-grow-1 d-flex justify-content-center align-items-center p-4">
        {verseOfDay ? (
          <div className="modern-verse-card position-relative rounded-4 w-100 overflow-hidden" style={{ maxWidth: '600px' }}>
            {/* Gradient Background */}
            <div 
              className="position-absolute w-100 h-100" 
              style={{
                background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
                zIndex: 0
              }}
            ></div>

            {/* Verse Content */}
            <div className="position-relative h-100 d-flex flex-column justify-content-center align-items-center text-center text-white p-4 p-md-5" style={{ zIndex: 1 }}>
              <div className="verse-header mb-4 w-100">
                <h2 className="fs-5 fw-normal text-white-70 mb-0">
                  {selectedLanguage === LANGUAGES.KINYARWANDA ? 'Umurongo w\'Umunsi' : 'Verse of the Day'}
                </h2>
              </div>

              <div className="verse-number mb-3">
                <span className="badge bg-white text-primary rounded-pill px-3 py-1">
                  {verseOfDay.reference.split(':')[1]}
                </span>
              </div>

              <div className="verse-text-container my-4">
                <div className="quote-mark-start fs-1 text-white-50 mb-2">“</div>
                <h2 className="fs-3 fw-light mb-3 lh-base" style={{ lineHeight: '1.6' }}>
                  {verseOfDay.text}
                </h2>
                <div className="quote-mark-end fs-1 text-white-50 mt-2">”</div>
              </div>

              <div className="verse-reference mt-3">
                <p className="fw-semibold mb-1">{verseOfDay.reference}</p>
                <p className="small text-white-70">({verseOfDay.translation})</p>
              </div>

              {/* Floating Action Buttons */}
              <div className="floating-actions d-flex justify-content-center mt-4 gap-3">
                <button 
                  className={`btn ${theme.subtleBtn || 'btn-light'} btn-sm rounded-pill px-3 d-flex align-items-center`}
                  onClick={handleShare}
                  aria-label="Share verse"
                >
                  <Share2 size={16} className="me-1" />
                  <span className="d-none d-sm-inline">Share</span>
                </button>

                <button 
                  className={`btn ${bookmarked ? 'btn-success' : theme.subtleBtn || 'btn-light'} btn-sm rounded-pill px-3 d-flex align-items-center`}
                  onClick={bookmarked ? null : handleBookmarkVOTD}
                  disabled={bookmarkLoading}
                  aria-label="Bookmark verse"
                >
                  {bookmarked ? (
                    <>
                      <Check size={16} className="me-1" />
                      <span className="d-none d-sm-inline">Saved</span>
                    </>
                  ) : (
                    <>
                      <Bookmark size={16} className="me-1" />
                      <span className="d-none d-sm-inline">Save</span>
                    </>
                  )}
                </button>

                <button 
                  className={`btn ${isPlaying ? 'btn-danger' : theme.subtleBtn || 'btn-light'} btn-sm rounded-pill px-3 d-flex align-items-center`}
                  onClick={isPlaying ? stopReading : readVerse}
                  aria-label={isPlaying ? "Stop reading" : "Read verse aloud"}
                >
                  <Volume2 size={16} className="me-1" />
                  <span className="d-none d-sm-inline">{isPlaying ? 'Stop' : 'Listen'}</span>
                </button>
              </div>
            </div>
          </div>
        ) : (
          <div className="d-flex justify-content-center py-5">
            <div className="spinner-border text-primary" role="status">
              <span className="visually-hidden">Loading verse of the day...</span>
            </div>
          </div>
        )}
      </div>

      {/* Footer */}
      <div className="text-center py-3 text-muted small">
        {selectedLanguage === LANGUAGES.KINYARWANDA ? 
          'Bibiliya yose muri Bibiliya Snap' : 
          'The complete Bible in Bible Snap'}
      </div>
    </div>
  );
};

export default HomeTab;
