import React, { useState, useEffect } from 'react';
import { Cross } from 'lucide-react';

const DEFAULT_PRAYERS = [
  { 
    name: "Sign of the Cross", 
    text: "In the name of the Father, and of the Son, and of the Holy Spirit. Amen.", 
    repetitions: 1 
  },
  { 
    name: "Our Father", 
    text: "Our Father, who art in heaven, hallowed be thy name; thy kingdom come; thy will be done on earth as it is in heaven. Give us this day our daily bread; and forgive us our trespasses as we forgive those who trespass against us; and lead us not into temptation, but deliver us from evil. Amen.", 
    repetitions: 1 
  },
  { 
    name: "Hail Mary", 
    text: "Hail Mary, full of grace, the Lord is with thee; blessed art thou among women, and blessed is the fruit of thy womb, Jesus. Holy Mary, Mother of God, pray for us sinners, now and at the hour of our death. Amen.", 
    repetitions: 10 
  },
  { 
    name: "Glory Be", 
    text: "Glory be to the Father, and to the Son, and to the Holy Spirit. As it was in the beginning, is now, and ever shall be, world without end. Amen.", 
    repetitions: 1 
  }
];

const Chapulle = () => {
  // Dark theme colors based on the screenshot
  const theme = {
    background: "#121218",
    cardBackground: "#1e1e29",
    textColor: "#ffffff",
    accentColor: "#c9a45c", // Gold accent color
    subtleText: "#8a8a9a"
  };

  const [isPraying, setIsPraying] = useState(false);
  const [hasStarted, setHasStarted] = useState(false);
  const [currentPrayerIndex, setCurrentPrayerIndex] = useState(0);
  const [counters, setCounters] = useState(DEFAULT_PRAYERS.map(p => p.repetitions));
  const [message, setMessage] = useState("");
  const [prayers, setPrayers] = useState(DEFAULT_PRAYERS);
  const [isAutoPlaying, setIsAutoPlaying] = useState(false);

  const getCurrentPrayer = () => {
    return prayers[currentPrayerIndex] || prayers[0];
  };

  const startPrayer = () => {
    setHasStarted(true);
    setIsPraying(true);
    setMessage("");
    setCurrentPrayerIndex(0);
    setCounters(prayers.map(p => p.repetitions));
  };

  const toggleAutoPlay = () => {
    setIsAutoPlaying(!isAutoPlaying);
  };

  const handleCounterClick = () => {
    setCounters(prev => {
      const newCounters = [...prev];
      if (newCounters[currentPrayerIndex] > 0) {
        newCounters[currentPrayerIndex] -= 1;
      }

      // Move to next prayer if current counter reaches 0
      if (newCounters[currentPrayerIndex] === 0) {
        if (currentPrayerIndex < prayers.length - 1) {
          setCurrentPrayerIndex(prev => prev + 1);
        } else {
          completePrayer();
        }
      }

      return newCounters;
    });
  };

  const navigateToPrevious = () => {
    if (currentPrayerIndex > 0) {
      setCurrentPrayerIndex(prev => prev - 1);
      // Reset counter for the previous prayer
      setCounters(prev => {
        const newCounters = [...prev];
        newCounters[currentPrayerIndex - 1] = prayers[currentPrayerIndex - 1].repetitions;
        return newCounters;
      });
    }
  };

  const navigateToNext = () => {
    if (currentPrayerIndex < prayers.length - 1) {
      setCurrentPrayerIndex(prev => prev + 1);
    } else {
      completePrayer();
    }
  };

  const completePrayer = () => {
    setIsPraying(false);
    setIsAutoPlaying(false);
    setMessage("Amen! You completed your prayer chaplet. ❤️");
  };

  useEffect(() => {
    let timer;
    if (isPraying && isAutoPlaying) {
      timer = setInterval(() => {
        handleCounterClick();
      }, 3000); // Slower auto-play for meditation
    }
    return () => clearInterval(timer);
  }, [isPraying, currentPrayerIndex, isAutoPlaying]);

  const PrayerInProgress = () => (
    <div className="prayer-screen" style={{ 
      minHeight: '80vh', 
      display: 'flex', 
      flexDirection: 'column',
      justifyContent: 'space-between',
      padding: '1rem'
    }}>
      <header style={{ textAlign: 'center', marginBottom: '1rem' }}>
        <h3 style={{ 
          color: theme.textColor, 
          fontSize: '1.4rem', 
          fontWeight: '600' 
        }}>
          {getCurrentPrayer().name}
        </h3>
      </header>

      <main style={{ 
        flex: 1, 
        display: 'flex', 
        flexDirection: 'column', 
        justifyContent: 'center',
        alignItems: 'center',
        padding: '1rem',
        textAlign: 'center'
      }}>
        <div style={{ 
          position: 'relative',
          width: '100%',
          maxWidth: '500px', 
          padding: '2rem',
          backgroundColor: theme.cardBackground,
          borderRadius: '0.5rem',
          marginBottom: '2rem'
        }}>
          <span style={{ 
            position: 'absolute', 
            top: 0, 
            left: '1rem', 
            fontSize: '3rem', 
            color: theme.accentColor,
            transform: 'translateY(-50%)'
          }}>
            "
          </span>
          <p style={{ 
            color: theme.textColor, 
            fontSize: '1.5rem', 
            lineHeight: 1.6,
            textAlign: 'center',
            margin: '1rem 0'
          }}>
            {getCurrentPrayer().text}
          </p>
          <span style={{ 
            position: 'absolute', 
            bottom: 0, 
            right: '1rem', 
            fontSize: '3rem', 
            color: theme.accentColor,
            transform: 'translateY(50%)'
          }}>
            "
          </span>
        </div>

        <div style={{ 
          cursor: 'pointer',
          display: 'flex',
          flexDirection: 'column',
          alignItems: 'center',
          justifyContent: 'center'
        }}
        onClick={handleCounterClick}>
          <div style={{ 
            width: '100px', 
            height: '100px', 
            borderRadius: '50%', 
            backgroundColor: theme.background,
            border: `3px solid ${theme.accentColor}`,
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            fontSize: '2.5rem',
            color: theme.textColor
          }}>
            {counters[currentPrayerIndex]}
          </div>
          <p style={{ 
            color: theme.subtleText, 
            marginTop: '0.5rem'
          }}>
            Une fois
          </p>
        </div>
      </main>

      <footer style={{ 
        display: 'flex', 
        justifyContent: 'space-between', 
        alignItems: 'center', 
        paddingTop: '1rem'
      }}>
        <button 
          onClick={navigateToPrevious} 
          style={{ 
            backgroundColor: 'transparent',
            border: 'none',
            color: theme.accentColor,
            fontSize: '2rem',
            cursor: 'pointer',
            opacity: currentPrayerIndex > 0 ? 1 : 0.3
          }}
          disabled={currentPrayerIndex === 0}
        >
          ❮
        </button>

        <div style={{ textAlign: 'center', color: theme.subtleText }}>
          {currentPrayerIndex + 1}/{prayers.length}
        </div>

        <button 
          onClick={navigateToNext} 
          style={{ 
            backgroundColor: 'transparent',
            border: 'none',
            color: theme.accentColor,
            fontSize: '2rem',
            cursor: 'pointer'
          }}
        >
          ❯
        </button>
      </footer>

      <div style={{ 
        position: 'absolute', 
        top: '1rem', 
        right: '1rem', 
        display: 'flex', 
        gap: '0.5rem' 
      }}>
        <button 
          onClick={toggleAutoPlay}
          style={{ 
            backgroundColor: theme.cardBackground,
            border: 'none',
            color: isAutoPlaying ? theme.accentColor : theme.textColor,
            borderRadius: '0.25rem',
            padding: '0.25rem 0.5rem',
            fontSize: '0.8rem',
            cursor: 'pointer'
          }}
        >
          {isAutoPlaying ? 'Pause' : 'Auto'}
        </button>
      </div>
    </div>
  );

  const PrayerStartScreen = () => (
    <div style={{ 
      textAlign: 'center', 
      padding: '2rem', 
      minHeight: '80vh',
      display: 'flex',
      flexDirection: 'column',
      justifyContent: 'center',
      alignItems: 'center'
    }}>
      <h3 style={{ 
        color: theme.textColor, 
        fontSize: '1.8rem', 
        fontWeight: '600',
        marginBottom: '2rem'
      }}>
        Christian Prayer Chaplet
      </h3>

      <button
        onClick={startPrayer}
        style={{
          backgroundColor: theme.cardBackground,
          color: theme.accentColor,
          border: `2px solid ${theme.accentColor}`,
          borderRadius: '2rem',
          padding: '1rem 2rem',
          fontSize: '1.2rem',
          cursor: 'pointer',
          marginBottom: '2rem'
        }}
      >
        Start Praying
      </button>

      <div style={{ maxWidth: '400px', width: '100%' }}>
        <h4 style={{ 
          color: theme.textColor, 
          fontSize: '1.2rem', 
          marginBottom: '1rem' 
        }}>
          Prayer Sequence
        </h4>

        <div style={{ 
          display: 'flex', 
          flexDirection: 'column', 
          gap: '0.5rem' 
        }}>
          {prayers.map((prayer, index) => (
            <div 
              key={index}
              style={{
                backgroundColor: theme.cardBackground,
                borderRadius: '0.5rem',
                padding: '0.75rem 1rem',
                display: 'flex',
                justifyContent: 'space-between'
              }}
            >
              <span style={{ color: theme.textColor }}>{prayer.name}</span>
              <span style={{ color: theme.accentColor }}>{prayer.repetitions}x</span>
            </div>
          ))}
        </div>
      </div>
    </div>
  );

  const PrayerCompletedScreen = () => (
    <div style={{ 
      textAlign: 'center', 
      padding: '2rem', 
      minHeight: '80vh',
      display: 'flex',
      flexDirection: 'column',
      justifyContent: 'center',
      alignItems: 'center'
    }}>
      <div style={{
        backgroundColor: theme.cardBackground,
        color: theme.accentColor,
        padding: '1rem',
        borderRadius: '0.5rem',
        marginBottom: '2rem',
        maxWidth: '400px',
        width: '100%'
      }}>
        {message}
      </div>

      <button
        onClick={startPrayer}
        style={{
          backgroundColor: theme.cardBackground,
          color: theme.accentColor,
          border: `2px solid ${theme.accentColor}`,
          borderRadius: '2rem',
          padding: '1rem 2rem',
          fontSize: '1.2rem',
          cursor: 'pointer'
        }}
      >
        Restart Prayer
      </button>
    </div>
  );

  return (
    <div style={{ 
      backgroundColor: theme.background, 
      minHeight: '100vh', 
      color: theme.textColor,
      position: 'relative'
    }}>
      <header style={{ 
        padding: '1rem', 
        display: 'flex', 
        alignItems: 'center', 
        borderBottom: `1px solid ${theme.cardBackground}`
      }}>
        {hasStarted && (
          <button 
            onClick={() => setHasStarted(false)}
            style={{ 
              backgroundColor: 'transparent',
              border: 'none',
              color: theme.accentColor,
              fontSize: '1.5rem',
              marginRight: '1rem',
              cursor: 'pointer'
            }}
          >
            ❮
          </button>
        )}
        <h2 style={{ 
          color: theme.textColor, 
          fontSize: '1.5rem', 
          fontWeight: 'bold',
          margin: 0,
          flex: 1,
          textAlign: hasStarted ? 'center' : 'left'
        }}>
          Prayer Chaplet
        </h2>
        <div style={{ width: hasStarted ? '24px' : '0' }}></div> {/* For balance */}
      </header>

      {!hasStarted ? (
        <PrayerStartScreen />
      ) : isPraying ? (
        <PrayerInProgress />
      ) : (
        <PrayerCompletedScreen />
      )}
    </div>
  );
};

export default Chapulle;
